import React, { useEffect, useState } from 'react';
import { X } from '@/components/ui/HearthlinkIcons';
import { cn } from '@/lib/utils';
import { createPortal } from 'react-dom';

interface PremiumModalProps {
  isOpen: boolean;
  onClose: () => void;
  title?: string;
  children: React.ReactNode;
  className?: string;
  size?: 'sm' | 'md' | 'lg' | 'xl' | 'full';
  hideCloseButton?: boolean;
  hideHeader?: boolean;
}

export const PremiumModal: React.FC<PremiumModalProps> = ({
  isOpen,
  onClose,
  title,
  children,
  className,
  size = 'md',
  hideCloseButton = false,
  hideHeader = false
}) => {
  const [mounted, setMounted] = useState(false);
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    if (isOpen) {
      setMounted(true);
      // Small delay to allow mounting before animating in
      requestAnimationFrame(() => setVisible(true));
      document.body.style.overflow = 'hidden';
    } else {
      setVisible(false);
      const timer = setTimeout(() => setMounted(false), 300); // Match transition duration
      document.body.style.overflow = '';
      return () => clearTimeout(timer);
    }
  }, [isOpen]);

  if (!mounted) return null;

  const sizeClasses = {
    sm: "max-w-sm",
    md: "max-w-md",
    lg: "max-w-lg",
    xl: "max-w-xl",
    full: "max-w-[95vw] h-[90vh]"
  };

  return createPortal(
    <div className="fixed inset-0 z-50 flex items-center justify-center p-4 sm:p-6 pointer-events-auto">
      {/* Backdrop */}
      <div 
        className={cn(
          "fixed inset-0 bg-black/60 transition-opacity duration-300",
          visible ? "opacity-100" : "opacity-0"
        )}
        onClick={onClose}
      />
      
      {/* Modal Content */}
      <div 
        className={cn(
          "relative w-full bg-background/80 backdrop-blur-xl border border-border/50 shadow-2xl rounded-xl overflow-hidden transition-all duration-300 transform",
          visible ? "opacity-100 scale-100 translate-y-0" : "opacity-0 scale-95 translate-y-4",
          sizeClasses[size],
          className
        )}
      >
        {!hideHeader && (
          <div className="flex items-center justify-between px-6 py-4 border-b border-border/20 bg-transparent">
            {title && <h2 className="text-lg font-semibold tracking-tight">{title}</h2>}
            {!hideCloseButton && (
              <button
                onClick={onClose}
                className="rounded-full p-1 hover:bg-accent hover:text-accent-foreground transition-colors"
              >
                <X className="h-5 w-5" />
                <span className="sr-only">Close</span>
              </button>
            )}
          </div>
        )}

        {/* Body */}
        <div className="p-6 overflow-y-auto max-h-[calc(85vh-4rem)]">
          {children}
        </div>
      </div>
    </div>,
    document.body
  );
};
