#### Step 2A: Create useBridgeQuery Hook

**Create file:** `src/hooks/useBridgeQuery.ts`

```typescript
import { useState, useEffect, useCallback } from 'react';
import type { BridgeResponse } from '../types/agents';

export interface UseBridgeQueryOptions<TData> {
  queryFn: () => Promise<BridgeResponse<TData>>;
  onSuccess?: (data: TData) => void;
  onError?: (error: string) => void;
  enabled?: boolean;
  refetchInterval?: number;
}

export interface UseBridgeQueryResult<TData> {
  data: TData | null;
  loading: boolean;
  error: string | null;
  refetch: () => Promise<void>;
}

export function useBridgeQuery<TData>(
  options: UseBridgeQueryOptions<TData>
): UseBridgeQueryResult<TData> {
  const [data, setData] = useState<TData | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const refetch = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await options.queryFn();
      if (response.success && response.data) {
        setData(response.data);
        options.onSuccess?.(response.data);
      } else {
        const errorMsg = response.error || 'Query failed';
        setError(errorMsg);
        options.onError?.(errorMsg);
      }
    } catch (err) {
      const errorMsg = err instanceof Error ? err.message : 'Query failed';
      setError(errorMsg);
      options.onError?.(errorMsg);
    } finally {
      setLoading(false);
    }
  }, [options]);

  useEffect(() => {
    if (options.enabled !== false) {
      refetch();
    }
  }, [refetch, options.enabled]);

  // Auto-refetch interval
  useEffect(() => {
    if (options.refetchInterval && options.enabled !== false) {
      const interval = setInterval(refetch, options.refetchInterval);
      return () => clearInterval(interval);
    }
  }, [refetch, options.refetchInterval, options.enabled]);

  return { data, loading, error, refetch };
}
```

