            {[...Array(10)].map((_, i) => (
              <div key={i} className="flex gap-3 shrink-0">
                <div className="flex flex-col items-center gap-1">
                  <div className="w-2 h-2 rounded-full bg-emerald-500" />
                  <div className="w-0.5 h-full bg-[#334155]/50" />
                </div>
                <div className="pb-4">
                  <div className="text-[#e2e8f0] text-xs font-medium">Access Granted</div>
                  <div className="text-[#64748b] text-[10px] font-mono">User: KRKNA ƒ?› 10:4{i} AM</div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );

  const renderMemoryBank = () => (
    <div className="flex flex-col h-full p-6 gap-6 overflow-hidden">
      {/* Memory Search Bar */}
      <div className="shrink-0">
        <MemorySearchBar 
          userId={userId} 
          onMemorySelect={(memory) => setSelectedMemory(memory)}
        />
      </div>

      {/* Selected Memory Preview */}
      {selectedMemory && (
        <div className="bg-[#0f172a]/60 border border-amber-500/30 rounded-xl p-4 shrink-0">
          <h4 className="text-amber-400 text-xs uppercase tracking-wider font-semibold mb-2">Selected Memory</h4>
          <pre className="text-[#e2e8f0] text-sm font-mono overflow-auto max-h-40">
            {JSON.stringify(selectedMemory, null, 2)}
          </pre>
        </div>
      )}

      {/* Memory List Placeholder */}
      <div className="flex-1 bg-[#0f172a]/60 border border-[#334155] rounded-2xl p-6 flex items-center justify-center">
        <div className="text-center">
          <Database size={48} className="text-amber-500/50 mx-auto mb-4" />
          <p className="text-[#94a3b8] text-sm">Use the search bar above to find memories</p>
          <p className="text-[#64748b] text-xs mt-1">Supports keyword and semantic search</p>
        </div>
      </div>
    </div>
  );

  const renderKnowledgeGraph = () => (
    <div className="h-full overflow-hidden">
      <KnowledgeGraphViewer userId={userId} />
    </div>
  );

  const renderSecureStorage = () => (
    <div className="flex items-center justify-center h-full">
      <div className="text-center">
        <Lock size={48} className="text-amber-500/50 mx-auto mb-4" />
        <p className="text-[#94a3b8] text-sm">Secure Storage</p>
        <p className="text-[#64748b] text-xs mt-1">Coming soon</p>
      </div>
