/**
 * Synapse Dashboard Component - External Gateway & Plugin Management
 *
 * Provides interface for plugin management, external connections, and security monitoring.
 * Follows the AldenCommandCenter pattern for consistency.
 *
 * @version 2.0.0
 * @date 2025-11-08
 */

import React, { useState, useEffect } from 'react';
import { SynapseBridge } from '../../bridges/SynapseBridge';
import type { SynapsePlugin, SynapseConnection, SynapseSecurityLog } from '../../types/agents';
import { BackToLauncher } from '../shared/BackToLauncher';

const toErrorMessage = (err: unknown): string => (
  err instanceof Error ? err.message : String(err)
);

export interface SynapseDashboardProps {
  userId: string;
  onBackToLauncher?: () => void;
}

export const SynapseDashboard: React.FC<SynapseDashboardProps> = ({ userId, onBackToLauncher }) => {
  const [plugins, setPlugins] = useState<SynapsePlugin[]>([]);
  const [connections, setConnections] = useState<SynapseConnection[]>([]);
  const [securityLogs, setSecurityLogs] = useState<SynapseSecurityLog[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [actionSuccess, setActionSuccess] = useState<string | null>(null);

  useEffect(() => {
    loadDashboardData();
  }, [userId]);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      setError(null);

      const bridge = new SynapseBridge();
      await bridge.initialize();

      const props = await bridge.getProps();
      setPlugins(props.plugins);
      setConnections(props.connections);
      setSecurityLogs(props.securityLogs);
    } catch (err: unknown) {
      console.error('[Synapse] Failed to load dashboard:', err);
      setError(toErrorMessage(err) || 'Failed to load synapse data');
    } finally {
      setLoading(false);
    }
  };

  const handleTogglePlugin = async (pluginId: string, enabled: boolean) => {
    try {
      const bridge = new SynapseBridge();
      await bridge.initialize();

      const response = await bridge.togglePlugin(pluginId, enabled);
      if (response.success) {
        setPlugins(prev => prev.map(p =>
          p.plugin_id === pluginId ? { ...p, enabled } : p
        ));
        setActionSuccess(`Plugin ${enabled ? 'enabled' : 'disabled'}`);
        setTimeout(() => setActionSuccess(null), 3000);
      } else {
        setError(response.error || 'Failed to toggle plugin');
      }
    } catch (err: unknown) {
      setError(toErrorMessage(err) || 'Failed to toggle plugin');
    }
  };

  const renderHeader = () => (
    <div className="flex items-center justify-between p-6 border-b border-border">
      <div className="flex items-center gap-4">
        {/* Back to Launcher Button */}
        {onBackToLauncher && (
          <BackToLauncher onClick={onBackToLauncher} variant="minimal" />
        )}

        <div className="flex items-center gap-3">
          <img
            src="/assets/Synapse.png"
            alt="Synapse"
            className="w-12 h-12 object-contain"
            style={{
              filter: 'drop-shadow(0 0 15px color-mix(in oklch, var(--synapse-accent) 40%, transparent))'
            }}
          />
          <div>
            <h1 className="text-2xl font-semibold">Synapse Gateway</h1>
            <p className="text-xs text-muted-foreground">External Plugin & Integration Hub</p>
          </div>
        </div>
      </div>
      <div className="flex items-center gap-2">
        <span className="w-2 h-2 rounded-full animate-pulse" style={{ backgroundColor: 'var(--synapse-accent)' }}></span>
        <span className="text-sm" style={{ color: 'var(--synapse-accent)' }}>Gateway Active</span>
      </div>
    </div>
  );

  const renderMetrics = () => {
    const activePlugins = plugins.filter(p => p.enabled).length;
    const activeConnections = connections.filter(c => c.status === 'connected').length;
    const criticalLogs = securityLogs.filter(l => l.severity === 'critical').length;

    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
        <div className="stat-card">
          <span className="stat-value">{activePlugins}/{plugins.length}</span>
          <span className="stat-label">Active Plugins</span>
          <div className="mt-2 flex items-center gap-2" style={{ color: 'var(--synapse-accent)' }}>
            <span className="w-1.5 h-1.5 rounded-full" style={{ backgroundColor: 'var(--synapse-accent)' }}></span>
            <span className="text-xs">Plugin system</span>
          </div>
        </div>

        <div className="stat-card">
          <span className="stat-value">{activeConnections}/{connections.length}</span>
          <span className="stat-label">Active Connections</span>
          <div className="mt-2 flex items-center gap-2" style={{ color: activeConnections > 0 ? 'var(--success)' : 'var(--muted-foreground)' }}>
            <span className="w-1.5 h-1.5 rounded-full" style={{ backgroundColor: activeConnections > 0 ? 'var(--success)' : 'var(--muted-foreground)' }}></span>
            <span className="text-xs">{activeConnections > 0 ? 'Connected' : 'Offline'}</span>
          </div>
        </div>

        <div className="stat-card">
          <span className="stat-value">{securityLogs.length}</span>
          <span className="stat-label">Security Logs</span>
          <div className="mt-2 text-xs text-muted-foreground">Recent events</div>
        </div>

        <div className="stat-card">
          <span className="stat-value">{criticalLogs}</span>
          <span className="stat-label">Critical Events</span>
          <div className="mt-2 flex items-center gap-2" style={{ color: criticalLogs > 0 ? 'var(--error)' : 'var(--success)' }}>
            <span className="w-1.5 h-1.5 rounded-full" style={{ backgroundColor: criticalLogs > 0 ? 'var(--error)' : 'var(--success)' }}></span>
            <span className="text-xs">{criticalLogs > 0 ? 'Alert' : 'Secure'}</span>
          </div>
        </div>
      </div>
    );
  };

  const renderPlugins = () => (
    <div className="module-card mb-6">
      <div className="flex items-center justify-between p-4 border-b border-border">
        <h2 className="text-xl font-semibold">Installed Plugins ({plugins.length})</h2>
        <button
          className="synapse-button text-xs px-3 py-1.5 rounded-md"
          style={{
            backgroundColor: 'var(--synapse-accent)',
            color: 'var(--accent-foreground)',
          }}
        >
          + Add Plugin
        </button>
      </div>
      <div className="p-4">
        {loading ? (
          <div className="text-center py-8 text-muted-foreground">
            <div className="inline-block w-6 h-6 border-2 rounded-full animate-spin border-t-transparent"
              style={{ borderColor: 'var(--synapse-accent)', borderTopColor: 'transparent' }}></div>
            <p className="mt-2">Loading plugins...</p>
          </div>
        ) : plugins.length === 0 ? (
          <div className="text-center py-12 text-muted-foreground">
            <p className="text-lg mb-2">No plugins installed</p>
            <p className="text-sm">Add a plugin to extend functionality</p>
          </div>
        ) : (
          <div className="space-y-3">
            {plugins.map(plugin => (
              <div
                key={plugin.plugin_id}
                className="module-card p-4 hover:shadow-md transition-all duration-300"
              >
                <div className="flex justify-between items-start">
                  <div className="flex-1">
                    <div className="flex items-center gap-3 mb-2">
                      <h3 className="font-semibold">{plugin.name}</h3>
                      <span className="text-xs px-2 py-1 rounded bg-muted text-muted-foreground">
                        v{plugin.version}
                      </span>
                      <span className={`text-xs px-2 py-1 rounded ${plugin.enabled ? 'bg-success/10 text-success' : 'bg-muted text-muted-foreground'}`}>
                        {plugin.enabled ? 'Enabled' : 'Disabled'}
                      </span>
                    </div>
                    <p className="text-sm text-muted-foreground mb-2">{plugin.description}</p>
                    <div className="flex flex-wrap gap-1">
                      {plugin.permissions.map((perm, idx) => (
                        <span
                          key={idx}
                          className="px-2 py-0.5 text-xs rounded font-mono"
                          style={{
                            backgroundColor: 'color-mix(in oklch, var(--synapse-accent) 10%, transparent)',
                            color: 'var(--synapse-accent)',
                          }}
                        >
                          {perm}
                        </span>
                      ))}
                    </div>
                  </div>
                  <button
                    onClick={() => handleTogglePlugin(plugin.plugin_id, !plugin.enabled)}
                    className="px-4 py-2 rounded-md text-sm font-medium transition-all"
                    style={{
                      backgroundColor: plugin.enabled
                        ? 'color-mix(in oklch, var(--error) 10%, transparent)'
                        : 'color-mix(in oklch, var(--success) 10%, transparent)',
                      color: plugin.enabled ? 'var(--error)' : 'var(--success)',
                    }}
                  >
                    {plugin.enabled ? 'Disable' : 'Enable'}
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );

  const renderConnections = () => (
    <div className="module-card mb-6">
      <div className="flex items-center justify-between p-4 border-b border-border">
        <h2 className="text-xl font-semibold">Active Connections ({connections.length})</h2>
        <button
          className="synapse-button text-xs px-3 py-1.5 rounded-md"
          style={{
            backgroundColor: 'var(--synapse-accent)',
            color: 'var(--accent-foreground)',
          }}
        >
          + New Connection
        </button>
      </div>
      <div className="p-4">
        {connections.length === 0 ? (
          <div className="text-center py-8 text-muted-foreground">
            <p className="text-sm">No active connections</p>
          </div>
        ) : (
          <div className="space-y-3">
            {connections.map(connection => (
              <div
                key={connection.connection_id}
                className="module-card p-4"
              >
                <div className="flex justify-between items-center">
                  <div className="flex-1">
                    <div className="flex items-center gap-3 mb-1">
                      <h3 className="font-semibold">{connection.service_name}</h3>
                      <span className={`px-2 py-1 text-xs rounded ${
                        connection.status === 'connected'
                          ? 'bg-success/10 text-success'
                          : connection.status === 'error'
                          ? 'bg-error/10 text-error'
                          : 'bg-muted text-muted-foreground'
                      }`}>
                        {connection.status}
                      </span>
                    </div>
                    <p className="text-sm text-muted-foreground mb-1">
                      {connection.protocol.toUpperCase()} - {connection.endpoint}
                    </p>
                    {connection.authenticated && (
                      <p className="text-xs text-success">Authenticated</p>
                    )}
                    <div className="text-xs text-muted-foreground mt-1">
                      Last activity: {new Date(connection.last_activity).toLocaleString()}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );

  const renderSecurityLogs = () => {
    if (securityLogs.length === 0) return null;

    return (
      <div className="module-card">
        <div className="p-4 border-b border-border">
          <h2 className="text-xl font-semibold">Recent Security Logs</h2>
        </div>
        <div className="p-4 space-y-2">
          {securityLogs.slice(0, 10).map(log => (
            <div
              key={log.log_id}
              className="p-3 rounded text-sm"
              style={{
                backgroundColor: 'var(--muted)',
              }}
            >
              <div className="flex justify-between items-start mb-1">
                <span
                  className="font-medium capitalize"
                  style={{
                    color: log.severity === 'critical'
                      ? 'var(--error)'
                      : log.severity === 'warning'
                      ? 'var(--warning)'
                      : 'inherit',
                  }}
                >
                  {log.event_type.replace('_', ' ')}
                </span>
                <span className="text-xs text-muted-foreground">
                  {new Date(log.timestamp).toLocaleTimeString()}
                </span>
              </div>
              {log.details && Object.keys(log.details).length > 0 && (
                <p className="text-xs text-muted-foreground">
                  {JSON.stringify(log.details).substring(0, 100)}
                  {JSON.stringify(log.details).length > 100 && '...'}
                </p>
              )}
            </div>
          ))}
        </div>
      </div>
    );
  };

  return (
    <>
      {/* Success/Error Toast */}
      {actionSuccess && (
        <div className="fixed top-4 right-4 z-50 p-4 rounded-md shadow-lg animate-in slide-in-from-top"
          style={{
            backgroundColor: 'var(--success)',
            color: 'white',
          }}>
          {actionSuccess}
        </div>
      )}
