import React, { useState, useEffect, useCallback } from 'react';
import { PersonaModal, PersonaForm } from './PersonaModal';
import { MimicBridge } from '../../bridges/MimicBridge';
import type { MimicPersona } from '../../types/agents';
import { BackToLauncher } from '../shared/BackToLauncher';

// Extended Persona interface for UI display with computed properties
interface DisplayPersona extends MimicPersona {
  // UI-only computed property
  isActive: boolean;
}

export interface MimicDashboardProps {
  userId?: string;
  onBackToLauncher?: () => void;
}

export const MimicDashboard: React.FC<MimicDashboardProps> = ({ userId = 'default-user', onBackToLauncher }) => {
  const [personas, setPersonas] = useState<DisplayPersona[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [showPersonaModal, setShowPersonaModal] = useState(false);
  const [editingPersona, setEditingPersona] = useState<PersonaForm | null>(null);
  const [successMessage, setSuccessMessage] = useState<string | null>(null);

  const createBridge = useCallback(() => new MimicBridge({ userId }), [userId]);

  const loadPersonas = useCallback(async () => {
    setError(null);
    try {
      setLoading(true);
      const bridge = createBridge();
      await bridge.initialize();
      const response = await bridge.getPersonas();
      if (response.success && response.data) {
        setPersonas(response.data.map(p => ({ ...p, isActive: p.active })));
      } else if (!response.success) {
        setError(response.error || 'Failed to load personas.');
      }
    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to load personas.';
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  }, [createBridge]);

  useEffect(() => {
    loadPersonas();
  }, [loadPersonas]);

  useEffect(() => {
    if (successMessage) {
      const timer = setTimeout(() => setSuccessMessage(null), 3000);
      return () => clearTimeout(timer);
    }
  }, [successMessage]);

  const togglePersona = async (personaId: string) => {
    setError(null);
    try {
      setLoading(true);
      const bridge = createBridge();
      await bridge.initialize();
      const personaToToggle = personas.find(p => p.persona_id === personaId);
      if (!personaToToggle) {
        setError('Persona not found.');
        return;
      }
      // Switch persona activates it (only one can be active at a time)
      const response = await bridge.switchPersona(personaId);
      if (response.success && response.data) {
        // Update all personas: deactivate others, activate the selected one
        setPersonas(prev => prev.map(p => ({
          ...p,
          active: p.persona_id === personaId,
          isActive: p.persona_id === personaId
        })));
        setSuccessMessage(`Persona "${personaToToggle.name}" activated.`);
      } else if (!response.success) {
        setError(response.error || 'Failed to toggle persona.');
      }
    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to toggle persona.';
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const handleCreatePersona = async (personaForm: PersonaForm) => {
    setError(null);
    try {
      setLoading(true);
      const bridge = createBridge();
      await bridge.initialize();
      const response = await bridge.createPersona({
        name: personaForm.name,
        description: personaForm.description,
        traits: personaForm.traits,
        expertise_areas: personaForm.expertiseAreas.split(',').map(area => area.trim()).filter(Boolean),
        communication_style: personaForm.communicationStyle,
        active: false, // Newly created personas are not active by default
      });
      if (response.success && response.data) {
        setPersonas(prev => [...prev, { ...response.data, isActive: response.data.active }]);
        setSuccessMessage(`Persona "${response.data.name}" created successfully!`);
        setShowPersonaModal(false);
      } else if (!response.success) {
        setError(response.error || 'Failed to create persona.');
      }
    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to create persona.';
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const handleEditPersona = async (personaForm: PersonaForm) => {
    setError(null);
    if (!personaForm.id) {
      setError('Persona ID missing for update.');
      return;
    }
    try {
      setLoading(true);
      const bridge = createBridge();
      await bridge.initialize();
      const response = await bridge.updatePersona(personaForm.id, {
        name: personaForm.name,
        description: personaForm.description,
        expertise_areas: personaForm.expertiseAreas.split(',').map(area => area.trim()).filter(Boolean),
        communication_style: personaForm.communicationStyle,
        traits: personaForm.traits,
      });
      if (response.success && response.data) {
        setPersonas(prev => prev.map(p =>
          p.persona_id === response.data?.persona_id
            ? { ...response.data, isActive: response.data.active }
            : p
        ));
        setSuccessMessage(`Persona "${response.data.name}" updated successfully!`);
        setShowPersonaModal(false);
        setEditingPersona(null);
      } else if (!response.success) {
        setError(response.error || 'Failed to update persona.');
      }
    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to update persona.';
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const openEditModal = (persona: DisplayPersona) => {
    setEditingPersona({
      id: persona.persona_id,
      name: persona.name,
      description: persona.description,
      avatar: '/assets/Mimic.png', // Default avatar since MimicPersona doesn't have avatar
      tags: persona.expertise_areas.join(', '), // Use expertise_areas as tags for display
      traits: persona.traits,
      expertiseAreas: persona.expertise_areas.join(', '),
      communicationStyle: persona.communication_style,
    });
    setShowPersonaModal(true);
  };

  const closePersonaModal = () => {
    setShowPersonaModal(false);
    setEditingPersona(null);
  };

  if (loading && personas.length === 0) {
    return (
      <div className="min-h-screen bg-gradient-mythiq p-6 flex items-center justify-center">
        <div className="text-center">
          <div className="relative inline-block mb-6">
            {/* Pulsing cyan backlight */}
            <div
              className="absolute inset-0 rounded-full animate-pulse"
              style={{
                background: 'radial-gradient(circle, rgba(34, 211, 238, 0.4) 0%, rgba(34, 211, 238, 0) 70%)',
                transform: 'scale(1.5)',
                filter: 'blur(8px)',
              }}
            />
            {/* Mimic icon */}
            <img
              src="/assets/Mimic.png"
              alt="Mimic"
              className="relative w-20 h-20 object-contain animate-pulse"
              style={{
                filter: 'drop-shadow(0 0 15px rgba(34, 211, 238, 0.6)) drop-shadow(0 0 30px rgba(34, 211, 238, 0.3))',
              }}
            />
          </div>
          <div className="text-white text-lg font-medium">Loading personas...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-mythiq p-6">
      <div className="max-w-7xl mx-auto">
        <div className="module-card mb-6">
        <div className="flex items-center justify-between p-6 border-b border-border">
          <div className="flex items-center gap-4">
            {/* Back to Launcher Button */}
            {onBackToLauncher && (
              <BackToLauncher onClick={onBackToLauncher} variant="minimal" />
            )}

            <div className="flex items-center gap-3">
              <img
                src="/assets/Mimic.png"
                alt="Mimic"
                className="w-12 h-12 object-contain"
                style={{
                  filter: 'drop-shadow(0 0 15px color-mix(in oklch, var(--mimic-accent) 40%, transparent))'
                }}
              />
              <div>
                <h1 className="text-2xl font-semibold">Mimic Persona Forge</h1>
                <p className="text-xs text-muted-foreground">Dynamic Persona Generator & Manager</p>
              </div>
            </div>
          </div>
          <div className="flex items-center gap-2">
            <span className="w-2 h-2 rounded-full animate-pulse" style={{ backgroundColor: 'var(--mimic-accent)' }}></span>
            <span className="text-sm" style={{ color: 'var(--mimic-accent)' }}>Adapting</span>
          </div>
        </div>
        </div>

        {error && (
          <div
            className="mb-6 p-4 rounded-md"
            style={{
              backgroundColor: 'color-mix(in oklch, var(--destructive) 20%, transparent)',
              borderColor: 'var(--destructive)',
              color: 'var(--destructive)',
              border: '1px solid var(--destructive)',
            }}
          >
            {error}
          </div>
        )}

        <div className="module-card">
          <div className="p-4 border-b border-border flex justify-between items-center">
            <h2 className="text-xl font-semibold">Persona Library</h2>
            <button
              onClick={() => setShowPersonaModal(true)}
              className="px-4 py-2 text-sm font-medium rounded-lg transition-opacity hover:opacity-90"
              style={{
                backgroundColor: 'var(--mimic-accent)',
                color: 'var(--accent-foreground)',
              }}
            >
              + Create New Persona
            </button>
          </div>
          <div className="p-4 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {personas.length === 0 ? (
              <div className="col-span-full text-center py-8 text-muted-foreground">
                No personas created yet. Click &quot;Create New Persona&quot; to get started.
              </div>
            ) : (
              personas.map(persona => (
                <div
                  key={persona.persona_id}
                  className="p-6 rounded-lg border-2 transition-all duration-300"
                  style={{
                    borderColor: persona.isActive
                      ? 'var(--mimic-accent)'
                      : 'var(--border)',
                    backgroundColor: persona.isActive
                      ? 'color-mix(in oklch, var(--mimic-accent) 10%, var(--card))'
