import React, { useEffect, useMemo, useState } from 'react';
import { useSystemBridge } from '../../hooks';
import { SystemTelemetry } from '../../types/agents';

export const MythTechDashboard: React.FC = () => {
  const { bridge, props } = useSystemBridge();
  const [telemetry, setTelemetry] = useState<SystemTelemetry | null>(props?.telemetry ?? null);

  useEffect(() => {
    setTelemetry(props?.telemetry ?? null);
  }, [props]);

  useEffect(() => {
    if (!bridge) return;

    const unsubscribe = bridge.subscribe((event: any) => {
      if (event?.type === 'telemetry') {
        setTelemetry(event.data as SystemTelemetry);
      }
    });

    bridge.startTelemetryStream(1500);

    return () => {
      unsubscribe?.();
      bridge.stopTelemetryStream();
    };
  }, [bridge]);

  const { cpuDisplay, latencyDisplay, cpuPercent, latencyPercent } = useMemo(() => {
    const cpuValue = telemetry?.cpuLoad ?? null;
    const latencyValue = telemetry?.latencyMs ?? null;
    const latencyPercent =
      latencyValue !== null ? Math.min(latencyValue, 250) / 2.5 : 0;
    return {
      cpuDisplay: cpuValue !== null ? `${cpuValue.toFixed(0)}%` : '--',
      latencyDisplay: latencyValue !== null ? `${latencyValue}ms` : '--',
      latencyPercent,
      cpuPercent: cpuValue !== null ? Math.min(Math.max(cpuValue, 0), 100) : 0,
