  onEventClick: (event: CalendarEvent) => void;
  onNewTask: () => void;
  onNewEvent: () => void;
}

// Memoized MetricCard component  
const MetricCard = memo<{
  title: string;
  value: number;
  subtitle: string;
  accentColor: 'cyan' | 'gold' | 'green';
