const ProjectOverview: React.FC<{ project: Project | null }> = ({ project }) => {
  if (!project) {
    return null;
  }

  return (
    <div className="space-y-6">
      <div>
        <h3 className="text-lg font-medium text-gray-900">Project Information</h3>
        <dl className="mt-4 grid grid-cols-1 gap-x-4 gap-y-6 sm:grid-cols-2">
          <div className="sm:col-span-2">
            <dt className="text-sm font-medium text-gray-500">Description</dt>
            <dd className="mt-1 text-sm text-gray-900">{project.description}</dd>
          </div>
          <div>
            <dt className="text-sm font-medium text-gray-500">Status</dt>
            <dd className="mt-1 text-sm text-gray-900 capitalize">{project.status.replace('_', ' ')}</dd>
          </div>
          <div>
            <dt className="text-sm font-medium text-gray-500">Priority</dt>
            <dd className="mt-1 text-sm text-gray-900 capitalize">{project.priority}</dd>
          </div>
          <div>
            <dt className="text-sm font-medium text-gray-500">Progress</dt>
            <dd className="mt-1">
              <div className="flex items-center gap-2">
                <div className="h-2 flex-1 rounded-full bg-gray-200">
                  <div
                    className="h-2 rounded-full bg-indigo-500 transition-all"
                    style={{ width: `${Math.min(project.progressPercentage ?? 0, 100)}%` }}
                  />
                </div>
                <span className="text-sm text-gray-900">{Math.round(project.progressPercentage ?? 0)}%</span>
              </div>
            </dd>
          </div>
          <div>
            <dt className="text-sm font-medium text-gray-500">Owner</dt>
            <dd className="mt-1 text-sm text-gray-900">{project.ownerId}</dd>
          </div>
          {project.startDate && (
            <div>
              <dt className="text-sm font-medium text-gray-500">Start Date</dt>
              <dd className="mt-1 text-sm text-gray-900">
                {new Date(project.startDate).toLocaleDateString()}
              </dd>
            </div>
          )}
          {project.targetEndDate && (
            <div>
              <dt className="text-sm font-medium text-gray-500">Target End Date</dt>
              <dd className="mt-1 text-sm text-gray-900">
                {new Date(project.targetEndDate).toLocaleDateString()}
              </dd>
            </div>
          )}
          {project.budget && (
            <div>
              <dt className="text-sm font-medium text-gray-500">Budget</dt>
              <dd className="mt-1 text-sm text-gray-900">
                ${project.budget.toLocaleString()} / ${project.budgetSpent.toLocaleString()} spent
              </dd>
            </div>
          )}
        </dl>
      </div>

      {project.teamMembers?.length ? (
        <div>
          <h4 className="text-sm font-medium text-gray-500 mb-2">Team Members</h4>
          <div className="flex flex-wrap gap-2">
            {project.teamMembers.map((member) => (
              <span
                key={member}
                className="inline-flex items-center rounded-full bg-indigo-50 px-3 py-1 text-sm font-medium text-indigo-700"
              >
                {member}
              </span>
            ))}
          </div>
        </div>
      ) : null}

      {project.tags?.length ? (
        <div>
          <h4 className="text-sm font-medium text-gray-500 mb-2">Tags</h4>
          <div className="flex flex-wrap gap-2">
            {project.tags.map((tag) => (
              <span
                key={tag}
                className="inline-flex items-center rounded-full bg-gray-100 px-3 py-1 text-sm font-medium text-gray-700"
              >
                {tag}
              </span>
            ))}
          </div>
        </div>
      ) : null}
    </div>
  );
};

