import React, { useState } from 'react';
import { Rnd } from 'react-rnd';
import { Activity, Brain, Heart, Zap, Shield, GripHorizontal } from '@/components/ui/HearthlinkIcons';

interface MetricCardProps {
  title: string;
  value: string | number;
  subtitle: string;
  accentColor: 'violet' | 'teal' | 'rose';
  icon: React.ReactNode;
  active?: boolean;
}

const MetricCard: React.FC<MetricCardProps> = ({ title, value, subtitle, accentColor, icon, active }) => {
  const styles = {
    violet: { text: 'text-violet-400', iconColor: 'text-violet-400', border: 'border-violet-500/30', shadow: 'shadow-[0_0_20px_rgba(139,92,246,0.15)]', bg: 'bg-violet-500/10' },
    teal: { text: 'text-teal-400', iconColor: 'text-teal-400', border: 'border-teal-500/30', shadow: 'shadow-[0_0_20px_rgba(45,212,191,0.15)]', bg: 'bg-teal-500/10' },
    rose: { text: 'text-rose-400', iconColor: 'text-rose-400', border: 'border-rose-500/30', shadow: 'shadow-[0_0_20px_rgba(251,113,133,0.15)]', bg: 'bg-rose-500/10' },
  };

  const style = styles[accentColor];
  
  return (
    <div className={`relative h-full flex flex-col justify-between p-5 rounded-xl backdrop-blur-xl border transition-all duration-300 ${style.bg} ${style.border} ${style.shadow}`}>
      <div className="flex justify-between items-start">
        <div className={`p-2 rounded-lg bg-black/20 ${style.iconColor}`}>{icon}</div>
        {active && <div className="w-2 h-2 rounded-full bg-white animate-pulse shadow-[0_0_10px_white]" />}
      </div>
      <div>
        <div className={`text-3xl font-bold mb-1 tracking-tight font-orbitron-force text-white`}>
          {value}
        </div>
        <div className="text-white/60 font-medium text-[10px] uppercase tracking-[0.2em] mb-1">
          {title}
        </div>
        <div className={`text-[10px] uppercase tracking-wider ${style.text}`}>
          {subtitle}
        </div>
      </div>
    </div>
  );
};

interface AliceOverviewProps {
  metrics: {
    focus: number;
    stress: number;
    load: string;
    energy: string;
  };
  onNavigateTo?: (view: string) => void;
}

export const AliceOverview: React.FC<AliceOverviewProps> = ({ metrics, onNavigateTo }) => {
  const [layout, setLayout] = useState([
    { id: 'focus', x: 0, y: 0, w: 300, h: 160 },
    { id: 'stress', x: 320, y: 0, w: 300, h: 160 },
    { id: 'load', x: 640, y: 0, w: 300, h: 160 },
    { id: 'energy', x: 960, y: 0, w: 300, h: 160 },
    { id: 'analysis', x: 0, y: 180, w: 800, h: 400 },
    { id: 'interventions', x: 820, y: 180, w: 440, h: 400 },
  ]);

  const updateLayout = (id: string, d: { x: number; y: number; w?: number; h?: number }) => {
    setLayout(prev => prev.map(item => item.id === id ? { ...item, ...d } : item));
  };

  return (
    <div className="relative w-full h-full overflow-hidden">
      {/* Background Ambient Glow */}
      <div className="absolute top-0 left-0 w-full h-full bg-[radial-gradient(circle_at_50%_50%,rgba(139,92,246,0.05),transparent_70%)] pointer-events-none" />

      {layout.map(item => (
        <Rnd
          key={item.id}
          size={{ width: item.w, height: item.h }}
          position={{ x: item.x, y: item.y }}
          onDragStop={(e, d) => updateLayout(item.id, { x: d.x, y: d.y })}
          onResizeStop={(e, direction, ref, delta, position) => {
            updateLayout(item.id, { 
              w: parseInt(ref.style.width), 
              h: parseInt(ref.style.height),
              x: position.x,
              y: position.y
            });
          }}
          bounds="parent"
          dragHandleClassName="drag-handle"
          className="z-10"
        >
          <div className="w-full h-full">
            {item.id === 'focus' && (
              <MetricCard 
                title="Focus Score" 
                value={metrics.focus} 
                subtitle="Flow State Detected" 
                accentColor="violet" 
                icon={<Brain size={20} />}
                active={true}
              />
            )}
            {item.id === 'stress' && (
              <MetricCard 
                title="Stress Level" 
                value={`${metrics.stress}%`} 
                subtitle="Optimal Range" 
                accentColor="teal" 
                icon={<Heart size={20} />}
              />
            )}
            {item.id === 'load' && (
              <MetricCard 
                title="Cognitive Load" 
                value={metrics.load} 
                subtitle="Sustainable" 
                accentColor="violet" 
                icon={<Activity size={20} />}
              />
            )}
            {item.id === 'energy' && (
              <MetricCard 
                title="Energy" 
                value={metrics.energy} 
                subtitle="Peak Performance" 
                accentColor="rose" 
                icon={<Zap size={20} />}
              />
            )}
            {item.id === 'analysis' && (
              <div className="w-full h-full bg-slate-900/80 backdrop-blur-xl border border-violet-500/20 rounded-xl overflow-hidden flex flex-col shadow-[0_0_30px_rgba(0,0,0,0.3)]">
                <div className="h-10 bg-white/5 border-b border-white/5 flex items-center justify-between px-4 drag-handle cursor-move">
                  <div className="flex items-center gap-2 text-violet-400">
                    <Activity size={16} />
                    <span className="text-xs font-bold uppercase tracking-widest font-orbitron-force">Live Analysis</span>
                  </div>
                  <GripHorizontal size={16} className="text-white/20" />
                </div>
                <div className="flex-1 flex items-center justify-center relative">
                   <div className="absolute inset-0 bg-[radial-gradient(circle_at_50%_50%,rgba(139,92,246,0.1),transparent)]" />
                   <div className="text-slate-500 italic text-sm relative z-10">Waiting for behavioral telemetry...</div>
                </div>
              </div>
            )}
            {item.id === 'interventions' && (
              <div className="w-full h-full bg-slate-900/80 backdrop-blur-xl border border-teal-500/20 rounded-xl overflow-hidden flex flex-col shadow-[0_0_30px_rgba(0,0,0,0.3)]">
                <div className="h-10 bg-white/5 border-b border-white/5 flex items-center justify-between px-4 drag-handle cursor-move">
                  <div className="flex items-center gap-2 text-teal-400">
                    <Shield size={16} />
                    <span className="text-xs font-bold uppercase tracking-widest font-orbitron-force">Interventions</span>
                  </div>
                  <GripHorizontal size={16} className="text-white/20" />
                </div>
                <div className="flex-1 p-4 space-y-3 overflow-y-auto scrollbar-thin scrollbar-thumb-teal-500/20 scrollbar-track-transparent">
                  <div className="p-4 bg-violet-500/10 border border-violet-500/20 rounded-lg group hover:bg-violet-500/20 transition-colors cursor-pointer">
                    <h3 className="text-violet-400 font-bold mb-1 text-sm group-hover:text-violet-300">Focus Timer</h3>
                    <p className="text-[10px] text-slate-400 mb-3">Initiate a 25-minute deep work session.</p>
                    <button 
                      onClick={() => onNavigateTo?.('deep_work')}
                      className="w-full py-1.5 bg-violet-600 hover:bg-violet-500 text-white rounded text-[10px] uppercase tracking-wider font-bold transition-colors shadow-lg shadow-violet-900/20"
                    >
                      Start Session
                    </button>
                  </div>
                  <div className="p-4 bg-teal-500/10 border border-teal-500/20 rounded-lg group hover:bg-teal-500/20 transition-colors cursor-pointer">
                    <h3 className="text-teal-400 font-bold mb-1 text-sm group-hover:text-teal-300">Decompress</h3>
                    <p className="text-[10px] text-slate-400 mb-3">Guided breathing to lower stress.</p>
                    <button className="w-full py-1.5 bg-teal-600 hover:bg-teal-500 text-white rounded text-[10px] uppercase tracking-wider font-bold transition-colors shadow-lg shadow-teal-900/20">
                      Begin
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>
        </Rnd>
      ))}
    </div>
  );
};

