import React, { useState, useEffect, useRef, useMemo, useCallback } from 'react';

import { UnifiedCommandCenterOptimized as UnifiedCommandCenter, type UnifiedTabItem } from '../shared/premium/UnifiedCommandCenter.optimized';

import { AliceBridge } from '../../bridges/AliceBridge';
import { AliceOverview } from './views/AliceOverview';
import { AliceDeepWorkSession } from './AliceDeepWorkSession';
import { AliceReflections } from './views/AliceReflections';
import { AliceProfile } from './views/AliceProfile';
import { PrivateChannel } from '../shared/chat/PrivateChannel';
import { useChatStateWithOrchestration } from '../shared/chat/useChatStateWithOrchestration';
import { MODULE_RESIDENT_AI, type SystemPersonaId } from '../shared/chat/types';
import { UnifiedChatContainer } from '../shared/chat/UnifiedChatContainer';

export interface AliceCommandCenterProps {
  userId: string;
  onBackToLauncher?: () => void;
  onNavigateToModule?: (moduleName: string) => void;
}

type ViewMode = 'overview' | 'deep_work' | 'reflections' | 'profile' | 'chat';

export const AliceCommandCenter: React.FC<AliceCommandCenterProps> = ({ userId, onBackToLauncher, onNavigateToModule }) => {
  const [activeView, setActiveView] = useState<ViewMode>('overview');
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);
  const [isConnected, setIsConnected] = useState(true);
  const [metrics, setMetrics] = useState({
    focus: 0,
    stress: 0,
    load: 'Low',
    energy: 'Stable'
  });

  const aliceBridge = useRef(new AliceBridge({ userId }));

  // Chat Integration
  const moduleId = 'alice';
  const residentPersonaId = (MODULE_RESIDENT_AI[moduleId] || 'alice') as SystemPersonaId;
  const {
    personas: chatPersonas,
    privateMessages,
    privateLoading,
    privateError,
    privateTyping,
    sendPrivateMessage,
  } = useChatStateWithOrchestration({
    userId,
    moduleId,
    residentPersonaId,
    onError: (err) => console.error('Chat Error:', err),
  });

  const residentPersona = chatPersonas.find((p) => p.id === residentPersonaId);

  useEffect(() => {
    const loadData = async () => {
      try {
        await aliceBridge.current.initialize();
        const profile = await aliceBridge.current.getProfile();
        const attention = await aliceBridge.current.analyzeAttentionPatterns();
        
        if (profile.success && attention.success) {
           // Simulate real-time metrics for now based on profile data
           setMetrics({
             focus: Math.round(attention.data.focus_duration_avg || 75),
             stress: 25, // Placeholder until real-time stress stream is ready
             load: 'Optimal',
             energy: 'High'
           });
        }
      } catch (err) {
        console.error("Failed to load Alice data", err);
        setIsConnected(false);
      }
    };
    loadData();
  }, [userId]);

  useEffect(() => {
    if (typeof document === 'undefined') {
      return;
    }
    document.body.classList.add('alice-theme');
    return () => {
      document.body.classList.remove('alice-theme');
    };
  }, []);

  const tabs: UnifiedTabItem[] = [
    { id: 'overview', label: 'Overview' },
    { id: 'deep_work', label: 'Deep Work' },
    { id: 'reflections', label: 'Reflections' },
    { id: 'profile', label: 'Profile' },
    { id: 'chat', label: 'Chat' }
  ];

  const renderContent = useCallback(() => {
    if (activeView === 'chat') {
      return (
        <UnifiedChatContainer
          userId={userId}
          moduleId="alice"
          residentPersonaId={residentPersonaId}
          onClose={() => setActiveView('overview')}
          className="h-full w-full"
        />
      );
    }

    switch (activeView) {
      case 'overview': return <AliceOverview metrics={metrics} onNavigateTo={(view) => setActiveView(view as ViewMode)} />;
      case 'deep_work': return <AliceDeepWorkSession userId={userId} onNavigateToModule={onNavigateToModule} />;
      case 'reflections': return <AliceReflections />;
      case 'profile': return <AliceProfile />;
      default: return <AliceOverview metrics={metrics} />;
    }
  }, [activeView, userId, metrics, residentPersonaId, onNavigateToModule]);

  const sidebar = useMemo(() => {
    if (activeView === 'chat' || !residentPersona) return null;

    return (
      <PrivateChannel
        residentPersona={residentPersona}
        messages={privateMessages}
        isLoading={privateLoading}
        error={privateError}
        isTyping={privateTyping}
        onSendMessage={sendPrivateMessage}
        isCollapsed={isSidebarCollapsed}
        onToggleCollapse={() => setIsSidebarCollapsed(prev => !prev)}
        onStandardView={() => setActiveView('chat')}
        className="h-full border-l border-blue-400/20"
      />
    );
  }, [activeView, residentPersona, isSidebarCollapsed, privateMessages, privateLoading, privateError, privateTyping, sendPrivateMessage]);

  return (
    <>
      <UnifiedCommandCenter
        title="Alice"
        subtitle="Behavioral Insight"
        iconPath="/assets/Alice.png"
        tabs={tabs}
        activeTab={activeView}
        onTabChange={(tabId) => setActiveView(tabId as ViewMode)}
        onBackToLauncher={onBackToLauncher}
        onNavigateToModule={onNavigateToModule}
        statusLabel={isConnected ? 'Monitoring Active' : 'Offline'}
        statusTone={isConnected ? 'success' : 'error'}
        accentColor="#2dd4bf"
        themeColor="#a78bfa"
        sidebar={sidebar}
        withFrame={activeView !== 'chat'}
        fullWidthContent={activeView === 'chat'}
        sidebarCollapsed={isSidebarCollapsed}
        headerStyle={{
           background: 'linear-gradient(90deg, #130a26 0%, #1e1b4b 50%, #130a26 100%)', // Deep Indigo/Purple gradient
           borderBottom: '3px solid #2dd4bf', // Teal border
           boxShadow: '0 8px 30px rgba(0,0,0,0.8), inset 0 -2px 0 rgba(45,212,191,0.6), inset 0 2px 0 rgba(167,139,250,0.5)'
        }}
        titleColor="#2dd4bf" // Teal title
        subtitleColor="#e2e8f0"
      >
        {renderContent()}
      </UnifiedCommandCenter>
      
      <style>
        {`
          .alice-theme .text-alden-gold { color: #2dd4bf !important; } /* Override gold with teal */
          .alice-theme .border-alden-gold { border-color: #2dd4bf !important; }
        `}
      </style>
    </>
  );
};

export default AliceCommandCenter;

