/**
 * KnowledgeGraphViewer renders Vault knowledge graph inside dashboard.
 * Combines D3.js force simulation with CRUD controls for entities and relationships.
 */
import React, {
  useCallback,
  useEffect,
  useMemo,
  useRef,
  useState,
} from 'react';
import * as d3 from 'd3';
import { VaultBridge, type GraphPath } from '../../bridges/VaultBridge';
import {
  GraphFilters,
  GraphLinkDatum,
  GraphNodeDatum,
  GraphStats,
  KnowledgeGraphData,
  NodeFormState,
  PathSearchState,
  RelationshipFormState,
} from '../../types/knowledge-graph';

interface KnowledgeGraphViewerProps {
  userId: string;
}

const DEFAULT_NODE_LIMIT = 250;
const ENTITY_FALLBACK_TYPES = ['person', 'project', 'task', 'idea', 'memory', 'context'];

const emptyNodeForm: NodeFormState = {
  entityType: 'concept',
  name: '',
  description: '',
  tags: '',
  metadata: '',
};

const emptyRelationshipForm: RelationshipFormState = {
  sourceId: '',
  targetId: '',
  relationshipType: 'related_to',
  weight: 1,
  metadata: '',
};

const DEFAULT_PATH_STATE: PathSearchState = {
  sourceId: '',
  targetId: '',
  maxHops: 3,
  loading: false,
  results: [],
};
export const KnowledgeGraphViewer: React.FC<KnowledgeGraphViewerProps> = ({ userId }) => {
  const [graphData, setGraphData] = useState<KnowledgeGraphData>({ nodes: [], links: [] });
  const [graphStats, setGraphStats] = useState<GraphStats | null>(null);
  const [filters, setFilters] = useState<GraphFilters>({
    entityType: 'all',
    relationshipType: 'all',
    searchQuery: '',
    limit: DEFAULT_NODE_LIMIT,
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [statusMessage, setStatusMessage] = useState<string | null>(null);
  const [selectedNodeId, setSelectedNodeId] = useState<string | null>(null);
  const [selectedRelationshipId, setSelectedRelationshipId] = useState<string | null>(null);
  const [nodeForm, setNodeForm] = useState<NodeFormState>(emptyNodeForm);
  const [relationshipForm, setRelationshipForm] = useState<RelationshipFormState>(
    emptyRelationshipForm
  );
  const [savingNode, setSavingNode] = useState(false);
  const [savingRelationship, setSavingRelationship] = useState(false);
  const [chargeStrength, setChargeStrength] = useState(-200);
  const [linkDistance, setLinkDistance] = useState(140);
  const [pathState, setPathState] = useState<PathSearchState>(DEFAULT_PATH_STATE);
  const [activePathIndex, setActivePathIndex] = useState<number | null>(null);
  const graphRef = useRef<SVGSVGElement | null>(null);
  const graphContainerRef = useRef<HTMLDivElement | null>(null);
  const [dimensions, setDimensions] = useState({ width: 960, height: 560 });
  const [renderTime, setRenderTime] = useState<number | null>(null);
  const [showPerformanceTest, setShowPerformanceTest] = useState(false);

  // Helper to parse properties from JSON string
  const parseGraphNodeProperties = useCallback((props: string | null): Record<string, unknown> | null => {
    if (!props) return null;
    try {
      return JSON.parse(props);
    } catch {
      return null;
    }
  }, []);

  // Helper to get display name from node
  const getNodeDisplayName = useCallback((node: GraphNodeDatum): string => {
    if (node.name) return node.name;
    const props = parseGraphNodeProperties(node.properties);
    if (props?.name) return props.name as string;
    return node.entity_type;
  }, [parseGraphNodeProperties]);

  const entityTypeOptions = useMemo(() => {
    if (!graphStats) {
      return ENTITY_FALLBACK_TYPES;
    }
    const entries = Object.keys(graphStats.entity_types || {});
    return entries.length ? entries : ENTITY_FALLBACK_TYPES;
  }, [graphStats]);

  const relationshipTypeOptions = useMemo(() => {
    if (!graphStats) {
      return ['related_to', 'influences', 'depends_on', 'contains'];
    }
    const entries = Object.keys(graphStats.relationship_types || {});
    return entries.length ? entries : ['related_to', 'influences', 'depends_on', 'contains'];
  }, [graphStats]);

  const activePath: GraphPath | null = useMemo(() => {
    if (activePathIndex == null || activePathIndex < 0) return null;
    return pathState.results[activePathIndex] ?? null;
  }, [activePathIndex, pathState.results]);
  const highlightNodeIds = useMemo(() => {
    const ids = new Set<string>();
    if (selectedNodeId) ids.add(selectedNodeId);
    if (activePath) {
      activePath.entities.forEach((entity) => ids.add(entity.id));
    }
    return ids;
  }, [selectedNodeId, activePath]);

  const highlightRelationshipIds = useMemo(() => {
    const ids = new Set<string>();
    if (selectedRelationshipId) ids.add(selectedRelationshipId);
    if (activePath) {
      activePath.relationships.forEach((rel) => ids.add(rel.id));
    }
    return ids;
  }, [selectedRelationshipId, activePath]);



  const filteredNodes = useMemo(() => {
    if (!filters.searchQuery.trim()) {
      return graphData.nodes;
    }
    const query = filters.searchQuery.toLowerCase();
    return graphData.nodes.filter((node) => {
      const props = parseGraphNodeProperties(node.properties);
      const name = (node.name || props?.name as string || node.entity_type).toLowerCase();
      const description = (node.description || props?.description as string || '').toLowerCase();
      const tags = Array.isArray(props?.tags)
        ? (props.tags as string[]).join(',').toLowerCase()
        : '';
      return name.includes(query) || description.includes(query) || tags.includes(query);
    });
  }, [graphData.nodes, filters.searchQuery, parseGraphNodeProperties]);

  const filteredLinks = useMemo(() => {
    if (filters.relationshipType === 'all') {
      return graphData.links;
    }
    return graphData.links.filter((link) => link.relationship_type === filters.relationshipType);
  }, [graphData.links, filters.relationshipType]);

  const graphToRender = useMemo<KnowledgeGraphData>(() => {
    const nodes = filteredNodes.slice(0, filters.limit);
    const nodeIds = new Set(nodes.map((node) => node.id));
    const links = filteredLinks.filter(
      (link) =>
        nodeIds.has(typeof link.source === 'string' ? link.source : link.source.id) &&
        nodeIds.has(typeof link.target === 'string' ? link.target : link.target.id)
    );
    return { nodes, links };
  }, [filteredNodes, filteredLinks, filters.limit]);

  const handleResize = useCallback(() => {
    if (!graphContainerRef.current) return;
    const width = graphContainerRef.current.clientWidth;
    const height = Math.max(480, Math.round(width * 0.55));
    setDimensions({ width, height });
  }, []);

  const clearBanner = useCallback(() => {
    setError(null);
    setStatusMessage(null);
  }, []);

  const resetForms = useCallback(() => {
    setNodeForm(emptyNodeForm);
    setRelationshipForm(emptyRelationshipForm);
    setSelectedNodeId(null);
    setSelectedRelationshipId(null);
  }, []);

  const generateTestGraph = useCallback((nodeCount: number) => {
    const now = Math.floor(Date.now() / 1000);
    const testNodes: GraphNodeDatum[] = Array.from({ length: nodeCount }, (_, i) => ({
      id: `test-node-${i}`,
      user_id: userId,
      persona_id: 'vault',
      entity_type: ['concept', 'person', 'project', 'task', 'idea'][i % 5],
      name: `Test Entity ${i}`,
      description: `Auto-generated test node for performance testing`,
      properties: JSON.stringify({
        tags: ['test', 'performance'],
      }),
      memory_slice_id: null,
      confidence: 0.8,
      created_at: now,
      updated_at: now,
      accessed_at: now,
      access_count: 0,
    }));

    const testLinks: GraphLinkDatum[] = [];
    for (let i = 0; i < nodeCount * 2; i++) {
      const sourceIdx = Math.floor(Math.random() * nodeCount);
      const targetIdx = Math.floor(Math.random() * nodeCount);
      if (sourceIdx !== targetIdx) {
        testLinks.push({
          id: `test-rel-${i}`,
          user_id: userId,
          persona_id: 'vault',
          source: `test-node-${sourceIdx}`,
          target: `test-node-${targetIdx}`,
          source_entity_id: `test-node-${sourceIdx}`,
          target_entity_id: `test-node-${targetIdx}`,
          relationship_type: ['related_to', 'influences', 'depends_on', 'contains'][i % 4],
          strength: Math.random() * 2,
          properties: null,
          memory_slice_id: null,
          bidirectional: false,
          created_at: now,
          updated_at: now,
        });
      }
    }

    setGraphData({ nodes: testNodes, links: testLinks });
    setStatusMessage(`Generated test graph with ${nodeCount} nodes and ${testLinks.length} edges`);
    setTimeout(() => setStatusMessage(null), 3000);
  }, [userId]);

  const clearPathHighlight = useCallback(() => {
    setActivePathIndex(null);
    setPathState((prev) => ({ ...prev, results: [] }));
  }, []);

  const parseMetadata = (metadata: string) => {
    if (!metadata.trim()) return undefined;
    try {
      return JSON.parse(metadata);
    } catch (err) {
      throw new Error('Metadata must be valid JSON');
    }
  };
  const fetchGraphData = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      // Build query for findEntities
      const entitiesQuery = {
        user_id: userId,
        entity_types: filters.entityType === 'all' ? undefined : [filters.entityType],
        max_depth: 10,
      };

      const [entitiesResponse, statsResponse] = await Promise.all([
        VaultBridge.findEntities(entitiesQuery),
        VaultBridge.getGraphStats(userId),
      ]);

      if (!entitiesResponse.success) {
        throw new Error(entitiesResponse.error);
      }

      if (!statsResponse.success) {
        throw new Error(statsResponse.error);
      }

      const entities = entitiesResponse.data;
      const stats = statsResponse.data;

      // Parse properties from JSON string if needed
      const parseProperties = (props: string | null): Record<string, unknown> | null => {
        if (!props) return null;
        try {
          return JSON.parse(props);
        } catch {
          return null;
        }
      };

      const uniqueEntities = filters.searchQuery
        ? entities.filter((entity) => {
            const query = filters.searchQuery.toLowerCase();
            const props = parseProperties(entity.properties);
            const name = (props?.name as string || entity.name || entity.entity_type).toLowerCase();
            const description = (props?.description as string || entity.description || '').toLowerCase();
            const tags = Array.isArray(props?.tags)
              ? (props.tags as string[]).join(',').toLowerCase()
              : '';
            return name.includes(query) || description.includes(query) || tags.includes(query);
          })
        : entities;

      const limitedEntities = filters.limit > 0 ? uniqueEntities.slice(0, filters.limit) : uniqueEntities;
      const entityMap = new Map(limitedEntities.map((entity) => [entity.id, entity]));

      // Get relationships for each entity
      const allRelationships: GraphLinkDatum[] = [];
      for (const entity of limitedEntities) {
        const relResponse = await VaultBridge.getRelationships(entity.id, 'Both');
        if (relResponse.success) {
          for (const rel of relResponse.data) {
            // Only add if both source and target are in our entity set
            if (entityMap.has(rel.source_entity_id) && entityMap.has(rel.target_entity_id)) {
              // Avoid duplicates
              if (!allRelationships.find(r => r.id === rel.id)) {
                if (filters.relationshipType === 'all' || rel.relationship_type === filters.relationshipType) {
                  allRelationships.push({
                    ...rel,
                    source: rel.source_entity_id,
                    target: rel.target_entity_id,
                  });
                }
              }
            }
          }
        }
      }

      setGraphData({
        nodes: limitedEntities.map((entity) => ({ ...entity })) as GraphNodeDatum[],
        links: allRelationships,
      });
      setGraphStats(stats as GraphStats);
    } catch (err: unknown) {
      console.error('[KG] Failed to load graph data', err);
      setError(err instanceof Error ? err.message : 'Failed to load knowledge graph data');
    } finally {
      setLoading(false);
    }
  }, [filters, userId]);

  const refreshGraph = useCallback(async () => {
    setStatusMessage('Refreshing knowledge graph.');
    await fetchGraphData();
    setStatusMessage('Knowledge graph updated');
    setTimeout(() => setStatusMessage(null), 2500);
  }, [fetchGraphData]);

  useEffect(() => {
    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, [handleResize]);

  useEffect(() => {
    fetchGraphData();
  }, [fetchGraphData]);
  useEffect(() => {
    const svgElement = graphRef.current;
    if (!svgElement) return;

    const startTime = performance.now();

    const nodes: GraphNodeDatum[] = graphToRender.nodes.map((node) => ({ ...node }));
    const links: GraphLinkDatum[] = graphToRender.links.map((link) => ({ ...link }));

    const svg = d3.select(svgElement);
    svg.selectAll('*').remove();
    svg.attr('width', dimensions.width).attr('height', dimensions.height);

    if (!nodes.length) {
      svg
        .append('text')
        .attr('x', dimensions.width / 2)
        .attr('y', dimensions.height / 2)
        .attr('text-anchor', 'middle')
        .attr('fill', 'var(--muted-foreground)')
        .text('No knowledge graph entities yet.');
      setRenderTime(null);
      return;
    }

    const linkForce = d3
      .forceLink<GraphNodeDatum, GraphLinkDatum>(links)
      .id((d) => d.id)
      .distance(linkDistance)
      .strength(0.75);

    const simulation = d3
      .forceSimulation(nodes)
      .force('link', linkForce)
      .force('charge', d3.forceManyBody().strength(chargeStrength))
      .force('center', d3.forceCenter(dimensions.width / 2, dimensions.height / 2))
      .force('collision', d3.forceCollide(40));

    const linkGroup = svg.append('g').attr('stroke', 'var(--border)').attr('stroke-opacity', 0.5);

    const link = linkGroup
      .selectAll('line')
      .data(links)
      .join('line')
      .attr('stroke-width', (d) =>
        highlightRelationshipIds.has(d.id) ? 3 : Math.max(1, d.strength ?? 1)
      )
      .attr('stroke', (d) =>
        highlightRelationshipIds.has(d.id) ? '#fbbf24' : 'var(--border)'
      )
      .attr('stroke-opacity', (d) =>
        highlightRelationshipIds.has(d.id) ? 0.9 : 0.5
      )
      .style('cursor', 'pointer')
      .on('click', (event, datum) => {
        event.stopPropagation();
        setSelectedRelationshipId(datum.id);
        setSelectedNodeId(null);
        const propsObj = datum.properties ? JSON.parse(datum.properties) : {};
        setRelationshipForm({
          id: datum.id,
          sourceId: typeof datum.source === 'string' ? datum.source : datum.source.id,
          targetId: typeof datum.target === 'string' ? datum.target : datum.target.id,
          relationshipType: datum.relationship_type,
          weight: datum.strength ?? 1,
          metadata: JSON.stringify(propsObj, null, 2),
        });
      });

    const node = svg
      .append('g')
      .attr('stroke', '#fff')
      .attr('stroke-width', 1.5)
      .selectAll<SVGCircleElement, GraphNodeDatum>('circle')
      .data(nodes)
      .join('circle')
      .attr('r', 18)
      .attr('fill', (d) =>
        highlightNodeIds.has(d.id) ? 'var(--vault-accent)' : 'var(--muted)'
      )
      .attr('fill-opacity', (d) => (highlightNodeIds.has(d.id) ? 0.95 : 0.6))
      .style('cursor', 'pointer')
      .call(
        d3
