          .on('start', (event, d) => {
            if (!event.active) simulation.alphaTarget(0.3).restart();
            d.fx = d.x;
            d.fy = d.y;
          })
          .on('drag', (event, d) => {
            d.fx = event.x;
            d.fy = event.y;
          })
          .on('end', (event, d) => {
            if (!event.active) simulation.alphaTarget(0);
            d.fx = null;
            d.fy = null;
          })
      )
      .on('click', (event, datum) => {
        event.stopPropagation();
        setSelectedNodeId(datum.id);
        setSelectedRelationshipId(null);
        const props = parseGraphNodeProperties(datum.properties);
        const tags = Array.isArray(props?.tags) ? (props.tags as string[]).join(', ') : '';
        setNodeForm({
          id: datum.id,
          entityType: datum.entity_type,
          name: datum.name || props?.name as string || '',
          description: datum.description || props?.description as string || '',
          tags,
          metadata: JSON.stringify(props || {}, null, 2),
        });
      });

    const labels = svg
      .append('g')
      .selectAll('text')
      .data(nodes)
      .join('text')
      .attr('font-size', 11)
      .attr('fill', 'var(--muted-foreground)')
      .attr('text-anchor', 'middle')
      .text((d) => getNodeDisplayName(d));

    svg.on('click', () => resetForms());

    simulation.on('tick', () => {
      link
        .attr('x1', (d) => ((d.source as GraphNodeDatum).x ?? 0))
        .attr('y1', (d) => ((d.source as GraphNodeDatum).y ?? 0))
        .attr('x2', (d) => ((d.target as GraphNodeDatum).x ?? 0))
        .attr('y2', (d) => ((d.target as GraphNodeDatum).y ?? 0));

      node
        .attr('cx', (d) => (d.x = Math.max(24, Math.min(dimensions.width - 24, d.x ?? 0))))
        .attr('cy', (d) => (d.y = Math.max(24, Math.min(dimensions.height - 24, d.y ?? 0))));

      labels
        .attr('x', (d) => d.x ?? 0)
        .attr('y', (d) => (d.y ?? 0) + 34);
    });

    const endTime = performance.now();
    const elapsed = endTime - startTime;
    setRenderTime(elapsed);

    return () => {
      simulation.stop();
    };
  }, [
    graphToRender,
    chargeStrength,
    linkDistance,
    highlightNodeIds,
    highlightRelationshipIds,
    dimensions,
    resetForms,
    getNodeDisplayName,
    parseGraphNodeProperties,
  ]);
