      // Ensure userId is set
      if (!request.user_id) {
        request.user_id = this.getUserId();
      }
      const result = await invoke<MemorySlice>('vault_memory_create', { request });
      return { success: true, data: result };
    } catch (error) {
      return { success: false, error: String(error) };
    }
  }

