    const link = linkGroup
      .selectAll('line')
      .data(links)
      .join('line')
      .attr('stroke-width', (d) =>
        highlightRelationshipIds.has(d.id) ? 3 : Math.max(1, d.strength ?? 1)
      )
      .attr('stroke', (d) =>
        highlightRelationshipIds.has(d.id) ? '#fbbf24' : 'var(--border)'
      )
      .attr('stroke-opacity', (d) =>
        highlightRelationshipIds.has(d.id) ? 0.9 : 0.5
      )
      .style('cursor', 'pointer')
      .on('click', (event, datum) => {
        event.stopPropagation();
        setSelectedRelationshipId(datum.id);
        setSelectedNodeId(null);
        const propsObj = datum.properties ? JSON.parse(datum.properties) : {};
        setRelationshipForm({
          id: datum.id,
          sourceId: typeof datum.source === 'string' ? datum.source : datum.source.id,
          targetId: typeof datum.target === 'string' ? datum.target : datum.target.id,
          relationshipType: datum.relationship_type,
          weight: datum.strength ?? 1,
          metadata: JSON.stringify(propsObj, null, 2),
        });
      });

    const node = svg
      .append('g')
      .attr('stroke', '#fff')
      .attr('stroke-width', 1.5)
