import React from 'react';
import { cn } from '@/lib/utils';

interface PremiumCardProps extends React.HTMLAttributes<HTMLDivElement> {
  variant?: 'default' | 'glass' | 'outline';
  hoverEffect?: boolean;
}

export const PremiumCard: React.FC<PremiumCardProps> = ({
  className,
  variant = 'default',
  hoverEffect = false,
  children,
  ...props
}) => {
  const baseStyles = "rounded-xl border transition-all duration-300";
  
  const variants = {
    default: "bg-card text-card-foreground shadow-sm border-border/50",
    glass: "bg-card/30 backdrop-blur-md border-white/10 shadow-xl",
    outline: "bg-transparent border-border text-foreground"
  };

  const hoverStyles = hoverEffect 
    ? "hover:shadow-lg hover:border-primary/20 hover:-translate-y-1 hover:bg-card/50" 
    : "";

  return (
    <div 
      className={cn(baseStyles, variants[variant], hoverStyles, className)}
      {...props}
    >
      {children}
    </div>
  );
};

export const PremiumCardHeader: React.FC<React.HTMLAttributes<HTMLDivElement>> = ({ className, ...props }) => (
  <div className={cn("flex flex-col space-y-1.5 p-6", className)} {...props} />
);

