// ADHD Focus Management Hook
export const useFocusManagement = () => {
  const { focusState, setFocusState } = useADHDUI();
  const [distractionTimer, setDistractionTimer] = useState<number | null>(null);

  const trackFocus = useCallback((elementId: string) => {
    setFocusState((prev: FocusState) => ({
      ...prev,
      currentFocus: elementId,
      focusHistory: [...prev.focusHistory, elementId].slice(-10)
    }));

    if (distractionTimer) {
      clearTimeout(distractionTimer);
    }

    const timer = window.setTimeout(() => {
      setFocusState((prev: FocusState) => ({
        ...prev,
        distractionCount: prev.distractionCount + 1
      }));
    }, 30000);

    setDistractionTimer(timer);
  }, [distractionTimer, setFocusState]);

  const clearFocus = useCallback(() => {
    setFocusState((prev: FocusState) => ({
      ...prev,
      currentFocus: null
    }));

    if (distractionTimer) {
      clearTimeout(distractionTimer);
      setDistractionTimer(null);
    }
  }, [distractionTimer, setFocusState]);

  const updateEnergyLevel = useCallback((level: 'low' | 'medium' | 'high') => {
    setFocusState((prev: FocusState) => ({
      ...prev,
      energyLevel: level
    }));
  }, [setFocusState]);

  return {
    focusState,
    trackFocus,
    clearFocus,
    updateEnergyLevel
  };
};

