// ADHD-Optimized Input Component
export const ADHDInput: React.FC<{
  label?: string;
  placeholder?: string;
  value?: string;
  type?: 'text' | 'email' | 'password' | 'number';
  onChange?: (value: string) => void;
  onFocus?: () => void;
  onBlur?: () => void;
  disabled?: boolean;
  error?: string;
  helpText?: string;
  autoComplete?: string;
  variant?: 'default' | 'gentle' | 'focus';
  size?: 'small' | 'medium' | 'large';
  className?: string;
}> = ({
  label,
  placeholder,
  value = '',
  type = 'text',
  onChange,
  onFocus,
  onBlur,
  disabled = false,
  error,
  helpText,
  autoComplete,
  variant = 'default',
  size = 'medium',
  className = ''
}) => {
  const { uiConfig } = useADHDUI();
  const [isFocused, setIsFocused] = useState(false);
  const inputRef = useRef<HTMLInputElement>(null);

  const handleFocus = () => {
    setIsFocused(true);
    onFocus?.();
  };

  const handleBlur = () => {
    setIsFocused(false);
    onBlur?.();
  };

  const getInputStyles = () => {
    const baseStyles = `
      w-full border-2 rounded-lg transition-all duration-200
      focus:outline-none focus:ring-2 focus:ring-offset-1
      disabled:opacity-50 disabled:cursor-not-allowed
      placeholder-gray-400
    `;

    const sizeStyles = {
      small: 'px-3 py-2 text-sm',
      medium: 'px-4 py-3 text-base',
      large: 'px-5 py-4 text-lg'
    };

    const variantStyles = {
      default: `
        border-gray-300 bg-white text-gray-900
        focus:border-blue-500 focus:ring-blue-500
        hover:border-gray-400
      `,
      gentle: `
        border-green-200 bg-green-50 text-green-900
        focus:border-green-500 focus:ring-green-500
        hover:border-green-300
      `,
      focus: `
        border-purple-200 bg-purple-50 text-purple-900
        focus:border-purple-500 focus:ring-purple-500
        hover:border-purple-300
      `
    };

    const stateStyles = error
      ? 'border-red-300 bg-red-50 focus:border-red-500 focus:ring-red-500'
      : isFocused
      ? 'shadow-lg'
      : '';

    return `${baseStyles} ${sizeStyles[size]} ${variantStyles[variant]} ${stateStyles}`;
  };

  return (
    <div className={`adhd-input-wrapper ${className}`}>
      {/* Label */}
      {label && (
        <label className="block text-sm font-medium text-gray-700 mb-2">
          {label}
        </label>
      )}

      {/* Input field */}
      <div className="relative">
        <input
          ref={inputRef}
          type={type}
          value={value}
          onChange={(e) => onChange?.(e.target.value)}
          onFocus={handleFocus}
          onBlur={handleBlur}
          placeholder={placeholder}
          disabled={disabled}
          autoComplete={autoComplete}
          className={getInputStyles()}
        />

        {/* Focus indicator */}
        {uiConfig.enableFocusIndicators && isFocused && (
          <motion.div
            className="absolute inset-0 border-2 border-blue-400 rounded-lg pointer-events-none"
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.95 }}
            transition={{ duration: 0.2 }}
          />
        )}
      </div>

      {/* Help text */}
      {helpText && !error && (
        <p className="mt-2 text-sm text-gray-600">
          {helpText}
        </p>
      )}

      {/* Error message */}
      {error && (
        <motion.p
          className="mt-2 text-sm text-red-600"
          initial={{ opacity: 0, height: 0 }}
          animate={{ opacity: 1, height: 'auto' }}
          exit={{ opacity: 0, height: 0 }}
        >
          {error}
        </motion.p>
      )}
    </div>
  );
};

// ADHD-Optimized Notification Component
export const ADHDNotification: React.FC<{
  title: string;
  message: string;
  type?: 'info' | 'success' | 'warning' | 'error' | 'gentle';
  duration?: number;
  onClose?: () => void;
  persistent?: boolean;
  gentleMode?: boolean;
}> = ({
  title,
  message,
  type = 'info',
  duration = 5000,
  onClose,
  persistent = false,
  gentleMode = true
}) => {
  const { uiConfig } = useADHDUI();
  const [isVisible, setIsVisible] = useState(true);

  useEffect(() => {
    if (!persistent && duration > 0) {
      const timer = setTimeout(() => {
        setIsVisible(false);
        onClose && setTimeout(onClose, 300); // Wait for animation
      }, duration);

      return () => clearTimeout(timer);
    }
  }, [duration, persistent, onClose]);

  const getNotificationStyles = () => {
    const baseStyles = `
      max-w-sm p-4 rounded-lg shadow-lg border-l-4
      ${gentleMode ? 'animate-pulse-gentle' : ''}
    `;

    const typeStyles = {
      info: 'bg-blue-50 border-blue-400 text-blue-800',
      success: 'bg-green-50 border-green-400 text-green-800',
      warning: 'bg-yellow-50 border-yellow-400 text-yellow-800',
      error: 'bg-red-50 border-red-400 text-red-800',
      gentle: 'bg-teal-50 border-teal-400 text-teal-800'
    };

    return `${baseStyles} ${typeStyles[type]}`;
  };

  if (!isVisible) return null;

  return (
    <motion.div
      className={getNotificationStyles()}
      initial={!uiConfig.reduceMotion ? { opacity: 0, x: 300, scale: 0.8 } : {}}
      animate={!uiConfig.reduceMotion ? { opacity: 1, x: 0, scale: 1 } : {}}
      exit={!uiConfig.reduceMotion ? { opacity: 0, x: 300, scale: 0.8 } : {}}
      transition={{
        duration: gentleMode ? 0.5 : 0.3,
        ease: gentleMode ? 'easeOut' : 'easeInOut'
      }}
    >
      <div className="flex justify-between items-start">
        <div className="flex-1">
          <h4 className="font-medium mb-1">{title}</h4>
          <p className="text-sm opacity-90">{message}</p>
        </div>

        {/* Close button */}
        <button
          onClick={() => {
            setIsVisible(false);
            onClose && setTimeout(onClose, 300);
          }}
          className="ml-3 text-current opacity-60 hover:opacity-100 transition-opacity"
        >
          <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
            <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
          </svg>
        </button>
      </div>

      {/* Gentle progress bar for auto-dismiss */}
      {!persistent && duration > 0 && (
        <motion.div
          className="mt-3 h-1 bg-current opacity-20 rounded-full overflow-hidden"
          initial={{ width: '100%' }}
          animate={{ width: '0%' }}
          transition={{ duration: duration / 1000, ease: 'linear' }}
        />
      )}
    </motion.div>
  );
};

