  data: TRow[];
  onRowClick?: (row: TRow) => void;
  emptyMessage?: string;
}

export const DataGrid = <TRow extends Record<string, unknown> = Record<string, unknown>>({
  columns,
  data,
  onRowClick,
  emptyMessage = 'No data available',
}: DataGridProps<TRow>) => (
  <div className="data-grid">
    <div className="data-grid-header grid" style={{
      gridTemplateColumns: columns.map(c => c.width || '1fr').join(' '),
    }}>
      {columns.map(col => (
        <div key={col.key}>{col.label}</div>
      ))}
    </div>
    <div>
      {data.length === 0 ? (
        <div className="text-center py-12 text-muted-foreground" role="status" aria-live="polite">
          {emptyMessage}
        </div>
      ) : (
        data.map((row, index) => (
          <div
            key={index}
            className={`data-grid-row grid ${onRowClick ? 'cursor-pointer' : ''}`}
            style={{
              gridTemplateColumns: columns.map(c => c.width || '1fr').join(' '),
            }}
            onClick={() => onRowClick?.(row)}
            role={onRowClick ? 'button' : undefined}
            tabIndex={onRowClick ? 0 : undefined}
            onKeyDown={onRowClick ? (e) => {
              if (e.key === 'Enter' || e.key === ' ') {
                e.preventDefault();
                onRowClick(row);
              }
            } : undefined}
            aria-label={`Row ${index + 1}`}
          >
            {columns.map(col => (
              <div key={col.key}>
                {col.render ? col.render(row[col.key], row) : (row[col.key] as ReactNode)}
              </div>
            ))}
          </div>
        ))
      )}
    </div>
  </div>
);

// ============================================================================
// Loading Spinner Component
// ============================================================================

interface LoadingSpinnerProps {
  message?: string;
  size?: 'sm' | 'md' | 'lg';
  color?: 'cyan' | 'violet' | 'green';
}

export const LoadingSpinner: React.FC<LoadingSpinnerProps> = ({
  message = 'Loading...',
  size = 'md',
  color = 'cyan',
}) => {
  const sizeClasses = {
    sm: 'w-4 h-4',
    md: 'w-6 h-6',
    lg: 'w-8 h-8',
  };

  const colorClasses = {
    cyan: 'border-cyan-500',
    violet: 'border-violet-500',
    green: 'border-green-500',
  };

  return (
    <div className="text-center py-8 text-muted-foreground" role="status" aria-live="polite">
      <div className={`inline-block ${sizeClasses[size]} border-2 ${colorClasses[color]} border-t-transparent rounded-full animate-spin`} aria-hidden="true"></div>
      {message && <p className="mt-2">{message}</p>}
    </div>
  );
};

// ============================================================================
// Empty State Component
// ============================================================================

interface EmptyStateProps {
  icon?: string;
  title: string;
  description?: string;
  action?: ReactNode;
}

export const EmptyState: React.FC<EmptyStateProps> = ({
  icon = 'dY"-',
  title,
  description,
  action,
}) => (
  <div className="text-center py-12 text-muted-foreground" role="status">
    <div className="text-6xl mb-4" aria-hidden="true">{icon}</div>
    <p className="text-lg font-medium mb-2">{title}</p>
    {description && <p className="text-sm mb-4">{description}</p>}
    {action && <div className="mt-6">{action}</div>}
  </div>
);

// ============================================================================
// Progress Bar Component
// ============================================================================

interface ProgressBarProps {
  current: number;
  max: number;
  label?: string;
  showPercentage?: boolean;
  color?: 'cyan' | 'violet' | 'green' | 'orange' | 'red';
}

export const ProgressBar: React.FC<ProgressBarProps> = ({
  current,
  max,
  label,
  showPercentage = true,
  color = 'cyan',
}) => {
  const percentage = Math.min(100, (current / max) * 100);

  const colorClasses = {
    cyan: 'bg-cyan-500',
    violet: 'bg-violet-500',
    green: 'bg-green-500',
    orange: 'bg-orange-500',
    red: 'bg-red-500',
  };

  return (
    <div role="progressbar" aria-valuenow={current} aria-valuemin={0} aria-valuemax={max} aria-label={label}>
      {(label || showPercentage) && (
        <div className="flex justify-between text-xs mb-1">
          {label && <span className="text-muted-foreground">{label}</span>}
          {showPercentage && <span className="text-foreground">{current} / {max}</span>}
        </div>
      )}
      <div className="h-2 bg-muted rounded-full overflow-hidden">
        <div
          className={`h-full ${colorClasses[color]} transition-all duration-300`}
          style={{ width: `${percentage}%` }}
          aria-hidden="true"
        ></div>
      </div>
    </div>
  );
};

// ============================================================================
// Tag Component
// ============================================================================

interface TagProps {
  children: ReactNode;
  variant?: 'default' | 'primary' | 'success' | 'warning' | 'danger';
  size?: 'sm' | 'md';
  onRemove?: () => void;
  'aria-label'?: string;
}

export const Tag: React.FC<TagProps> = ({
  children,
  variant = 'default',
  size = 'sm',
  onRemove,
  'aria-label': ariaLabel,
}) => {
  const variantClasses = {
    default: 'bg-muted text-muted-foreground',
    primary: 'bg-cyan-500/20 text-cyan-400',
    success: 'bg-green-500/20 text-green-400',
    warning: 'bg-orange-500/20 text-orange-400',
    danger: 'bg-red-500/20 text-red-400',
  };

  const sizeClasses = {
    sm: 'text-xs px-2 py-1',
    md: 'text-sm px-3 py-1.5',
  };

  return (
    <span className={`inline-flex items-center gap-1 rounded ${variantClasses[variant]} ${sizeClasses[size]}`} role="status" aria-label={ariaLabel}>
      {children}
      {onRemove && (
        <button
          onClick={onRemove}
          className="hover:opacity-70 transition-opacity"
          aria-label="Remove tag"
        >
          A-
        </button>
      )}
    </span>
  );
};

// ============================================================================
// Module Icon Component
// ============================================================================

interface ModuleIconProps {
  module: 'alden' | 'vault' | 'synapse' | 'sentry' | 'alice' | 'mimic';
  size?: 'sm' | 'md' | 'lg';
}

export const ModuleIcon: React.FC<ModuleIconProps> = ({
  module,
  size = 'md',
}) => {
  const sizeClasses = {
    sm: 'w-8 h-8 text-lg',
    md: 'w-10 h-10 text-2xl',
    lg: 'w-12 h-12 text-3xl',
  };

  const moduleConfig = {
    alden: { letter: 'A', gradient: 'from-cyan-500 to-blue-600' },
    vault: { letter: 'V', gradient: 'from-violet-500 to-purple-600' },
    synapse: { letter: 'S', gradient: 'from-pink-500 to-rose-600' },
    sentry: { letter: 'S', gradient: 'from-red-500 to-orange-600' },
    alice: { letter: 'A', gradient: 'from-teal-500 to-emerald-600' },
    mimic: { letter: 'M', gradient: 'from-indigo-500 to-blue-600' },
  };

  const config = moduleConfig[module];

  return (
    <div className={`rounded-lg bg-gradient-to-br ${config.gradient} flex items-center justify-center ${sizeClasses[size]}`} role="img" aria-label={`${module} module`}>
      <span className="font-bold text-white">{config.letter}</span>
    </div>
  );
};

