import React, { useState, useEffect, useRef, useCallback, createContext, useContext } from 'react';
import type { ReactNode } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

// ADHD-Optimized UI Components
// Designed with cognitive load reduction, focus management, and gentle interactions

interface ADHDUIConfig {
  reduceMotion: boolean;
  useHighContrast: boolean;
  enableFocusIndicators: boolean;
  gentleNotifications: boolean;
  minimizeClutter: boolean;
  energyAwareColors: boolean;
  predictableLayouts: boolean;
}

interface FocusState {
  currentFocus: string | null;
  focusHistory: string[];
  distractionCount: number;
  sessionDuration: number;
  energyLevel: 'low' | 'medium' | 'high';
}

interface CognitiveLoadIndicator {
  level: 'minimal' | 'light' | 'moderate' | 'heavy' | 'overwhelming';
  factors: string[];
  recommendations: string[];
}

// Context for ADHD UI state
const ADHDUIContext = createContext<{
  uiConfig: ADHDUIConfig;
  setUIConfig: (config: ADHDUIConfig) => void;
  focusState: FocusState;
  setFocusState: (state: FocusState | ((prev: FocusState) => FocusState)) => void;
} | null>(null);

// ADHD-Optimized Theme Provider
export const ADHDThemeProvider: React.FC<{
  children: ReactNode;
  config?: Partial<ADHDUIConfig>;
}> = ({ children, config = {} }) => {
  const defaultConfig: ADHDUIConfig = {
    reduceMotion: false,
    useHighContrast: false,
    enableFocusIndicators: true,
    gentleNotifications: true,
    minimizeClutter: true,
    energyAwareColors: true,
    predictableLayouts: true,
    ...config
  };

  const [uiConfig, setUIConfig] = useState(defaultConfig);
  const [focusState, setFocusState] = useState<FocusState>({
    currentFocus: null,
    focusHistory: [],
    distractionCount: 0,
    sessionDuration: 0,
    energyLevel: 'medium'
  });

  const themeClasses = `
    ${uiConfig.useHighContrast ? 'adhd-high-contrast' : ''}
    ${uiConfig.minimizeClutter ? 'adhd-minimal' : ''}
    ${uiConfig.energyAwareColors ? `adhd-energy-${focusState.energyLevel}` : ''}
    ${uiConfig.predictableLayouts ? 'adhd-predictable' : ''}
  `.trim();

  return (
    <>
      <style dangerouslySetInnerHTML={{ __html: `
        .adhd-optimized-interface {
          /* Base ADHD-friendly styles */
          --adhd-primary: #4A90E2;
          --adhd-success: #7ED321;
          --adhd-warning: #F5A623;
          --adhd-danger: #D0021B;
          --adhd-calm: #50E3C2;
          --adhd-focus: #B794F6;

          /* Energy-aware colors */
          --adhd-energy-low: #6B73FF;
          --adhd-energy-medium: #4A90E2;
          --adhd-energy-high: #FF6B6B;

          /* Spacing for cognitive load */
          --adhd-space-minimal: 0.5rem;
          --adhd-space-comfortable: 1rem;
          --adhd-space-generous: 2rem;
          --adhd-space-breathing: 3rem;

          /* Typography for readability */
          font-family: 'Inter', 'SF Pro Display', -apple-system, sans-serif;
          font-size: 16px;
          line-height: 1.6;
          letter-spacing: 0.01em;
        }

        .adhd-high-contrast {
          --adhd-bg: #000000;
          --adhd-surface: #1a1a1a;
          --adhd-text: #ffffff;
          --adhd-border: #333333;
        }

        .adhd-minimal {
          --adhd-shadow: none;
          --adhd-border-radius: 4px;
          --adhd-transition: none;
        }

        .adhd-energy-low {
          --adhd-primary: var(--adhd-energy-low);
          filter: saturate(0.8) brightness(0.9);
        }

        .adhd-energy-high {
          --adhd-primary: var(--adhd-energy-high);
          filter: saturate(1.2) brightness(1.1);
        }

        .adhd-predictable {
          /* Consistent spacing and sizing */
          --adhd-button-height: 44px;
          --adhd-input-height: 44px;
          --adhd-card-padding: var(--adhd-space-comfortable);
        }

        /* Accessibility */
        @media (prefers-reduced-motion: reduce) {
          .adhd-optimized-interface * {
            animation-duration: 0.01ms !important;
            animation-iteration-count: 1 !important;
            transition-duration: 0.01ms !important;
          }
        }

        @keyframes pulse-gentle {
          0%, 100% { opacity: 1; }
          50% { opacity: 0.8; }
        }

        .animate-pulse-gentle {
          animation: pulse-gentle 3s cubic-bezier(0.4, 0, 0.6, 1) infinite;
        }
      ` }} />
      <div className={`adhd-optimized-interface ${themeClasses}`}>
        <ADHDUIContext.Provider value={{ uiConfig, setUIConfig, focusState, setFocusState }}>
          {children}
        </ADHDUIContext.Provider>
      </div>
    </>
  );
};

export const useADHDUI = () => {
  const context = useContext(ADHDUIContext);
  if (!context) {
    throw new Error('useADHDUI must be used within ADHDThemeProvider');
  }
  return context;
};

// ADHD Focus Management Hook
export const useFocusManagement = () => {
  const { focusState, setFocusState } = useADHDUI();
  const [distractionTimer, setDistractionTimer] = useState<number | null>(null);

  const trackFocus = useCallback((elementId: string) => {
    setFocusState((prev: FocusState) => ({
      ...prev,
      currentFocus: elementId,
      focusHistory: [...prev.focusHistory, elementId].slice(-10)
    }));

    if (distractionTimer) {
      clearTimeout(distractionTimer);
    }

    const timer = window.setTimeout(() => {
      setFocusState((prev: FocusState) => ({
        ...prev,
        distractionCount: prev.distractionCount + 1
      }));
    }, 30000);

    setDistractionTimer(timer);
  }, [distractionTimer, setFocusState]);

  const clearFocus = useCallback(() => {
    setFocusState((prev: FocusState) => ({
      ...prev,
      currentFocus: null
    }));

    if (distractionTimer) {
      clearTimeout(distractionTimer);
      setDistractionTimer(null);
    }
  }, [distractionTimer, setFocusState]);

  const updateEnergyLevel = useCallback((level: 'low' | 'medium' | 'high') => {
    setFocusState((prev: FocusState) => ({
      ...prev,
      energyLevel: level
    }));
  }, [setFocusState]);

  return {
    focusState,
    trackFocus,
    clearFocus,
    updateEnergyLevel
  };
};

// ADHD-Optimized Button Component
export const ADHDButton: React.FC<{
  children: ReactNode;
  variant?: 'primary' | 'secondary' | 'gentle' | 'focus' | 'calm';
  size?: 'small' | 'medium' | 'large' | 'touch-friendly';
  loading?: boolean;
  disabled?: boolean;
  onClick?: () => void;
  className?: string;
  cognitiveLoad?: 'low' | 'medium' | 'high';
}> = ({
  children,
  variant = 'primary',
  size = 'medium',
  loading = false,
  disabled = false,
  onClick,
  className = '',
  cognitiveLoad = 'low'
}) => {
  const { uiConfig, focusState } = useADHDUI();
  const [ripples, setRipples] = useState<Array<{ x: number; y: number; id: number }>>([]);
  const [isPressed, setIsPressed] = useState(false);

  const handleClick = useCallback((e: React.MouseEvent) => {
    if (disabled || loading) return;

    // Gentle haptic feedback (visual ripple)
    if (!uiConfig.reduceMotion) {
      const rect = e.currentTarget.getBoundingClientRect();
      const x = e.clientX - rect.left;
      const y = e.clientY - rect.top;
      const newRipple = { x, y, id: Date.now() };

      setRipples(prev => [...prev, newRipple]);
      setTimeout(() => {
        setRipples(prev => prev.filter(r => r.id !== newRipple.id));
      }, 600);
    }

    onClick?.();
  }, [disabled, loading, onClick, uiConfig.reduceMotion]);

  const getVariantStyles = () => {
    const baseStyles = `
      relative overflow-hidden font-medium transition-all duration-200
      border-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-offset-2
      disabled:opacity-50 disabled:cursor-not-allowed
    `;

    const sizeStyles = {
      small: 'px-3 py-2 text-sm',
      medium: 'px-4 py-3 text-base',
      large: 'px-6 py-4 text-lg',
      'touch-friendly': 'px-6 py-4 text-base min-h-[44px] min-w-[44px]'
    };

    const variantStyles = {
      primary: `
        bg-blue-600 border-blue-600 text-white
        hover:bg-blue-700 hover:border-blue-700
        focus:ring-blue-500
        active:bg-blue-800
      `,
      secondary: `
        bg-gray-100 border-gray-300 text-gray-800
        hover:bg-gray-200 hover:border-gray-400
        focus:ring-gray-500
        active:bg-gray-300
      `,
      gentle: `
        bg-green-50 border-green-200 text-green-800
        hover:bg-green-100 hover:border-green-300
        focus:ring-green-500
        active:bg-green-200
      `,
      focus: `
        bg-purple-100 border-purple-300 text-purple-800
        hover:bg-purple-200 hover:border-purple-400
        focus:ring-purple-500
        active:bg-purple-300
      `,
      calm: `
        bg-teal-50 border-teal-200 text-teal-800
        hover:bg-teal-100 hover:border-teal-300
        focus:ring-teal-500
        active:bg-teal-200
      `
    };

    // Adjust based on energy level
    const energyAdjustment = focusState.energyLevel === 'low'
      ? 'brightness-90 saturate-75'
      : focusState.energyLevel === 'high'
      ? 'brightness-110 saturate-125'
      : '';

    return `${baseStyles} ${sizeStyles[size]} ${variantStyles[variant]} ${energyAdjustment}`;
  };

  return (
    <motion.button
      className={`${getVariantStyles()} ${className}`}
      whileHover={!uiConfig.reduceMotion ? { scale: 1.02 } : {}}
      whileTap={!uiConfig.reduceMotion ? { scale: 0.98 } : {}}
      onClick={handleClick}
      disabled={disabled || loading}
      onMouseDown={() => setIsPressed(true)}
      onMouseUp={() => setIsPressed(false)}
      onMouseLeave={() => setIsPressed(false)}
    >
      {/* Loading indicator */}
      {loading && (
        <div className="absolute inset-0 flex items-center justify-center bg-white bg-opacity-20">
          <div className="w-4 h-4 border-2 border-current border-t-transparent rounded-full animate-spin" />
        </div>
      )}

      {/* Ripple effects */}
      <AnimatePresence>
        {ripples.map(ripple => (
          <motion.div
            key={ripple.id}
            className="absolute bg-white bg-opacity-30 rounded-full pointer-events-none"
            style={{
              left: ripple.x - 20,
              top: ripple.y - 20,
              width: 40,
              height: 40
            }}
            initial={{ scale: 0, opacity: 1 }}
            animate={{ scale: 2, opacity: 0 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.6 }}
          />
        ))}
      </AnimatePresence>

      {/* Content */}
      <span className={`relative z-10 ${loading ? 'opacity-0' : 'opacity-100'}`}>
        {children}
      </span>

      {/* Cognitive load indicator */}
      {cognitiveLoad === 'high' && (
        <div className="absolute top-1 right-1 w-2 h-2 bg-orange-500 rounded-full"
             title="High cognitive load required" />
      )}
    </motion.button>
  );
};

// ADHD-Optimized Card Component
// ADHDCard removed. Use PremiumCard with variant="adhd" instead.

// ADHD-Optimized Input Component
export const ADHDInput: React.FC<{
  label?: string;
  placeholder?: string;
  value?: string;
  type?: 'text' | 'email' | 'password' | 'number';
  onChange?: (value: string) => void;
  onFocus?: () => void;
  onBlur?: () => void;
  disabled?: boolean;
  error?: string;
  helpText?: string;
  autoComplete?: string;
  variant?: 'default' | 'gentle' | 'focus';
  size?: 'small' | 'medium' | 'large';
  className?: string;
}> = ({
  label,
  placeholder,
  value = '',
  type = 'text',
  onChange,
  onFocus,
  onBlur,
  disabled = false,
  error,
  helpText,
  autoComplete,
  variant = 'default',
  size = 'medium',
  className = ''
}) => {
  const { uiConfig } = useADHDUI();
  const [isFocused, setIsFocused] = useState(false);
  const inputRef = useRef<HTMLInputElement>(null);

  const handleFocus = () => {
    setIsFocused(true);
    onFocus?.();
  };

  const handleBlur = () => {
    setIsFocused(false);
    onBlur?.();
  };

  const getInputStyles = () => {
    const baseStyles = `
      w-full border-2 rounded-lg transition-all duration-200
      focus:outline-none focus:ring-2 focus:ring-offset-1
      disabled:opacity-50 disabled:cursor-not-allowed
      placeholder-gray-400
    `;

    const sizeStyles = {
      small: 'px-3 py-2 text-sm',
      medium: 'px-4 py-3 text-base',
      large: 'px-5 py-4 text-lg'
    };

    const variantStyles = {
      default: `
        border-gray-300 bg-white text-gray-900
        focus:border-blue-500 focus:ring-blue-500
        hover:border-gray-400
      `,
      gentle: `
        border-green-200 bg-green-50 text-green-900
        focus:border-green-500 focus:ring-green-500
        hover:border-green-300
      `,
      focus: `
        border-purple-200 bg-purple-50 text-purple-900
        focus:border-purple-500 focus:ring-purple-500
        hover:border-purple-300
      `
    };

    const stateStyles = error
      ? 'border-red-300 bg-red-50 focus:border-red-500 focus:ring-red-500'
      : isFocused
      ? 'shadow-lg'
      : '';

    return `${baseStyles} ${sizeStyles[size]} ${variantStyles[variant]} ${stateStyles}`;
  };

  return (
    <div className={`adhd-input-wrapper ${className}`}>
      {/* Label */}
      {label && (
        <label className="block text-sm font-medium text-gray-700 mb-2">
          {label}
        </label>
      )}

      {/* Input field */}
      <div className="relative">
        <input
          ref={inputRef}
          type={type}
          value={value}
          onChange={(e) => onChange?.(e.target.value)}
          onFocus={handleFocus}
          onBlur={handleBlur}
          placeholder={placeholder}
          disabled={disabled}
          autoComplete={autoComplete}
          className={getInputStyles()}
        />

        {/* Focus indicator */}
        {uiConfig.enableFocusIndicators && isFocused && (
          <motion.div
            className="absolute inset-0 border-2 border-blue-400 rounded-lg pointer-events-none"
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.95 }}
            transition={{ duration: 0.2 }}
          />
        )}
      </div>

      {/* Help text */}
      {helpText && !error && (
        <p className="mt-2 text-sm text-gray-600">
          {helpText}
        </p>
      )}

      {/* Error message */}
      {error && (
        <motion.p
          className="mt-2 text-sm text-red-600"
          initial={{ opacity: 0, height: 0 }}
          animate={{ opacity: 1, height: 'auto' }}
          exit={{ opacity: 0, height: 0 }}
        >
          {error}
        </motion.p>
      )}
    </div>
  );
};

// ADHD-Optimized Notification Component
export const ADHDNotification: React.FC<{
  title: string;
  message: string;
  type?: 'info' | 'success' | 'warning' | 'error' | 'gentle';
  duration?: number;
  onClose?: () => void;
  persistent?: boolean;
  gentleMode?: boolean;
}> = ({
  title,
  message,
  type = 'info',
  duration = 5000,
  onClose,
  persistent = false,
  gentleMode = true
}) => {
  const { uiConfig } = useADHDUI();
  const [isVisible, setIsVisible] = useState(true);

  useEffect(() => {
    if (!persistent && duration > 0) {
      const timer = setTimeout(() => {
        setIsVisible(false);
        onClose && setTimeout(onClose, 300); // Wait for animation
      }, duration);

      return () => clearTimeout(timer);
    }
  }, [duration, persistent, onClose]);

  const getNotificationStyles = () => {
    const baseStyles = `
      max-w-sm p-4 rounded-lg shadow-lg border-l-4
      ${gentleMode ? 'animate-pulse-gentle' : ''}
    `;

    const typeStyles = {
      info: 'bg-blue-50 border-blue-400 text-blue-800',
      success: 'bg-green-50 border-green-400 text-green-800',
      warning: 'bg-yellow-50 border-yellow-400 text-yellow-800',
      error: 'bg-red-50 border-red-400 text-red-800',
      gentle: 'bg-teal-50 border-teal-400 text-teal-800'
    };

    return `${baseStyles} ${typeStyles[type]}`;
  };

  if (!isVisible) return null;

  return (
    <motion.div
      className={getNotificationStyles()}
      initial={!uiConfig.reduceMotion ? { opacity: 0, x: 300, scale: 0.8 } : {}}
      animate={!uiConfig.reduceMotion ? { opacity: 1, x: 0, scale: 1 } : {}}
      exit={!uiConfig.reduceMotion ? { opacity: 0, x: 300, scale: 0.8 } : {}}
      transition={{
        duration: gentleMode ? 0.5 : 0.3,
        ease: gentleMode ? 'easeOut' : 'easeInOut'
      }}
    >
      <div className="flex justify-between items-start">
        <div className="flex-1">
          <h4 className="font-medium mb-1">{title}</h4>
          <p className="text-sm opacity-90">{message}</p>
        </div>

        {/* Close button */}
        <button
          onClick={() => {
            setIsVisible(false);
            onClose && setTimeout(onClose, 300);
          }}
          className="ml-3 text-current opacity-60 hover:opacity-100 transition-opacity"
        >
          <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
            <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
          </svg>
        </button>
      </div>

      {/* Gentle progress bar for auto-dismiss */}
      {!persistent && duration > 0 && (
        <motion.div
          className="mt-3 h-1 bg-current opacity-20 rounded-full overflow-hidden"
          initial={{ width: '100%' }}
          animate={{ width: '0%' }}
          transition={{ duration: duration / 1000, ease: 'linear' }}
        />
      )}
    </motion.div>
  );
};

