/**
 * React Hooks for Agent Bridge Integration
 *
 * Provides React hooks for all 7 agent bridges with:
 * - Automatic initialization and cleanup
 * - Real-time event subscription
 * - Loading and error states
 * - Type-safe bridge access
 *
 * @version 1.0.0
 * @date 2025-10-10
 */

import { useState, useEffect, useCallback, useRef } from 'react';
import {
  AldenBridge,
  AliceBridge,
  MimicBridge,
  CoreBridge,
  SynapseBridge,
  SentryBridge,
  SystemBridge,
  SettingsBridge,
  BridgeFactory,
} from '../bridges';
import { SettingsProps } from '../bridges/SettingsBridge';
import type {
  AgentBridge,
  AgentId,
  AldenPersonaProps,
  AliceProps,
  MimicProps,
  CoreProps,
  SynapseProps,
  SentryProps,
  SystemProps,
} from '../types/agents';

/**
 * Base hook for any agent bridge
 */
export function useBridge<TProps>(
  BridgeClass: new (config?: { userId?: string }) => AgentBridge<TProps>,
  agentId: AgentId,
  config?: { userId?: string }
) {
  const [bridge, setBridge] = useState<AgentBridge<TProps> | null>(null);
  const [props, setProps] = useState<TProps | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<Error | null>(null);
  const initRef = useRef(false);

  useEffect(() => {
    let mounted = true;
    let currentBridge: AgentBridge<TProps> | null = null;

    const initBridge = async () => {
      // Force re-init if userId changes or on first mount
      // We removed the strict initRef check to generic userId changes
      
      try {
        setLoading(true);
        setError(null);

        // Pass userId if provided in config
        currentBridge = await BridgeFactory.createBridge(
          BridgeClass, 
          agentId, 
          config?.userId
        );

        if (!mounted) {
          await BridgeFactory.destroyBridge(agentId);
          return;
        }

        const bridgeProps = await currentBridge.getProps();

        if (!mounted) {
          await BridgeFactory.destroyBridge(agentId);
          return;
        }

        setBridge(currentBridge);
        setProps(bridgeProps);
      } catch (err) {
        if (mounted) {
          setError(err instanceof Error ? err : new Error('Bridge initialization failed'));
        }
      } finally {
        if (mounted) {
          setLoading(false);
        }
      }
    };

    initBridge();

    return () => {
      mounted = false;
      void BridgeFactory.destroyBridge(agentId);
    };
  }, [BridgeClass, agentId, config?.userId]);

  const refresh = useCallback(async () => {
    if (!bridge) return;

    try {
      const updatedProps = await bridge.getProps();
      setProps(updatedProps);
    } catch (err) {
      setError(err instanceof Error ? err : new Error('Failed to refresh props'));
    }
  }, [bridge]);

  return { bridge, props, loading, error, refresh };
}

/**
 * Hook for Alden bridge
 */
export function useAldenBridge(userId?: string) {
  const { bridge, props, loading, error, refresh } = useBridge<AldenPersonaProps>(
    AldenBridge,
    'alden',
    userId ? { userId } : undefined
  );

  return {
    bridge: bridge as AldenBridge | null,
    props,
    loading,
    error,
    refresh,
  };
}

/**
 * Hook for Alice bridge
 */
export function useAliceBridge(userId?: string) {
  const { bridge, props, loading, error, refresh } = useBridge<AliceProps>(
    AliceBridge as any,
    'alice',
    userId ? { userId } : undefined
  );

  return {
    bridge: bridge as AliceBridge | null,
    props,
    loading,
    error,
    refresh,
  };
}

/**
 * Hook for Mimic bridge
 */
export function useMimicBridge(userId?: string) {
  const { bridge, props, loading, error, refresh } = useBridge<MimicProps>(
    MimicBridge as any,
