/**
 * Hook for Settings bridge
 */
export function useSettingsBridge(userId?: string) {
  const { bridge, props, loading, error, refresh } = useBridge<SettingsProps>(
    SettingsBridge,
    'settings',
    userId ? { userId } : undefined
  );

  return {
    bridge: bridge as SettingsBridge | null,
    props,
    loading,
    error,
    refresh,
  };
}

/**
 * Hook for subscribing to bridge events
 */
export function useBridgeSubscription<T = unknown>(
  bridge: AgentBridge | null,
  callback: (data: T) => void
) {
  useEffect(() => {
    if (!bridge) return;

    const unsubscribe = bridge.subscribe(callback as (data: unknown) => void);
    return unsubscribe;
  }, [bridge, callback]);
}

