+```tsx
+import React from 'react';
+import { AlertCircle } from '@/components/ui/HearthlinkIcons';
+import { useADHDUI } from '@/components/ui/ADHDOptimizedInterface';
+import { cn } from '@/lib/utils';
+
+interface FocusAssistButtonProps {
+  onActivate: () => void;
+  isActive?: boolean;
+  label?: string;
+  className?: string;
+}
+
+export const FocusAssistButton: React.FC<FocusAssistButtonProps> = ({
+  onActivate,
+  isActive = false,
+  label = 'Focus Assist',
+  className,
+}) => {
+  const { uiConfig } = useADHDUI();
+
+  return (
+    <button
+      type="button"
+      onClick={onActivate}
+      aria-pressed={isActive}
+      aria-label={label}
+      title={label}
+      className={cn(
+        'group flex items-center gap-2 rounded-full border px-3 py-2 text-xs font-semibold',
+        'bg-[#0b1221]/80 text-[#fbbf24] border-[#f59e0b]/40 shadow-lg shadow-black/40',
+        'hover:bg-[#111a2c] focus:outline-none focus-visible:ring-2 focus-visible:ring-[#fbbf24]',
+        !uiConfig.reduceMotion && 'transition-all duration-200',
+        isActive && 'ring-2 ring-[#fbbf24] bg-[#111827]',
+        uiConfig.useHighContrast && 'bg-white text-black border-black',
+        className
+      )}
+    >
