                </div>
                <div className="text-right">
                  <div className="text-2xl font-bold mb-1" style={{ color: 'var(--sentry-accent)' }}>
                    {report.summary.total_events}
                  </div>
                  <div className="text-xs text-muted-foreground">Total Events</div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  // Page-level loading state
  if (loading && events.length === 0) {
    return (
      <div className="min-h-screen bg-gradient-mythiq p-6 flex items-center justify-center">
        <div className="text-center">
          <div className="relative inline-block mb-6">
            {/* Pulsing cyan backlight */}
            <div
              className="absolute inset-0 rounded-full animate-pulse"
              style={{
                background: 'radial-gradient(circle, rgba(34, 211, 238, 0.4) 0%, rgba(34, 211, 238, 0) 70%)',
                transform: 'scale(1.5)',
                filter: 'blur(8px)',
              }}
            />
            {/* Sentry icon */}
            <img
              src="/assets/Sentry.png"
              alt="Sentry"
              className="relative w-20 h-20 object-contain animate-pulse"
              style={{
                filter: 'drop-shadow(0 0 15px rgba(34, 211, 238, 0.6)) drop-shadow(0 0 30px rgba(34, 211, 238, 0.3))',
              }}
            />
          </div>
          <div className="text-white text-lg font-medium">Loading security events...</div>
        </div>
      </div>
    );
  }

  return (
    <>
      {/* Success/Error Toast */}
      {actionSuccess && (
        <div className="fixed top-4 right-4 z-50 p-4 rounded-md shadow-lg animate-in slide-in-from-top"
          style={{
            backgroundColor: 'var(--success)',
            color: 'white',
          }}>
          {actionSuccess}
        </div>
      )}
      {error && (
        <div className="fixed top-4 right-4 z-50 p-4 rounded-md shadow-lg animate-in slide-in-from-top"
          style={{
            backgroundColor: 'var(--error)',
            color: 'white',
          }}>
          {error}
          <button
            onClick={() => setError(null)}
            className="ml-4 underline"
          >
            Dismiss
          </button>
        </div>
      )}

      <div className="min-h-screen p-6" style={{ backgroundColor: 'var(--background)' }}>
        <div className="max-w-7xl mx-auto">
          <div className="module-card mb-6">
            {renderHeader()}
          </div>

          {renderMetrics()}
          {renderSecurityEvents()}
          {renderComplianceRules()}
          {renderAuditReports()}
        </div>
      </div>
    </>
  );
};

