import React from 'react';

export interface MetricCardProps {
  title: string;
  value: number | string;
  subtitle: string;
  accentColor: 'cyan' | 'gold' | 'green' | 'violet' | 'teal' | 'rose';
  icon: React.ReactNode;
  active?: boolean;
  variant?: 'counter';
  className?: string;
}

export const MetricCard: React.FC<MetricCardProps> = ({ 
  title, 
  value, 
  subtitle, 
  accentColor, 
  icon, 
  active, 
  variant = 'counter',
  className = ''
}) => {
  const styles = {
    cyan: 'text-[#9fb4d6]',
    gold: 'text-alden-gold',
    green: 'text-alden-cyan',
    violet: 'text-violet-400',
    teal: 'text-teal-400',
    rose: 'text-rose-400',
  } as const;

  const activeStyle: React.CSSProperties = {
    border: '1px solid rgba(0, 240, 255, 0.55)',
    boxShadow: '0 8px 20px rgba(0, 240, 255, 0.18), inset 0 0 0 1px rgba(197,160,89,0.38)',
    background: 'linear-gradient(135deg, rgba(0,240,255,0.12), rgba(0,240,255,0.03) 42%, rgba(197,160,89,0.06))',
  };

  const getStyleForColor = (color: string) => {
    switch(color) {
        case 'violet': return { border: '1px solid rgba(139,92,246,0.3)', background: 'rgba(139,92,246,0.1)', boxShadow: '0 0 20px rgba(139,92,246,0.15)' };
        case 'teal': return { border: '1px solid rgba(45,212,191,0.3)', background: 'rgba(45,212,191,0.1)', boxShadow: '0 0 20px rgba(45,212,191,0.15)' };
        case 'rose': return { border: '1px solid rgba(251,113,133,0.3)', background: 'rgba(251,113,133,0.1)', boxShadow: '0 0 20px rgba(251,113,133,0.15)' };
        default: return {
            border: '1px solid rgba(120, 135, 170, 0.55)',
            boxShadow: '0 6px 14px rgba(0, 0, 0, 0.35)',
            background: 'linear-gradient(135deg, rgba(255,255,255,0.03), rgba(0,0,0,0.18) 45%, rgba(0,0,0,0.35))',
        };
    }
  };

  const inactiveStyle = active ? activeStyle : getStyleForColor(accentColor);

  const valueColor = active
    ? 'text-[#e5f7ff]'
    : styles[accentColor as keyof typeof styles] || styles.cyan;

  const borderClasses = active
    ? 'border ring-1 ring-[rgba(0,240,255,0.32)] bg-gradient-to-br from-[rgba(0,240,255,0.08)] via-transparent to-[rgba(0,0,0,0.25)]'
    : 'border ring-1 ring-[rgba(255,255,255,0.12)] hover:border-[rgba(0,240,255,0.35)]';

  const variantSizing = {
    counter: 'h-[130px] min-h-[130px]'
  } as const;

  return (
    <div
      className={`relative bg-alden-panel rounded-xl p-3 overflow-hidden transition-all duration-300 ${borderClasses} ${variantSizing[variant]} flex flex-col gap-1.5 ${className}`}
      style={active ? activeStyle : inactiveStyle}
    >
      <div className="flex items-center gap-1.5 text-[#9fb4d6]">
        {icon}
      </div>
      <div className={`text-2xl font-bold tracking-tight font-orbitron-force ${valueColor}`}>{value}</div>
      <div className="text-[#e5c478] font-semibold text-[11px] uppercase tracking-[0.2em] font-orbitron-force leading-snug">{title}</div>
      <div className={`text-[11px] uppercase tracking-widest font-inter leading-snug ${active ? 'text-[#72f0ff] font-semibold' : 'text-[#9fb4d6]'}`}>
        {subtitle}
      </div>
      <div className="absolute bottom-0 right-0 w-8 h-8 border-b-2 border-r-2 rounded-br-xl opacity-30 border-alden-cyan" />
    </div>
  );
};

