import React, { useEffect, useMemo, useRef, useState, useCallback } from 'react';
import { Shield, AlertTriangle, CheckCircle2, Lock, Unlock, FileText, Activity } from '@/components/ui/HearthlinkIcons';

import { SentryBridge } from '../../bridges/SentryBridge';
import type { SentrySecurityEvent, SentryComplianceRule, SentryAuditReport, JsonObject } from '../../types/agents';
import { UnifiedCommandCenter } from '../shared/UnifiedCommandCenter';
import { MetricCard } from '../shared/premium/MetricCard';
import { UnifiedChatContainer } from '../shared/chat/UnifiedChatContainer';
import { PrivateChannel } from '../shared/chat/PrivateChannel';
import { MODULE_RESIDENT_AI, type SystemPersonaId } from '../shared/chat/types';
import { useChatStateWithOrchestration } from '../shared/chat/useChatStateWithOrchestration';

export interface SentryCommandCenterProps {
  userId: string;
  onBackToLauncher?: () => void;
  onNavigateToModule?: (moduleName: string) => void;
}

// Grid layout constants
const FRAMES = {
  METRICS_HEIGHT: '140px'
};

type ViewMode = 'overview' | 'events' | 'rules' | 'reports' | 'chat';

const EVENT_TYPES: SentrySecurityEvent['type'][] = ['authentication', 'authorization', 'data_access', 'policy_violation'];
const EVENT_SEVERITIES: SentrySecurityEvent['severity'][] = ['low', 'medium', 'high', 'critical'];
const RULE_SEVERITIES: SentryComplianceRule['severity'][] = ['low', 'medium', 'high'];
const REPORT_SCHEDULES: SentryAuditReport['type'][] = ['daily', 'weekly', 'monthly', 'on-demand'];

type DashboardTileId =
  | 'counter-unresolved'
  | 'counter-critical'
  | 'counter-rules'
  | 'counter-reports'
  | 'panel-events'
  | 'panel-compliance'
  | 'panel-status'
  | 'panel-threats';

export const SentryCommandCenter: React.FC<SentryCommandCenterProps> = ({ userId, onBackToLauncher, onNavigateToModule }) => {
  const [activeView, setActiveView] = useState<ViewMode>('overview');
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);
  const [events, setEvents] = useState<SentrySecurityEvent[]>([]);
  const [rules, setRules] = useState<SentryComplianceRule[]>([]);
  const [reports, setReports] = useState<SentryAuditReport[]>([]);
  const [dataEditMode, setDataEditMode] = useState(false);
  const [actionMessage, setActionMessage] = useState<string | null>(null);
  const [actionError, setActionError] = useState<string | null>(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [eventResolutionDrafts, setEventResolutionDrafts] = useState<Record<string, string>>({});
  const [newEventForm, setNewEventForm] = useState({
    eventType: EVENT_TYPES[0],
    severity: EVENT_SEVERITIES[1],
    description: '',
    agentId: '',
    detailsJson: '{}',
  });
  const [newRuleForm, setNewRuleForm] = useState({
    name: '',
    description: '',
    severity: RULE_SEVERITIES[1],
    conditionsJson: '{}',
    actionsJson: '{}',
  });
  const [newReportForm, setNewReportForm] = useState({
    schedule: REPORT_SCHEDULES[0],
    startDate: '',
    endDate: '',
  });
  const containerRef = useRef<HTMLDivElement>(null);
  const sentryBridge = useRef<SentryBridge | null>(null);
  const actionTimerRef = useRef<ReturnType<typeof setTimeout> | null>(null);

  // Chat integration
  const moduleId = 'sentry';
  const residentPersonaId = (MODULE_RESIDENT_AI[moduleId] || 'sentry') as SystemPersonaId;
  const {
    personas: chatPersonas,
    privateMessages,
    privateLoading,
    privateError,
    privateTyping,
    sendPrivateMessage,
  } = useChatStateWithOrchestration({
    userId,
    moduleId,
    residentPersonaId,
    onError: (err) => console.error('Sentry Chat Error:', err),
  });

  const residentPersona = chatPersonas.find((p) => p.id === residentPersonaId);

  useEffect(() => {
    const bridge = new SentryBridge();
    sentryBridge.current = bridge;

    const init = async () => {
      await bridge.initialize();
      loadData();
    };
    init();
  }, []);

  useEffect(() => {
    if (activeView === 'overview' || activeView === 'chat') {
      setDataEditMode(false);
    }
  }, [activeView]);

  useEffect(() => () => {
    if (actionTimerRef.current) {
      clearTimeout(actionTimerRef.current);
    }
  }, []);

  const loadData = async () => {
    if (!sentryBridge.current) return;
    try {
      const props = await sentryBridge.current.getProps();
      setEvents(props.events || []);
      setRules(props.rules || []);
      setReports(props.reports || []);
    } catch (err) {
      console.error('Failed to load Sentry data', err);
    }
  };

  const pushActionMessage = (message: string, isError = false) => {
    if (actionTimerRef.current) {
      clearTimeout(actionTimerRef.current);
    }
    if (isError) {
      setActionError(message);
      setActionMessage(null);
    } else {
      setActionMessage(message);
      setActionError(null);
    }
    actionTimerRef.current = setTimeout(() => {
      setActionMessage(null);
      setActionError(null);
    }, 3500);
  };

  const parseJsonObject = (value: string, label: string): { ok: true; value: JsonObject } | { ok: false; error: string } => {
    const trimmed = value.trim();
    if (!trimmed) {
      return { ok: true, value: {} };
    }
    try {
      const parsed = JSON.parse(trimmed);
      if (!parsed || typeof parsed !== 'object' || Array.isArray(parsed)) {
        return { ok: false, error: `${label} must be a JSON object.` };
      }
      return { ok: true, value: parsed as JsonObject };
    } catch (error) {
      return { ok: false, error: `${label} must be valid JSON.` };
    }
  };

  const mapReportType = (schedule: SentryAuditReport['type']): 'security' | 'compliance' | 'activity' | 'system' => {
    switch (schedule) {
      case 'weekly':
      case 'monthly':
        return 'compliance';
      case 'on-demand':
        return 'activity';
      case 'daily':
      default:
        return 'security';
    }
  };

  const handleResolveEvent = async (eventId: string) => {
    if (!sentryBridge.current) return;
    const resolution = (eventResolutionDrafts[eventId] || '').trim();
    if (!resolution) {
      pushActionMessage('Resolution details are required.', true);
      return;
    }
    try {
      setIsSubmitting(true);
      const response = await sentryBridge.current.resolveEvent(eventId, resolution);
      if (response.success && response.data) {
        setEvents(prev => prev.map(event => (
          event.event_id === eventId ? { ...event, resolved: true, resolution } : event
        )));
        pushActionMessage('Security event resolved.');
      } else {
        pushActionMessage(response.error || 'Failed to resolve event.', true);
      }
    } catch (error) {
      pushActionMessage('Failed to resolve event.', true);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleCreateEvent = async () => {
    if (!sentryBridge.current) return;
    const description = newEventForm.description.trim();
    if (!description) {
      pushActionMessage('Event description is required.', true);
      return;
    }
    const detailsResult = parseJsonObject(newEventForm.detailsJson, 'Details');
    if (!detailsResult.ok) {
      pushActionMessage(detailsResult.error, true);
      return;
    }
    try {
      setIsSubmitting(true);
      const response = await sentryBridge.current.createEvent({
        userId,
        agentId: newEventForm.agentId.trim() || undefined,
        eventType: newEventForm.eventType,
        severity: newEventForm.severity,
        description,
        details: detailsResult.value,
      });
      if (response.success && response.data) {
        setEvents(prev => [response.data, ...prev]);
        setNewEventForm({
          eventType: EVENT_TYPES[0],
          severity: EVENT_SEVERITIES[1],
          description: '',
          agentId: '',
          detailsJson: '{}',
        });
        pushActionMessage('Security event created.');
      } else {
        pushActionMessage(response.error || 'Failed to create event.', true);
      }
    } catch (error) {
      pushActionMessage('Failed to create event.', true);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleToggleRule = async (ruleId: string, enabled: boolean) => {
    if (!sentryBridge.current) return;
    try {
      setIsSubmitting(true);
      const response = await sentryBridge.current.toggleRule(ruleId, enabled);
      if (response.success && response.data) {
        setRules(prev => prev.map(rule => (
          rule.rule_id === ruleId ? { ...rule, enabled } : rule
        )));
        pushActionMessage(`Rule ${enabled ? 'enabled' : 'disabled'}.`);
      } else {
        pushActionMessage(response.error || 'Failed to toggle rule.', true);
      }
    } catch (error) {
      pushActionMessage('Failed to toggle rule.', true);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleCreateRule = async () => {
    if (!sentryBridge.current) return;
    const name = newRuleForm.name.trim();
    const description = newRuleForm.description.trim();
    if (!name || !description) {
      pushActionMessage('Rule name and description are required.', true);
      return;
    }
    const conditionsResult = parseJsonObject(newRuleForm.conditionsJson, 'Conditions');
    if (!conditionsResult.ok) {
      pushActionMessage(conditionsResult.error, true);
      return;
    }
    const actionsResult = parseJsonObject(newRuleForm.actionsJson, 'Actions');
    if (!actionsResult.ok) {
      pushActionMessage(actionsResult.error, true);
      return;
    }
    try {
      setIsSubmitting(true);
      const response = await sentryBridge.current.createRule({
        name,
        description,
        severity: newRuleForm.severity,
        conditions: conditionsResult.value,
        actions: actionsResult.value,
      });
      if (response.success && response.data) {
        setRules(prev => [response.data, ...prev]);
        setNewRuleForm({
          name: '',
          description: '',
          severity: RULE_SEVERITIES[1],
          conditionsJson: '{}',
          actionsJson: '{}',
        });
        pushActionMessage('Compliance rule created.');
      } else {
        pushActionMessage(response.error || 'Failed to create rule.', true);
      }
    } catch (error) {
      pushActionMessage('Failed to create rule.', true);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleGenerateReport = async () => {
    if (!sentryBridge.current) return;
    try {
      setIsSubmitting(true);
      const response = await sentryBridge.current.generateReport({
        userId,
        type: mapReportType(newReportForm.schedule),
        startDate: newReportForm.startDate || undefined,
        endDate: newReportForm.endDate || undefined,
        filters: {
          schedule: newReportForm.schedule,
          startDate: newReportForm.startDate || undefined,
          endDate: newReportForm.endDate || undefined,
        },
      });
      if (response.success && response.data) {
        setReports(prev => [response.data, ...prev]);
        pushActionMessage('Audit report generated.');
      } else {
        pushActionMessage(response.error || 'Failed to generate report.', true);
      }
    } catch (error) {
      pushActionMessage('Failed to generate report.', true);
    } finally {
      setIsSubmitting(false);
    }
  };

  const frameStyle = useMemo(() => ({
