             <div className="overflow-y-auto pr-1 space-y-2">
                 {rules.slice(0, 5).map(r => (
                     <div key={r.rule_id} className="flex items-center justify-between text-xs p-2 border border-white/5 rounded hover:bg-white/5">
                         <span className="text-red-100">{r.name}</span>
                         <div className="flex items-center gap-2">
                            <span className={`w-1.5 h-1.5 rounded-full ${r.enabled ? 'bg-green-500 shadow-[0_0_5px_rgba(34,197,94,0.5)]' : 'bg-gray-500'}`} />
                            <span className={r.enabled ? 'text-green-400' : 'text-gray-500'}>{r.enabled ? 'ON' : 'OFF'}</span>
                         </div>
                     </div>
                 ))}
                 {rules.length === 0 && <div className="text-center text-xs opacity-50 py-4 text-red-200">No rules configured</div>}
             </div>
         </div>
        );
