export type { GraphPath } from '../bridges/VaultBridge';

/**
 * Extends the backend KgEntity definition with the coordinates that D3.js adds
 * while simulating the force-directed layout.
 */
export interface GraphNodeDatum extends KgEntity {
  x?: number;
  y?: number;
  vx?: number;
  vy?: number;
  fx?: number | null;
  fy?: number | null;
  highlighted?: boolean;
}

/**
 * Relationships rendered inside the D3 graph require explicit source/target
 * references so the simulation can build link forces.
 */
export interface GraphLinkDatum extends KgRelationship {
  source: string | GraphNodeDatum;
  target: string | GraphNodeDatum;
  highlighted?: boolean;
}

/**
 * Graph statistics matching the backend GraphStats type
 */
export interface GraphStats {
  total_entities: number;
  total_relationships: number;
  unique_entity_types: number;
  unique_relationship_types: number;
  avg_entity_confidence: number;
  avg_relationship_strength: number;
