import { invoke } from '@tauri-apps/api';
import { BridgeResponse } from '../types/agents';

// ============================================================================
// Type Definitions - Memory Slices
// ============================================================================

export interface MemorySlice {
  id: string;
  user_id: string;
  persona_id: string;
  version: number;
  parent_version: number | null;
  content: string; // Encrypted JSON
  content_type: string; // "conversation", "task", "knowledge", "context", "other"
  tags: string | null; // JSON array
  metadata: string | null; // JSON object
  status: 'active' | 'archived' | 'deleted';
  created_at: number;
  updated_at: number;
  accessed_at: number;
  access_count: number;
}

export interface CreateSliceRequest {
  user_id: string;
  persona_id: string;
  content: Record<string, unknown>;
  content_type: string;
  tags?: string[];
  metadata?: Record<string, unknown>;
}

export interface UpdateSliceRequest {
