// ============================================================================
// VaultBridge Class
// ============================================================================

export class VaultBridge {
  /**
   * Get userId from auth context (if available)
   * Falls back to 'default_user' for backwards compatibility
   */
  private static getUserId(): string {
    // TODO: Integrate with AuthContext
    // For now, check if we have access to window context
    return (window as unknown as { __hearthlink_userId?: string }).__hearthlink_userId || 'default_user';
  }

  // ============================================================================
  // Memory Slices
  // ============================================================================

  /**
   * Create a new memory slice
   */
  static async createMemory(request: CreateSliceRequest): Promise<BridgeResponse<MemorySlice>> {
    try {
      // Ensure userId is set
      if (!request.user_id) {
        request.user_id = this.getUserId();
      }
      const result = await invoke<MemorySlice>('vault_memory_create', { request });
      return { success: true, data: result };
    } catch (error) {
      return { success: false, error: String(error) };
    }
  }

  /**
   * Get memory slices with optional filtering
   */
  static async getMemories(userId?: string, filter?: SliceFilter): Promise<BridgeResponse<MemorySlice[]>> {
    try {
      const effectiveUserId = userId || this.getUserId();
      const request = { userId: effectiveUserId, filter };
      const result = await invoke<MemorySlice[]>('vault_memory_get', { request });
      return { success: true, data: result };
    } catch (error) {
      return { success: false, error: String(error) };
    }
  }

  /**
   * Update an existing memory slice (creates new version)
   */
  static async updateMemory(request: UpdateSliceRequest): Promise<BridgeResponse<MemorySlice>> {
    try {
      const result = await invoke<MemorySlice>('vault_memory_update', { request });
      return { success: true, data: result };
    } catch (error) {
      return { success: false, error: String(error) };
    }
  }

  /**
   * Delete a memory slice (soft delete)
   */
  static async deleteMemory(sliceId: string, userId: string): Promise<BridgeResponse<void>> {
    try {
      const request = { sliceId, userId };
      await invoke<void>('vault_memory_delete', { request });
      return { success: true, data: undefined };
    } catch (error) {
      return { success: false, error: String(error) };
    }
  }

  /**
   * Get version history for a user/persona combination
   */
  static async getMemoryHistory(userId: string, personaId: string): Promise<BridgeResponse<MemorySlice[]>> {
    try {
      const request = { userId, personaId };
      const result = await invoke<MemorySlice[]>('vault_memory_history', { request });
      return { success: true, data: result };
    } catch (error) {
      return { success: false, error: String(error) };
    }
  }

  /**
   * Get memory statistics for a user
   */
  static async getMemoryStats(userId: string): Promise<BridgeResponse<SliceStats>> {
    try {
      const request = { userId };
      const result = await invoke<SliceStats>('vault_memory_stats', { request });
      return { success: true, data: result };
    } catch (error) {
      return { success: false, error: String(error) };
    }
  }

  // ============================================================================
  // RAG Engine
  // ============================================================================

  /**
   * Store a vector embedding for a memory slice
   */
  static async storeEmbedding(embedding: VectorEmbedding): Promise<BridgeResponse<void>> {
    try {
      const request = { embedding };
      await invoke<void>('vault_rag_store_embedding', { request });
      return { success: true, data: undefined };
    } catch (error) {
      return { success: false, error: String(error) };
    }
  }

  /**
   * Get embeddings for a specific memory slice
   */
  static async getEmbeddings(memorySliceId: string): Promise<BridgeResponse<VectorEmbedding[]>> {
    try {
      const request = { memorySliceId };
      const result = await invoke<VectorEmbedding[]>('vault_rag_get_embeddings', { request });
      return { success: true, data: result };
    } catch (error) {
      return { success: false, error: String(error) };
    }
  }

  /**
   * Delete embeddings for a memory slice
   */
  static async deleteEmbeddings(memorySliceId: string): Promise<BridgeResponse<void>> {
    try {
      const request = { memorySliceId };
      await invoke<void>('vault_rag_delete_embeddings', { request });
      return { success: true, data: undefined };
    } catch (error) {
      return { success: false, error: String(error) };
    }
  }

  /**
   * Perform semantic search using cosine similarity
   */
  static async searchSemantic(query: RagQuery): Promise<BridgeResponse<RagSearchResult[]>> {
    try {
      const request = { query };
      const result = await invoke<RagSearchResult[]>('vault_rag_search', { request });
      return { success: true, data: result };
    } catch (error) {
      return { success: false, error: String(error) };
    }
  }

  /**
   * Get RAG engine statistics
   */
  static async getRagStats(userId: string, personaId?: string): Promise<BridgeResponse<RagStats>> {
