/**
 * AldenDashboardGrid - CSS Grid-based responsive dashboard layout
 * Replaces the Rnd-based scaled layout with a truly responsive grid
 */
import React, { memo, useMemo } from 'react';
import { Plus, Calendar, CheckCircle2, Activity, Target } from '@/components/ui/HearthlinkIcons';
import { useADHDUI } from '@/components/ui/ADHDOptimizedInterface';
import type { Task, CalendarEvent } from '../../types/agents';
import { MetricCard } from '@/components/shared/premium/MetricCard';

interface AldenDashboardGridProps {
  stats: {
    totalTasks: number;
    completedToday: number;
    overdue: number;
    upcomingEvents: number;
    unreadEmails: number;
  };
  tasks: Task[];
  events: CalendarEvent[];
  onTaskClick: (task: Task) => void;
  onEventClick: (event: CalendarEvent) => void;
  onNewTask: () => void;
  onNewEvent: () => void;
}

// Memoized MetricCard component  
// MetricCard replaced with shared component

// Frame style for panels
// Frame style for panels
const panelStyle: React.CSSProperties = {
  border: '1px solid rgba(0, 205, 255, 0.35)',
  boxShadow: '0 0 20px rgba(0, 240, 255, 0.08), inset 0 0 12px rgba(197,160,89,0.08)',
  background: 'linear-gradient(135deg, rgba(11,17,36,0.95), rgba(0,240,255,0.02))',
};

// Priority pill styling
const priorityPill = (priority: string) => {
  const colors: Record<string, string> = {
    high: 'bg-red-500/20 text-red-400 border-red-500/30',
    medium: 'bg-yellow-500/20 text-yellow-400 border-yellow-500/30',
    low: 'bg-green-500/20 text-green-400 border-green-500/30',
  };
  return colors[priority] || colors.low;
};

// Status pill styling
const statusPill = (status: string) => {
  const colors: Record<string, string> = {
    pending: 'bg-slate-500/20 text-slate-400 border-slate-500/30',
    in_progress: 'bg-blue-500/20 text-blue-400 border-blue-500/30',
    completed: 'bg-green-500/20 text-green-400 border-green-500/30',
  };
  return colors[status] || colors.pending;
};

export const AldenDashboardGrid: React.FC<AldenDashboardGridProps> = memo(({
  stats,
  tasks,
  events,
  onTaskClick,
  onEventClick,
  onNewTask,
  onNewEvent,
}) => {
  /**
   * RIGOROUS METRICS - 7-Point Standard Compliance
   * 
   * Each metric below adheres to the "Alice Protocol" rigor requirements:
   * 1. Benefit: Direct utility to user productivity management
   * 2. Source: Derived from AldenBridge task/event data
   * 3. Calculation: Transparent formula documented inline
   * 4. Relevance: Correlates to ADHD executive function support
   * 5. Accessibility: Icon + numeric value + subtitle context
   * 6. Interpretability: Simple counts and ratios (traffic-light ready)
   * 7. Redundancy: Each metric is unique
   */
  
  // Calculate Task Velocity: Tasks completed / Hours elapsed today (8am-now)
  const taskVelocity = useMemo(() => {
    const now = new Date();
    const dayStart = new Date(now);
    dayStart.setHours(8, 0, 0, 0); // Workday starts at 8am
    const hoursElapsed = Math.max(1, (now.getTime() - dayStart.getTime()) / (1000 * 60 * 60));
    const velocity = stats.completedToday / hoursElapsed;
    return {
      value: velocity.toFixed(1),
      hoursElapsed: hoursElapsed.toFixed(1),
    };
  }, [stats.completedToday]);

  // Calculate Commitment Ratio: Completed / (Completed + Active) as percentage
  const commitmentRatio = useMemo(() => {
    const total = stats.completedToday + stats.totalTasks;
    if (total === 0) return { value: 0, label: 'No tasks' };
    const ratio = Math.round((stats.completedToday / total) * 100);
    return { value: ratio, label: `${stats.completedToday}/${total}` };
  }, [stats.completedToday, stats.totalTasks]);

  // Memoized metric data - RIGOROUS (no vanity metrics)
  const metrics = useMemo(() => [
    { 
      id: 'tasks', 
      title: 'Active Tasks', 
      value: stats.totalTasks, 
      subtitle: `${stats.overdue} overdue`, 
      accent: 'cyan' as const, 
      icon: <CheckCircle2 size={20} />, 
      active: true 
    },
    { 
      id: 'schedule', 
      title: 'Schedule', 
      value: stats.upcomingEvents, 
      subtitle: 'Events Today', 
      accent: 'gold' as const, 
      icon: <Calendar size={20} />, 
      active: false 
    },
    { 
      id: 'velocity', 
      title: 'Task Velocity', 
      value: taskVelocity.value, 
      subtitle: `tasks/hr (${taskVelocity.hoursElapsed}h)`, 
      accent: 'cyan' as const, 
      icon: <Activity size={20} />, 
      active: stats.completedToday > 0 
    },
    { 
      id: 'commitment', 
      title: 'Commitment', 
      value: `${commitmentRatio.value}%`, 
      subtitle: commitmentRatio.label, 
      accent: commitmentRatio.value >= 50 ? 'green' as const : 'gold' as const, 
      icon: <Target size={20} />, 
      active: commitmentRatio.value > 0 
    },
  ], [stats, taskVelocity, commitmentRatio]);

  return (
    <div className="w-full h-full p-4 overflow-auto">
      {/* CSS Grid Layout */}
      <div 
        className="grid gap-4 h-full"
        style={{
          gridTemplateColumns: 'repeat(4, 1fr)',
          gridTemplateRows: 'auto 1fr',
        }}
      >
        {/* Row 1: Metric Cards */}
        {metrics.map(metric => (
          <div key={metric.id} className="min-w-0">
            <MetricCard
              title={metric.title}
              value={metric.value}
              subtitle={metric.subtitle}
              accentColor={metric.accent}
              icon={metric.icon}
              active={metric.active}
            />
          </div>
        ))}

        {/* Row 2: Panels */}
        {/* Mission Queue - spans 3 columns */}
        <div 
          className="rounded-xl p-4 flex flex-col overflow-hidden"
          style={{ ...panelStyle, gridColumn: 'span 3' }}
        >
          <div className="flex items-center justify-between mb-3 shrink-0">
            <h2 className="text-sm font-semibold text-alden-gold uppercase tracking-widest flex items-center gap-2 font-orbitron-force">
              <CheckCircle2 size={16} />
              Mission Queue
            </h2>
            <button 
              onClick={onNewTask}
              className="flex items-center gap-1 px-3 py-1 bg-alden-cyan/10 border border-alden-cyan/30 rounded-full text-[10px] uppercase tracking-wider text-alden-cyan hover:bg-alden-cyan/20 transition-all font-semibold"
            >
              <Plus size={10} />
              New
            </button>
          </div>
          
          <div className="flex-1 overflow-y-auto space-y-2 min-h-0">
            {tasks.length > 0 ? (
              tasks.slice(0, 6).map(task => (
                <div
                  key={task.id}
                  className="p-3 bg-alden-bg/50 border border-alden-border rounded flex items-center justify-between group hover:border-alden-cyan/30 hover:shadow-[0_0_10px_rgba(0,240,255,0.07)] transition-all cursor-pointer"
                  onClick={() => onTaskClick(task)}
                >
                  <div className="flex items-center gap-3 min-w-0">
                    <div className={`w-1.5 h-1.5 rounded-full shrink-0 ${task.priority === 'high' ? 'bg-[#ef4444] shadow-[0_0_5px_rgba(239,68,68,0.5)]' : 'bg-alden-cyan'}`} />
                    <span className="text-sm text-[#c5d2e8] font-medium truncate">{task.title}</span>
                  </div>
                  <div className="flex items-center gap-2 shrink-0">
                    <span className={`px-2 py-0.5 rounded-full text-[9px] uppercase border ${priorityPill(task.priority)}`}>
                      {task.priority}
                    </span>
                    <span className={`px-2 py-0.5 rounded-full text-[9px] uppercase border ${statusPill(task.status)}`}>
                      {task.status}
                    </span>
                  </div>
                </div>
              ))
            ) : (
              <div className="flex flex-col items-center justify-center py-8 text-[#9fb4d6]/50">
                <CheckCircle2 size={32} className="mb-2 opacity-50" />
                <span className="text-sm">No active tasks</span>
              </div>
            )}
          </div>
        </div>

        {/* Schedule Panel - spans 1 column */}
        <div 
          className="rounded-xl p-4 flex flex-col overflow-hidden"
          style={panelStyle}
        >
          <div className="flex items-center justify-between mb-3 shrink-0">
            <h2 className="text-sm font-semibold text-alden-gold uppercase tracking-widest flex items-center gap-2 font-orbitron-force">
              <Calendar size={16} />
              Schedule
            </h2>
            <button 
              onClick={onNewEvent}
              className="flex items-center gap-1 px-3 py-1 bg-alden-cyan/10 border border-alden-cyan/30 rounded-full text-[10px] uppercase tracking-wider text-alden-cyan hover:bg-alden-cyan/20 transition-all font-semibold"
            >
              <Plus size={10} />
              New
            </button>
          </div>
          
          <div className="flex-1 overflow-y-auto space-y-2 min-h-0">
            {events.length > 0 ? (
              events.slice(0, 4).map(event => (
                <div
                  key={event.id}
                  className="p-3 bg-alden-bg/50 border border-alden-border rounded hover:border-alden-gold/30 transition-all cursor-pointer"
                  onClick={() => onEventClick(event)}
                >
                  <div className="text-[10px] text-alden-gold uppercase tracking-wider mb-1">
                    {new Date(event.start_time).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                  </div>
                  <div className="text-sm text-[#c5d2e8] font-medium truncate">{event.title}</div>
                </div>
              ))
            ) : (
              <div className="flex flex-col items-center justify-center py-6 text-[#9fb4d6]/50">
                <Calendar size={24} className="mb-2 opacity-50" />
                <span className="text-xs">No events</span>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
});

AldenDashboardGrid.displayName = 'AldenDashboardGrid';

export default AldenDashboardGrid;

