      // I should update AldenBridge.ts to support page. But I am "Frontend Integration".
      // I will update `EmailManager` to call it, but realize `AldenBridge` might need update.
      // Actually `AldenBridge.ts` is in `src/bridges`. I can update it.
      
      const messagesResponse = await bridge.getEmailMessages(accountId, 'inbox', pageSize, pageNum);

      if (messagesResponse.success) {
        const account = accounts.find((item) => item.id === accountId);
        const normalized = normalizeEmails(messagesResponse.data, account);
        setEmails(normalized);
        // Only reset selection if page 1?
        if (pageNum === 1) {
            setSelectedEmail(normalized[0] || null);
            setSelectedEmailIds(new Set());
        }
        setPage(pageNum);
      } else {
        console.error('Failed to load inbox:', messagesResponse.error);
        onError?.(messagesResponse.error || 'Failed to load inbox');
      }
    } catch (error) {
      console.error('Failed to load inbox:', error);
      onError?.('Failed to load inbox');
    } finally {
      setLoading(false);
    }
  };

  const addAccount = async () => {
    try {
      setLoading(true);

      const config = { ...newAccount };
      if (config.provider === 'gmail') {
        config.host = 'imap.gmail.com';
        config.port = 993;
        config.use_ssl = true;
      } else if (config.provider === 'outlook') {
        config.host = 'outlook.office365.com';
        config.port = 993;
        config.use_ssl = true;
      } else if (config.provider === 'yahoo') {
        config.host = 'imap.mail.yahoo.com';
        config.port = 993;
        config.use_ssl = true;
      }

      if (!config.display_name) {
        config.display_name = config.email_address;
      }

      const bridge = await ensureBridge();
      // Note: password and user_id are not part of EmailAccount interface, 
      // so we exclude them to satisfy strict typing. 
      // In a real app, password would be handled via a separate secure channel or auth flow.
      await bridge.createEmailAccount({
        email_address: config.email_address,
        display_name: config.display_name,
        server: config.host || '',
        server_host: config.host || undefined,
        port: config.port,
        server_port: config.port,
        ssl_enabled: config.use_ssl,
        protocol: config.protocol as 'imap' | 'pop3',
        status: 'active'
      });

      setShowAddAccount(false);
      setNewAccount({
        email_address: '',
        password: '',
        provider: 'gmail',
        protocol: 'imap',
        host: '',
        port: 993,
        use_ssl: true,
        display_name: '',
      });
      loadAccounts();
    } catch (error) {
      console.error('Failed to add account:', error);
      onError?.('Failed to add email account');
    } finally {
      setLoading(false);
    }
  };

  const syncSelectedAccount = async () => {
    if (!selectedAccountId) return;
    try {
      setLoading(true);
      const bridge = await ensureBridge();
      await bridge.syncEmailAccount(selectedAccountId);
      await loadInbox(selectedAccountId);
    } catch (error) {
      console.error('Failed to sync account:', error);
      onError?.('Failed to sync email account');
    } finally {
      setLoading(false);
    }
  };

  const toggleEmailSelection = useCallback((emailId: string) => {
    setSelectedEmailIds(prev => {
      const next = new Set(prev);
      if (next.has(emailId)) {
        next.delete(emailId);
      } else {
        next.add(emailId);
      }
      return next;
    });
  }, []);

  const markSelectedAsRead = useCallback((read = true) => {
    if (selectedEmailIds.size === 0) return;
    setEmails(prev => prev.map(email => (
      selectedEmailIds.has(email.id) ? { ...email, unread: !read } : email
    )));
    setSelectedEmail(prev => prev ? { ...prev, unread: selectedEmailIds.has(prev.id) ? !read : prev.unread } : null);
  }, [selectedEmailIds]);

  const deleteSelected = useCallback(() => {
    if (selectedEmailIds.size === 0) return;
    setEmails(prev => prev.filter(email => !selectedEmailIds.has(email.id)));
    setSelectedEmailIds(new Set());
    setSelectedEmail(null);
  }, [selectedEmailIds]);

  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (activeTab !== 'inbox') return;
      if (emails.length === 0) return;

      const currentIndex = selectedEmail ? emails.findIndex(mail => mail.id === selectedEmail.id) : -1;

      if (e.key === 'j' && currentIndex < emails.length - 1) {
        e.preventDefault();
        const next = emails[currentIndex + 1] || emails[0];
        setSelectedEmail(next);
      } else if (e.key === 'k' && currentIndex > 0) {
        e.preventDefault();
        const prev = emails[currentIndex - 1] || emails[emails.length - 1];
        setSelectedEmail(prev);
      } else if (e.key.toLowerCase() === 'x' && selectedEmail) {
        e.preventDefault();
        toggleEmailSelection(selectedEmail.id);
      } else if (e.key.toLowerCase() === 'e') {
        e.preventDefault();
        markSelectedAsRead(true);
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [activeTab, emails, selectedEmail, toggleEmailSelection, markSelectedAsRead]);

  const handleReply = (replyAll: boolean) => {
    if (!selectedEmail) return;
    setCompose({
      from_account: selectedEmail.accountId || accounts[0]?.id || '',
      to: replyAll ? `${selectedEmail.from}, ${selectedEmail.to}` : selectedEmail.from,
      subject: `Re: ${selectedEmail.subject}`,
      body: `\n\n--- Original Message ---\nFrom: ${selectedEmail.from}\nTo: ${selectedEmail.to}\nSubject: ${selectedEmail.subject}\nDate: ${selectedEmail.date}\n\n${selectedEmail.body}`,
    });
    setActiveTab('compose');
  };

  const handleForward = () => {
    if (!selectedEmail) return;
    setCompose({
      from_account: selectedEmail.accountId || accounts[0]?.id || '',
      to: '',
      subject: `Fwd: ${selectedEmail.subject}`,
      body: `\n\n--- Original Message ---\nFrom: ${selectedEmail.from}\nTo: ${selectedEmail.to}\nSubject: ${selectedEmail.subject}\nDate: ${selectedEmail.date}\n\n${selectedEmail.body}`,
    });
    setActiveTab('compose');
  };

  const handleMarkAsUnread = () => {
    if (!selectedEmail) return;
    const newState = !selectedEmail.unread;
    setEmails(prev => prev.map(email => email.id === selectedEmail.id ? { ...email, unread: newState } : email));
    setSelectedEmail(prev => prev ? { ...prev, unread: newState } : null);
  };

  const handleDeleteEmail = () => {
    if (!selectedEmail) return;
    setEmails(prev => prev.filter(email => email.id !== selectedEmail.id));
    setSelectedEmail(null);
  };


  const sendEmail = async () => {
    try {
      setLoading(true);
      const recipients = compose.to
        .split(',')
        .map((entry) => entry.trim())
        .filter(Boolean);

      if (!compose.from_account || recipients.length === 0) {
        onError?.('Please select an account and recipient.');
        return;
      }

      const bridge = await ensureBridge();
      const result = await bridge.sendEmail({
        account_id: compose.from_account,
        user_id: resolvedUserId,
        to_addresses: recipients,
        subject: compose.subject,
        body_plain: compose.body,
      });

      setCompose({ from_account: compose.from_account, to: '', subject: '', body: '' });
      if (result.success) {
        // Refresh inbox to show sent mail/state
        await loadInbox(compose.from_account);
      }
    } catch (error) {
      console.error('Failed to send email:', error);
      onError?.('Failed to send email');
    } finally {
      setLoading(false);
    }
  };

  const renderInboxTab = () => {
    if (accounts.length === 0) {
      return (
        <div className="text-center py-12 text-gray-400">
          No email accounts configured. Add an account to start syncing messages.
        </div>
      );
    }

    const sortedEmails = [...filteredEmails].sort(
      (a, b) => new Date(b.date).getTime() - new Date(a.date).getTime()
    );
    const unreadCount = sortedEmails.filter(e => e.unread).length;

    return (
      <div className="grid grid-cols-1 xl:grid-cols-[260px_360px_1fr] gap-4 text-[#c5d2e8] h-full overflow-hidden">
        {/* Folder Rail */}
        <div className="bg-[#0b1124]/50 border border-[rgba(0,205,255,0.15)] rounded-xl p-4 flex flex-col h-full overflow-hidden">
          <div className="flex items-center justify-between mb-4">
            <div className="text-[11px] uppercase tracking-[0.18em] text-[#d6b369] font-semibold">Folders</div>
            <button
              onClick={syncSelectedAccount}
              className="px-3 py-1 text-[11px] uppercase tracking-[0.16em] bg-[#0ef] text-[#051126] rounded-md font-semibold shadow-[0_0_10px_rgba(0,240,255,0.35)]"
            >
              Sync
            </button>
          </div>

          <div className="mb-3">
            <div className="text-[10px] uppercase tracking-[0.14em] text-[#8fa7c7] mb-2">Active Account</div>
            <div className="flex items-center gap-2">
              <select
                id="account-selector"
                value={selectedAccountId}
                onChange={(e) => setSelectedAccountId(e.target.value)}
                className="flex-1 px-3 py-2 bg-[#0a0f1d] border border-[rgba(0,205,255,0.35)] rounded text-[#c5d2e8] text-sm focus:outline-none"
              >
                {accounts.map((account) => (
                  <option key={account.id} value={account.id}>
                    {account.display_name} ({account.email_address})
                  </option>
                ))}
              </select>
            </div>
          </div>

          <div className="space-y-2">
            {[
              { label: 'Inbox', count: unreadCount || sortedEmails.length },
              { label: 'Drafts', count: 0 },
              { label: 'Archive', count: 0 },
              { label: 'Junk', count: 0 },
              { label: 'Trash', count: 0 },
            ].map((folder) => (
              <button
                key={folder.label}
          â€¦675 tokens truncatedâ€¦electedEmail(email)}
                  className={`w-full text-left px-3 py-3 rounded-lg border transition-all ${
                    isSelected
                      ? 'border-[#0ef] bg-[#0ef]/10 shadow-[0_0_12px_rgba(0,240,255,0.22)]'
                      : 'border-white/5 bg-[#0a0f1d] hover:border-[#0ef]/30'
                  }`}
                >
                  <div className="flex items-center justify-between text-xs text-[#8fa7c7]">
                    <span className={`font-semibold ${email.unread ? 'text-[#0ef]' : 'text-[#c5d2e8]'}`}>
                      {email.from || 'Unknown Sender'}
                    </span>
                    <span>{email.date}</span>
                  </div>
                  <div className="flex items-center gap-2 mt-1">
                    {email.unread && <span className="w-2 h-2 rounded-full bg-[#0ef] shadow-[0_0_8px_rgba(0,240,255,0.6)]" />}
                    <span className="text-sm text-[#c5d2e8] font-semibold">{email.subject || '(No Subject)'}</span>
                  </div>
                  <div className="text-xs text-[#8fa7c7] line-clamp-2 mt-1">{email.body || ''}</div>
                </button>
              );
            })}
             
             {/* Pagination Controls */}
             <div className="flex justify-between items-center pt-2 mt-2 border-t border-white/5">
                <button 
                  onClick={() => selectedAccountId && loadInbox(selectedAccountId, Math.max(1, page - 1))}
                  disabled={page <= 1 || loading}
                  className="text-xs text-[#8fa7c7] hover:text-[#0ef] disabled:opacity-30 disabled:hover:text-[#8fa7c7]"
                >
                  &lt; Previous
                </button>
                <span className="text-xs text-gray-500">Page {page}</span>
                <button 
                  onClick={() => selectedAccountId && loadInbox(selectedAccountId, page + 1)}
                  disabled={loading || sortedEmails.length < pageSize}
                  className="text-xs text-[#8fa7c7] hover:text-[#0ef] disabled:opacity-30 disabled:hover:text-[#8fa7c7]"
                >
                  Next &gt;
                </button>
             </div>
          </div>
        </div>

        {/* Reading Pane */}
        <div className="bg-[#0b1124]/50 border border-[rgba(0,205,255,0.15)] rounded-xl p-4 flex flex-col h-full overflow-y-auto scrollbar-thin scrollbar-thumb-alden-cyan/20 scrollbar-track-transparent">
          {selectedEmail ? (
            <>
              <div className="flex flex-wrap gap-2 justify-end mb-3">
                <button onClick={() => handleReply(false)} className="px-3 py-1 text-[11px] uppercase tracking-[0.14em] bg-[#0ef]/10 border border-[#0ef]/50 text-[#0ef] rounded">
                  Reply
                </button>
                <button onClick={() => handleReply(true)} className="px-3 py-1 text-[11px] uppercase tracking-[0.14em] bg-[#0ef]/10 border border-[#0ef]/50 text-[#0ef] rounded">
                  Reply All
                </button>
                <button onClick={handleForward} className="px-3 py-1 text-[11px] uppercase tracking-[0.14em] bg-[#0ef]/10 border border-[#0ef]/50 text-[#0ef] rounded">
                  Forward
                </button>
                <button onClick={handleMarkAsUnread} className="px-3 py-1 text-[11px] uppercase tracking-[0.14em] bg-white/5 border border-white/20 text-[#c5d2e8] rounded">
                  {selectedEmail.unread ? 'Mark Read' : 'Mark Unread'}
                </button>
                <button onClick={handleDeleteEmail} className="px-3 py-1 text-[11px] uppercase tracking-[0.14em] bg-[#f87171]/20 border border-[#f87171]/60 text-[#f87171] rounded">
                  Delete
                </button>
              </div>

              <div className="space-y-2">
                <div className="text-xs text-[#8fa7c7] uppercase tracking-[0.14em]">Subject</div>
                <div className="text-lg font-semibold text-[#c5d2e8]">{selectedEmail.subject}</div>

                <div className="grid grid-cols-2 gap-4 text-sm text-[#8fa7c7]">
                  <div>
                    <div className="uppercase text-[10px] tracking-[0.14em] text-[#d6b369]">From</div>
                    <div className="text-[#c5d2e8]">{selectedEmail.from}</div>
                  </div>
                  <div>
                    <div className="uppercase text-[10px] tracking-[0.14em] text-[#d6b369]">To</div>
                    <div className="text-[#c5d2e8]">{selectedEmail.to || 'N/A'}</div>
                  </div>
                  <div>
                    <div className="uppercase text-[10px] tracking-[0.14em] text-[#d6b369]">Date</div>
                    <div className="text-[#c5d2e8]">{selectedEmail.date}</div>
                  </div>
                </div>

                <div className="mt-3 text-sm text-[#c5d2e8] whitespace-pre-line leading-relaxed bg-black/10 border border-white/5 rounded p-3">
                  {selectedEmail.body || 'No content'}
                </div>

                {selectedEmail.body && selectedEmail.body.length > 0 && (
                  <div className="mt-3 text-xs text-[#8fa7c7]">Attachments: <span className="text-[#d6b369]">Æ’?"</span></div>
                )}
              </div>
            </>
          ) : (
            <div className="text-center text-[#8fa7c7] py-16 border border-white/5 rounded-lg bg-black/10">
              Select an email to view its contents.
            </div>
          )}
        </div>
      </div>
    );
  };

  const renderAccountsTab = () => (
    <div className="space-y-4">
      <div className="flex justify-between items-center">
        <h3 className="text-lg font-semibold text-[#22d3ee]">Accounts</h3>
        <button
          onClick={() => setShowAddAccount(true)}
          className="px-4 py-2 bg-[#22d3ee] text-[#0c1426] rounded hover:bg-[#1ca3c9] transition-colors"
        >
          + Add Account
        </button>
      </div>

      <div className="space-y-3">
        {accounts.map((account) => (
          <div
            key={account.id}
            className="bg-black/30 border border-[#2d3748] rounded-lg p-4 flex flex-col sm:flex-row sm:items-center sm:justify-between gap-3"
          >
            <div>
              <div className="text-white font-semibold">{account.display_name}</div>
              <div className="text-sm text-gray-400">{account.email_address}</div>
            </div>
            <div className="text-sm text-gray-400">
              <div>Provider: {account.provider}</div>
              <div>
                Server:{' '}
                {account.server_host
                  ? `${account.server_host}:${account.server_port ?? 0}`
                  : 'Auto'}
              </div>
              <div>Status: {account.is_active ? 'Active' : 'Disabled'}</div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  const [attachments, setAttachments] = useState<File[]>([]);
  const draftTimerRef = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    // Load draft from localStorage
    const savedDraft = localStorage.getItem('email_draft');
    if (savedDraft) {
      try {
        const draft = JSON.parse(savedDraft);
        if (draft && (draft.to || draft.subject || draft.body)) {
          // Optional: ask user if they want to restore? For now, just restore if empty
          if (!compose.to && !compose.subject && !compose.body) {
             setCompose(draft);
          }
        }
      } catch (e) { console.error('Failed to load draft', e); }
    }
  }, []);

  useEffect(() => {
    // Auto-save draft
    if (draftTimerRef.current) clearTimeout(draftTimerRef.current);
    draftTimerRef.current = setTimeout(() => {
       if (compose.to || compose.subject || compose.body) {
         localStorage.setItem('email_draft', JSON.stringify(compose));
       }
    }, 2000);
    return () => { if(draftTimerRef.current) clearTimeout(draftTimerRef.current); };
  }, [compose]);

  const handleAttachmentChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      setAttachments(prev => [...prev, ...Array.from(e.target.files as FileList)]);
    }
  };

  const removeAttachment = (index: number) => {
    setAttachments(prev => prev.filter((_, i) => i !== index));
  };


  const renderComposeTab = () => (
    <div className="space-y-4 h-full flex flex-col">
      <h3 className="text-lg font-semibold text-[#22d3ee] flex items-center gap-2">
         Compose Email
         <span className="text-xs text-gray-500 font-normal ml-auto">
           {compose.to || compose.subject || compose.body ? 'Draft saving...' : ''}
         </span>
      </h3>

      {accounts.length === 0 ? (
        <div className="text-center text-gray-400 py-12 bg-black/20 rounded-lg border border-[#2d3748]">
          Add an email account before composing a message.
        </div>
      ) : (
        <div className="space-y-4 flex-1 flex flex-col">
          <div className="grid grid-cols-2 gap-4">
             <div>
                <label htmlFor="compose-from-account" className="block text-sm font-medium text-gray-300 mb-1">From Account</label>
                <select
                  id="compose-from-account"
                  value={compose.from_account}
                  onChange={(e) => setCompose({ ...compose, from_account: e.target.value })}
                  className="w-full px-3 py-2 bg-[#0c1426] border border-[#2d3748] rounded text-white focus:outline-none focus:border-[#22d3ee]"
                >
                  <option value="">Select account...</option>
                  {accounts.map((account) => (
                    <option key={account.id} value={account.id}>
                      {account.email_address}
                    </option>
                  ))}
                </select>
             </div>
              <div>
                <label htmlFor="compose-to" className="block text-sm font-medium text-gray-300 mb-1">To</label>
                <input
                  id="compose-to"
                  type="text"
                  value={compose.to}
                  onChange={(e) => setCompose({ ...compose, to: e.target.value })}
                  className="w-full px-3 py-2 bg-[#0c1426] border border-[#2d3748] rounded text-white focus:outline-none focus:border-[#22d3ee]"
                  placeholder="recipient@example.com"
                />
              </div>
          </div>

          <div>
            <label htmlFor="compose-subject" className="block text-sm font-medium text-gray-300 mb-1">Subject</label>
            <input
              id="compose-subject"
              type="text"
              value={compose.subject}
              onChange={(e) => setCompose({ ...compose, subject: e.target.value })}
              className="w-full px-3 py-2 bg-[#0c1426] border border-[#2d3748] rounded text-white focus:outline-none focus:border-[#22d3ee]"
              placeholder="Email subject"
            />
          </div>
          
          <div className="relative">
             <label className="block text-sm font-medium text-gray-300 mb-1">Attachments</label>
             <div className="flex items-center gap-2 mb-2 flex-wrap">
               <label htmlFor="file-upload" className="cursor-pointer px-3 py-1 bg-[#0eff]/10 hover:bg-[#0eff]/20 text-[#0ef] text-xs uppercase tracking-wider border border-[#0ef]/40 rounded transition-all">
                  + Add File
               </label>
               <input 
                  id="file-upload"
                  type="file" 
                  multiple 
                  className="hidden" 
                  onChange={handleAttachmentChange}
               />
               {attachments.map((file, i) => (
                  <div key={i} className="flex items-center gap-2 bg-[#0c1426] border border-[#2d3748] px-2 py-1 rounded text-xs text-gray-300">
                     <span>{file.name}</span>
                     <button onClick={() => removeAttachment(i)} className="text-red-400 hover:text-red-300">A-</button>
                  </div>
               ))}
             </div>
          </div>

          <div className="flex-1 flex flex-col">
            <label htmlFor="compose-message" className="block text-sm font-medium text-gray-300 mb-1">Message</label>
            <textarea
              id="compose-message"
              value={compose.body}
              onChange={(e) => setCompose({ ...compose, body: e.target.value })}
              className="flex-1 w-full px-3 py-2 bg-[#0c1426] border border-[#2d3748] rounded text-white focus:outline-none focus:border-[#22d3ee] resize-none"
              placeholder="Email message..."
            />
          </div>

          <div className="flex justify-end space-x-3 pt-2">
            <button
              onClick={() => {
                setCompose({ from_account: compose.from_account, to: '', subject: '', body: '' });
                localStorage.removeItem('email_draft');
                setAttachments([]);
              }}
              className="px-4 py-2 bg-gray-600 text-white rounded hover:bg-gray-700 transition-colors"
            >
              Discard
            </button>
            <button
              onClick={sendEmail}
              disabled={loading || !compose.from_account || !compose.to || !compose.subject}
              className="px-4 py-2 bg-[#22d3ee] text-[#0c1426] rounded hover:bg-[#1ca3c9] transition-colors disabled:opacity-50"
            >
              {loading ? 'Sending...' : 'Send Email'}
            </button>
          </div>
        </div>
      )}
    </div>
  );

  return (
  <div className="h-full flex flex-col text-[#c5d2e8] p-2">
    <div className="mb-4 flex items-center justify-between">
      <div>
        <h2 className="text-xl font-semibold text-[#22d3ee] mb-1 tracking-wide">Email</h2>
        <p className="text-[#8fa7c7] text-sm">Manage multiple email accounts in one unified interface</p>
      </div>
      <div className="flex items-center gap-2">
        {['inbox','accounts','compose'].map(tab => (
          <button
            key={tab}
            onClick={() => setActiveTab(tab as 'inbox' | 'accounts' | 'compose')}
            className={`px-3 py-1 text-[11px] uppercase tracking-[0.14em] rounded-md ${
              activeTab === tab
                ? 'bg-[#0ef]/10 text-[#0ef] border border-[#0ef]/40 shadow-[0_0_10px_rgba(0,240,255,0.18)]'
                : 'border border-transparent text-[#8fa7c7] hover:text-[#c5d2e8] hover:bg-white/5'
            }`}
          >
            {tab}
          </button>
        ))}
      </div>
    </div>

    {loading && (
      <div className="text-center py-8">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-[#22d3ee] mx-auto"></div>
        <p className="text-gray-400 mt-2">Loading...</p>
      </div>
    )}

    {!loading && (
      <>
        <div className="flex-1 overflow-hidden">
          {activeTab === 'inbox' && renderInboxTab()}
          {activeTab === 'accounts' && renderAccountsTab()}
          {activeTab === 'compose' && renderComposeTab()}
        </div>
      </>
    )}

    {showAddAccount && (
      <div className="fixed inset-0 bg-black/60 flex items-center justify-center z-50 p-4">
        <div className="bg-[#050914] border border-[#22d3ee]/30 rounded-lg w-full max-w-2xl p-6 space-y-4">
          <h3 className="text-xl font-bold text-[#22d3ee]">Add Email Account</h3>
          
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-400 mb-1">Email Provider</label>
              <select
                value={newAccount.provider}
                onChange={(e) => setNewAccount({ ...newAccount, provider: e.target.value })}
                className="w-full px-3 py-2 bg-[#0c1426] border border-[#2d3748] rounded text-white"
              >
                <option value="gmail">Gmail</option>
                <option value="outlook">Outlook</option>
                <option value="yahoo">Yahoo</option>
                <option value="custom">Custom IMAP/SMTP</option>
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-400 mb-1">Display Name</label>
              <input
                type="text"
                value={newAccount.display_name}
                onChange={(e) => setNewAccount({ ...newAccount, display_name: e.target.value })}
                placeholder="My Email"
                className="w-full px-3 py-2 bg-[#0c1426] border border-[#2d3748] rounded text-white"
              />
            </div>
          </div>

          <div>
             <label className="block text-sm font-medium text-gray-400 mb-1">Email Address</label>
             <input
               type="email"
               value={newAccount.email_address}
               onChange={(e) => setNewAccount({ ...newAccount, email_address: e.target.value })}
               placeholder="user@example.com"
               className="w-full px-3 py-2 bg-[#0c1426] border border-[#2d3748] rounded text-white"
             />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-400 mb-1">Password / App Password</label>
            <input
              type="password"
              value={newAccount.password}
              onChange={(e) => setNewAccount({ ...newAccount, password: e.target.value })}
              className="w-full px-3 py-2 bg-[#0c1426] border border-[#2d3748] rounded text-white"
            />
            <p className="text-xs text-gray-500 mt-1">For Gmail/Outlook, please use an App Password if 2FA is enabled.</p>
          </div>

          {newAccount.provider === 'custom' && (
             <div className="grid grid-cols-2 gap-4 border-t border-gray-800 pt-4">
                <div>
                   <label className="block text-sm font-medium text-gray-400 mb-1">IMAP Host</label>
                   <input
                     type="text"
                     value={newAccount.host}
                     onChange={(e) => setNewAccount({ ...newAccount, host: e.target.value })}
                     className="w-full px-3 py-2 bg-[#0c1426] border border-[#2d3748] rounded text-white"
                   />
                </div>
                <div>
                   <label className="block text-sm font-medium text-gray-400 mb-1">IMAP Port</label>
                   <input
                     type="number"
                     value={newAccount.port}
                     onChange={(e) => setNewAccount({ ...newAccount, port: parseInt(e.target.value) })}
                     className="w-full px-3 py-2 bg-[#0c1426] border border-[#2d3748] rounded text-white"
                   />
                </div>
             </div>
          )}

          <div className="flex justify-end gap-3 pt-4">
            <button
               onClick={() => setShowAddAccount(false)}
               className="px-4 py-2 text-gray-400 hover:text-white"
            >
              Cancel
            </button>
            <button
               onClick={addAccount}
               disabled={!newAccount.email_address || !newAccount.password}
               className="px-4 py-2 bg-[#22d3ee] text-black rounded disabled:opacity-50"
            >
              Connect Account
            </button>
          </div>
        </div>
      </div>
    )}

  </div>
);
};

export default EmailManager;

