                        <h3 className="text-lg font-bold text-slate-200 group-hover:text-emerald-200 transition-colors mb-1">{plugin.name}</h3>
                        <p className="text-xs text-emerald-600/80 mb-3 font-mono">by {plugin.author}</p>
