  /**
   * Search the help catalog for context-aware assistance that agents can expand.
   */
  static searchHelp(
    query: string,
    options?: { modules?: HelpModuleId[]; tags?: string[]; contexts?: string[] },
  ): Array<{ id: string; title: string; description: string; moduleId: HelpModuleId; contexts?: string[]; tags: string[]; shortcuts?: string[] }> {
    const results = searchHelpCatalog(query, {
      modules: options?.modules,
