/**
 * Synapse Agent Bridge Implementation (Tauri IPC)
 *
 * Provides React/TypeScript bridge for Synapse agent system including:
 * - External service gateway management
 * - Plugin system
 * - Protocol boundary enforcement
 * - Connection management
 * - Security logging
 *
 * This bridge uses Tauri IPC (invoke) to communicate with the Rust backend
 * instead of HTTP requests or Python subprocess calls.
 *
 * @version 2.0.0
 * @date 2025-10-31
 */

import { invoke } from '@tauri-apps/api/tauri';
import { AsyncBaseBridge } from './AsyncBaseBridge';
import { BridgeConfig } from './BaseBridge';
import {
  SynapseProps,
  SynapsePlugin,
  SynapseConnection,
  SynapseSecurityLog,
  BridgeResponse,
} from '../types/agents';

export interface PluginMetrics {
  memory_mb: number;
  error_rate_percent: number;
  uptime_seconds: number;
}

type JsonObject = Record<string, unknown>;
type JsonValue = string | number | boolean | null | JsonObject | JsonValue[];

/**
 * Request type for creating a new plugin
 */
export interface CreatePluginRequest {
  name: string;
  version: string;
  description: string;
  permissions: string[];
  manifest: JsonObject;
  pluginId?: string;
}

/**
 * Request type for creating a new connection
 */
export interface CreateConnectionRequest {
  name: string;
  endpoint: string;
  credentials: Record<string, string>;
  pluginId?: string;
  protocol?: JsonObject;
}

/**
 * Security log filter options
 */
export interface SecurityLogFilter {
  pluginId?: string;
  connectionId?: string;
  severity?: 'info' | 'warning' | 'critical';
  limit?: number;
}

/**
 * Connection test result
 */
export interface ConnectionTestResult {
  success: boolean;
  status_code?: number;
  latency_ms?: number;
  endpoint: string;
  error?: string;
}

/**
 * Security summary statistics
 */
export interface SecuritySummary {
  total_logs: number;
  by_severity: Record<string, number>;
  by_event_type: Record<string, number>;
  last_24_hours: number;
}

export interface SkillPermission {
  type_: string;
  scope: string;
  justification: string;
}

export interface SkillDependency {
  name: string;
  version: string;
  type_: string;
}

export interface SkillParameter {
  name: string;
  type_: string;
  description: string;
  required: boolean;
  default?: JsonValue;
  validation?: JsonValue;
}

export interface SkillManifest {
  skill_id: string;
  name: string;
  version: string;
  description: string;
  author?: string | null;
  category: string;
  entry_point: string;
  runtime: string;
  permissions: SkillPermission[];
  required_tools: string[];
  dependencies: SkillDependency[];
  parameters: SkillParameter[];
  output_schema: JsonValue;
  tags: string[];
  compatibility: string[];
  created_at?: string | null;
  updated_at?: string | null;
}

export interface SkillInstance {
  instance_id: string;
  skill_id: string;
  agent_id: string;
  enabled: boolean;
  configuration: JsonValue;
  installed_at: string;
  last_used?: string | null;
  usage_count: number;
  error_count: number;
}

export interface InstanceConfigHistory {
  history_id: string;
  instance_id: string;
  configuration: JsonValue;
  changed_at: string;
  changed_by: string;
  reason?: string | null;
}

export interface InstanceStats {
  instance_id: string;
  skill_id: string;
  agent_id: string;
  enabled: boolean;
  total_executions: number;
  success_rate: number;
  average_duration_ms?: number | null;
  error_count: number;
  last_used?: string | null;
}

export interface ExecutionResult {
  execution_id: string;
  status: 'pending' | 'running' | 'completed' | 'failed' | 'cancelled';
  outputs?: JsonValue | null;
  error?: string | null;
  duration_ms: number;
}

export interface ExecutionRecord {
  execution_id: string;
  skill_id: string;
  instance_id: string;
  agent_id: string;
  status: ExecutionResult['status'];
  inputs: JsonValue;
