  /**
   * Clear security logs older than a specific timestamp
   * Invokes: synapse_clear_old_security_logs
   */
  async clearSecurityLogs(olderThan: Date): Promise<BridgeResponse<{ deleted: number }>> {
    try {
      const olderThanIso = olderThan.toISOString();
