/**
 * AI Skill Bridge - Frontend Integration
 * 
 * This module provides the TypeScript interface for AI systems (Claude, Gemini, Kilo Code)
 * to interact with Hearthlink's skill system through the Rust backend.
 */

import { invoke } from '@tauri-apps/api/tauri';
import { BaseBridge } from '../bridges/BaseBridge';
import { searchHelpCatalog, HelpModuleId } from '../data/helpCatalog';
import { JsonObject, AgentId } from '../types/agents';

// Types
export interface AISystem {
