          return advancedSkills;
        default:
          return basicSkills;
      }
    } catch (error) {
      console.error('Failed to get available skills:', error);
      return [];
    }
  }

  /**
   * Get skill information
   */
  static async getSkillInfo(skillName: string): Promise<JsonObject | null> {
