import React, { useEffect, useMemo, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Brain,
  Check,
  Download,
  Info,
  Loader2,
  Search,
  Shield,
  Star,
  Zap,
  X,
  Code,
  Mic,
  Box,
} from '@/components/ui/HearthlinkIcons';
import { useSynapseBridge } from '@/hooks/useBridge';
import type { SkillInstance, SkillManifest } from '@/bridges/SynapseBridge';

type SkillCategory = 'Automation' | 'Knowledge' | 'Security' | 'Voice' | 'General';

interface SkillCard {
  id: string;
  name: string;
  description: string;
  targetAgent: string;
  capabilities: string[];
  category: SkillCategory;
  rating: number;
  downloads: number;
  tags: string[];
  lastUpdated: string;
}

const CATEGORY_FILTERS: Array<SkillCategory | 'All'> = ['All', 'Automation', 'Knowledge', 'Security', 'Voice', 'General'];

const SKILL_OVERRIDES: Record<string, Partial<SkillCard>> = {
  'task-management': {
    category: 'Automation',
    tags: ['tasks', 'planning', 'workflow'],
    rating: 4.9,
    downloads: 2890,
    lastUpdated: '2 days ago',
  },
  'knowledge-retrieval': {
    category: 'Knowledge',
    tags: ['search', 'vault', 'summaries'],
    rating: 4.7,
    downloads: 2140,
    lastUpdated: '1 week ago',
  },
  'security-monitoring': {
    category: 'Security',
    tags: ['alerts', 'audit', 'guards'],
    rating: 4.8,
    downloads: 880,
    lastUpdated: '5 days ago',
  },
  'voice-integration': {
    category: 'Voice',
    tags: ['speech', 'hands-free', 'assistant'],
    rating: 4.5,
    downloads: 1320,
    lastUpdated: '3 weeks ago',
  },
  'development-automation': {
    category: 'Automation',
    tags: ['build', 'test', 'deploy'],
    rating: 4.6,
    downloads: 1450,
    lastUpdated: '1 month ago',
  },
  'general-assistance': {
    category: 'General',
    tags: ['general', 'assistant'],
    rating: 4.3,
    downloads: 5100,
    lastUpdated: '2 months ago',
  },
};

const titleCase = (value: string): string =>
  value
    .split('-')
    .map((segment) => segment.charAt(0).toUpperCase() + segment.slice(1))
    .join(' ');

const agentColor = (targetAgent: string) => {
  switch (targetAgent) {
    case 'sentry':
      return 'text-red-400 bg-red-500/10 border-red-500/20';
    case 'vault':
      return 'text-purple-400 bg-purple-500/10 border-purple-500/20';
    case 'synapse':
      return 'text-emerald-400 bg-emerald-500/10 border-emerald-500/20';
    case 'alden':
    default:
      return 'text-alden-gold bg-alden-gold/10 border-alden-gold/20';
  }
};

const categoryIcon = (category: SkillCategory) => {
  switch (category) {
    case 'Knowledge':
      return <Brain size={18} />;
    case 'Security':
      return <Shield size={18} />;
    case 'Voice':
      return <Mic size={18} />;
    case 'Automation':
      return <Code size={18} />;
    default:
      return <Box size={18} />;
  }
};

const categoryAccent = (category: SkillCategory) => {
  switch (category) {
    case 'Knowledge':
      return 'text-purple-300 bg-purple-500/10 border-purple-500/20';
    case 'Security':
      return 'text-red-300 bg-red-500/10 border-red-500/20';
    case 'Voice':
      return 'text-cyan-300 bg-cyan-500/10 border-cyan-500/20';
    case 'Automation':
      return 'text-emerald-300 bg-emerald-500/10 border-emerald-500/20';
    default:
      return 'text-slate-300 bg-white/5 border-white/10';
  }
};

const SKILL_COMPATIBILITY_FALLBACK = 'alden';

const normalizeManifestCategory = (category: SkillManifest['category']): string => {
  if (typeof category === 'string') return category;
  if (category && typeof category === 'object' && 'other' in category && typeof category.other === 'string') {
    return category.other;
  }
  return '';
};

const mapManifestCategory = (category: string): SkillCategory => {
  switch (category.toLowerCase()) {
    case 'analysis':
    case 'memory':
      return 'Knowledge';
    case 'automation':
    case 'productivity':
      return 'Automation';
    case 'communication':
    case 'integration':
      return 'General';
    case 'utility':
      return 'General';
    default:
      return category.includes('voice') ? 'Voice' : 'General';
  }
};

const resolveTargetAgent = (manifest: SkillManifest): string => {
  const allowed = new Set(['alden', 'alice', 'mimic', 'vault', 'synapse', 'sentry', 'core']);
  const compat = Array.isArray(manifest.compatibility) ? manifest.compatibility : [];
  const match = compat.find((entry) => allowed.has(entry.toLowerCase()));
  return match?.toLowerCase() ?? SKILL_COMPATIBILITY_FALLBACK;
};

const resolveCapabilities = (manifest: SkillManifest): string[] => {
  if (manifest.parameters?.length) {
    return manifest.parameters.map((param) => param.name);
  }
  if (manifest.required_tools?.length) {
    return manifest.required_tools;
  }
  return [];
};

const formatTimestamp = (value?: string | null): string => {
  if (!value) return 'recently';
  const parsed = new Date(value);
  if (Number.isNaN(parsed.getTime())) return value;
  return parsed.toLocaleDateString();
};

const mapSkillCard = (manifest: SkillManifest): SkillCard => {
  const overrides = SKILL_OVERRIDES[manifest.skill_id] || {};
  const category = overrides.category || mapManifestCategory(normalizeManifestCategory(manifest.category));

  return {
    id: manifest.skill_id,
    name: overrides.name || manifest.name || titleCase(manifest.skill_id),
    description: overrides.description || manifest.description || 'No description available yet.',
    targetAgent: overrides.targetAgent || resolveTargetAgent(manifest),
    capabilities: overrides.capabilities || resolveCapabilities(manifest),
    category,
    rating: overrides.rating ?? 4.4,
    downloads: overrides.downloads ?? 780,
    tags: overrides.tags ?? manifest.tags ?? [],
    lastUpdated: overrides.lastUpdated ?? formatTimestamp(manifest.updated_at || manifest.created_at),
  };
};

const buildInstanceMap = (instances: SkillInstance[]): Record<string, SkillInstance> => {
  return instances.reduce<Record<string, SkillInstance>>((acc, instance) => {
    const existing = acc[instance.skill_id];
    if (!existing) {
      acc[instance.skill_id] = instance;
      return acc;
    }
    if (existing.installed_at < instance.installed_at) {
      acc[instance.skill_id] = instance;
    }
    return acc;
  }, {});
};

const matchesCompatibility = (manifest: SkillManifest, aiSystem: string): boolean => {
  if (!manifest.compatibility || manifest.compatibility.length === 0) return true;
  return manifest.compatibility.some((entry) => entry.toLowerCase() === aiSystem.toLowerCase());
};

export const SkillMarketplace: React.FC = () => {
  const { bridge: synapseBridge } = useSynapseBridge();
  const [skills, setSkills] = useState<SkillCard[]>([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [categoryFilter, setCategoryFilter] = useState<SkillCategory | 'All'>('All');
  const [aiSystem, setAiSystem] = useState<'claude' | 'gemini' | 'kilo-code'>('claude');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [activeSkillIds, setActiveSkillIds] = useState<string[]>([]);
  const [selectedSkill, setSelectedSkill] = useState<SkillCard | null>(null);
  const [instancesBySkill, setInstancesBySkill] = useState<Record<string, SkillInstance>>({});
  const [busySkillIds, setBusySkillIds] = useState<string[]>([]);

  useEffect(() => {
    let mounted = true;
    const loadSkills = async () => {
      if (!synapseBridge) return;
      setLoading(true);
      setError(null);
      try {
        const [skillsResponse, instancesResponse] = await Promise.all([
          synapseBridge.listSkills(),
          synapseBridge.listSkillInstances(aiSystem),
        ]);

        if (!skillsResponse.success) {
          throw new Error(skillsResponse.error || 'Unable to load skills.');
        }

        if (!instancesResponse.success) {
          throw new Error(instancesResponse.error || 'Unable to load skill instances.');
        }

        const available = skillsResponse.data?.filter((skill) => matchesCompatibility(skill, aiSystem)) ?? [];
        const enriched = available.map(mapSkillCard);
        const instanceMap = buildInstanceMap(instancesResponse.data ?? []);
        const activeIds = Object.values(instanceMap)
          .filter((instance) => instance.enabled)
          .map((instance) => instance.skill_id);

        if (mounted) {
          setSkills(enriched);
          setInstancesBySkill(instanceMap);
          setActiveSkillIds(activeIds.filter((id) => enriched.some((skill) => skill.id === id)));
        }
      } catch (loadError) {
        console.error('Failed to load skills:', loadError);
        if (mounted) setError('Unable to load skills right now.');
      } finally {
        if (mounted) setLoading(false);
      }
    };

    loadSkills();
    return () => {
      mounted = false;
    };
  }, [aiSystem, synapseBridge]);

  const filteredSkills = useMemo(() => {
    const query = searchQuery.trim().toLowerCase();
    return skills.filter((skill) => {
      const matchesSearch =
        !query ||
        skill.name.toLowerCase().includes(query) ||
        skill.description.toLowerCase().includes(query) ||
        skill.tags.some((tag) => tag.toLowerCase().includes(query));
      const matchesCategory = categoryFilter === 'All' || skill.category === categoryFilter;
      return matchesSearch && matchesCategory;
    });
  }, [skills, searchQuery, categoryFilter]);

  const toggleActive = async (skillId: string) => {
    if (!synapseBridge) return;
    if (busySkillIds.includes(skillId)) return;

    setBusySkillIds((prev) => [...prev, skillId]);
    setError(null);

    try {
      const existing = instancesBySkill[skillId];
      if (!existing) {
        const created = await synapseBridge.createSkillInstance(skillId, aiSystem);
        if (!created.success || !created.data) {
          throw new Error(created.error || 'Failed to activate skill.');
        }

        const updatedMap = { ...instancesBySkill, [skillId]: created.data };
        setInstancesBySkill(updatedMap);
        setActiveSkillIds((prev) => Array.from(new Set([...prev, skillId])));
        return;
      }

      if (existing.enabled) {
        const disabled = await synapseBridge.disableSkillInstance(existing.instance_id);
        if (!disabled.success || !disabled.data) {
          throw new Error(disabled.error || 'Failed to deactivate skill.');
        }

        const updatedMap = { ...instancesBySkill, [skillId]: disabled.data };
        setInstancesBySkill(updatedMap);
        setActiveSkillIds((prev) => prev.filter((id) => id !== skillId));
      } else {
        const enabled = await synapseBridge.enableSkillInstance(existing.instance_id);
        if (!enabled.success || !enabled.data) {
          throw new Error(enabled.error || 'Failed to activate skill.');
        }

        const updatedMap = { ...instancesBySkill, [skillId]: enabled.data };
        setInstancesBySkill(updatedMap);
        setActiveSkillIds((prev) => Array.from(new Set([...prev, skillId])));
      }
    } catch (toggleError) {
      console.error('Failed to toggle skill:', toggleError);
      setError('Unable to update skill state right now.');
    } finally {
      setBusySkillIds((prev) => prev.filter((id) => id !== skillId));
    }
  };

  return (
    <div className="h-full flex flex-col space-y-5 min-h-0">
      <div className="flex flex-col gap-3 md:flex-row md:items-center md:justify-between">
        <div>
          <h2 className="text-lg font-semibold text-alden-gold uppercase tracking-widest font-orbitron-force flex items-center gap-2">
            <Brain size={20} />
            Skill Marketplace
          </h2>
          <p className="text-xs text-slate-400 mt-1">
            Browse and activate skills for the connected AI systems.
          </p>
        </div>
        <div className="flex items-center gap-3">
          <div className="flex items-center gap-2 text-[10px] uppercase tracking-widest text-slate-500">
            AI System
          </div>
          <select
            value={aiSystem}
            onChange={(event) => setAiSystem(event.target.value as typeof aiSystem)}
            className="bg-[#0b1221] border border-alden-cyan/30 rounded px-3 py-2 text-xs text-[#9fb4d6] focus:outline-none focus:border-alden-cyan/80"
            style={{ colorScheme: 'dark' }}
          >
            <option value="claude">Claude</option>
            <option value="gemini">Gemini</option>
            <option value="kilo-code">Kilo Code</option>
          </select>
        </div>
      </div>

      <div className="flex flex-col gap-3 md:flex-row md:items-center">
        <div className="relative flex-1">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-500" size={16} />
          <input
            type="text"
            placeholder="Search skills..."
            className="w-full pl-9 pr-4 py-2.5 bg-black/40 border border-white/10 rounded-lg text-slate-200 placeholder:text-slate-500 focus:outline-none focus:border-alden-cyan/60 focus:bg-black/60 transition-all"
            value={searchQuery}
            onChange={(event) => setSearchQuery(event.target.value)}
          />
        </div>
        <div className="flex gap-2 overflow-x-auto pb-1 md:pb-0 scrollbar-hide">
          {CATEGORY_FILTERS.map((category) => (
            <button
              key={category}
              onClick={() => setCategoryFilter(category)}
              className={`px-3 py-1.5 rounded-lg text-[11px] font-semibold uppercase tracking-widest whitespace-nowrap transition-all ${
                categoryFilter === category
                  ? 'bg-alden-cyan/20 text-alden-cyan border border-alden-cyan/40'
                  : 'bg-black/30 text-slate-400 border border-white/5 hover:text-slate-200 hover:bg-white/5'
              }`}
            >
              {category}
            </button>
          ))}
        </div>
      </div>

      {loading && (
        <div className="flex items-center gap-2 text-sm text-slate-400">
          <Loader2 size={16} className="text-alden-cyan" />
          Loading skills...
        </div>
      )}

      {error && (
        <div className="p-4 rounded-lg border border-red-500/30 bg-red-500/10 text-red-200 text-sm flex items-center gap-2">
          <Info size={16} />
          {error}
          <button
            onClick={() => setAiSystem((prev) => prev)}
            className="ml-auto px-3 py-1 rounded bg-red-500/20 text-red-100 text-xs uppercase tracking-widest"
          >
            Retry
          </button>
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 overflow-y-auto pr-2 pb-2 flex-1 scrollbar-thin scrollbar-thumb-alden-gold/40 scrollbar-track-transparent">
        <AnimatePresence mode="popLayout">
          {!loading && filteredSkills.length === 0 && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="col-span-full text-center text-sm text-slate-400 py-12 italic"
            >
              No skills match your filters.
            </motion.div>
          )}

          {filteredSkills.map((skill, index) => {
            const isActive = activeSkillIds.includes(skill.id);
            return (
              <motion.div
                layout
                initial={{ opacity: 0, y: 12 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, scale: 0.96 }}
                transition={{ duration: 0.25, delay: index * 0.03 }}
                key={skill.id}
                onClick={() => setSelectedSkill(skill)}
                className="relative group bg-black/35 border border-white/10 rounded-xl p-4 hover:border-alden-cyan/40 hover:bg-black/50 transition-all cursor-pointer"
              >
                <div className="flex items-start justify-between gap-3 mb-3">
                  <div className={`p-2.5 rounded-lg border ${categoryAccent(skill.category)}`}>
                    {categoryIcon(skill.category)}
                  </div>
                  <div className="flex flex-col items-end gap-1">
                    <span className="px-2 py-0.5 rounded-full bg-white/5 text-[10px] text-slate-400 border border-white/10">
                      v1.0
                    </span>
                    <div className="flex items-center text-amber-400 text-[10px]">
                      <Star size={10} fill="currentColor" />
                      <span className="ml-1 font-mono">{skill.rating.toFixed(1)}</span>
                    </div>
                  </div>
                </div>

                <h3 className="text-sm font-semibold text-slate-100 group-hover:text-alden-cyan transition-colors">
                  {skill.name}
                </h3>
                <p className="text-[11px] text-slate-500 mt-1">{skill.description}</p>

                <div className="flex flex-wrap gap-1.5 mt-3">
                  {skill.tags.slice(0, 3).map((tag) => (
                    <span key={tag} className="px-2 py-0.5 rounded-full bg-white/5 text-[10px] text-slate-400">
                      #{tag}
                    </span>
                  ))}
                </div>

                <div className="flex items-center justify-between mt-4 pt-3 border-t border-white/10">
                  <span className={`text-[10px] uppercase tracking-widest px-2 py-1 rounded-full border ${agentColor(skill.targetAgent)}`}>
                    {skill.targetAgent}
                  </span>
                  <button
                    onClick={(event) => {
                      event.stopPropagation();
                      void toggleActive(skill.id);
                    }}
                    disabled={busySkillIds.includes(skill.id)}
                    className={`px-3 py-1.5 rounded-md text-[10px] font-bold uppercase tracking-wider flex items-center gap-2 transition-all ${
                      isActive
                        ? 'bg-alden-cyan/20 text-alden-cyan border border-alden-cyan/40'
                        : 'bg-alden-gold/15 text-alden-gold border border-alden-gold/30 hover:bg-alden-gold/25'
                    }`}
                  >
                    {isActive ? (
                      <>
                        <Check size={12} />
                        Active
                      </>
                    ) : (
                      <>
                        <Zap size={12} />
                        Activate
                      </>
                    )}
                  </button>
                </div>
              </motion.div>
            );
          })}
        </AnimatePresence>
      </div>

      <AnimatePresence>
        {selectedSkill && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="absolute inset-0 z-50 flex items-center justify-center p-4 bg-black/70 backdrop-blur-sm"
            onClick={() => setSelectedSkill(null)}
          >
            <motion.div
              initial={{ scale: 0.96, opacity: 0, y: 10 }}
              animate={{ scale: 1, opacity: 1, y: 0 }}
              exit={{ scale: 0.96, opacity: 0, y: 10 }}
              onClick={(event) => event.stopPropagation()}
              className="w-full max-w-2xl bg-[#0b1221] border border-alden-cyan/30 rounded-2xl shadow-[0_0_40px_rgba(0,0,0,0.7)] overflow-hidden flex flex-col max-h-[90%]"
            >
              <div className="relative p-6 border-b border-white/10 bg-gradient-to-b from-alden-cyan/10 to-transparent">
                <button
                  onClick={() => setSelectedSkill(null)}
                  className="absolute top-4 right-4 p-2 text-slate-400 hover:text-white rounded-full hover:bg-white/10 transition-colors"
                >
                  <X size={18} />
                </button>
                <div className="flex items-start gap-4">
                  <div className={`p-3 rounded-xl border ${categoryAccent(selectedSkill.category)}`}>
                    {categoryIcon(selectedSkill.category)}
                  </div>
                  <div>
                    <h3 className="text-xl font-semibold text-white">{selectedSkill.name}</h3>
                    <p className="text-xs text-slate-400 mt-1">{selectedSkill.description}</p>
                    <div className="flex items-center gap-3 text-[11px] text-slate-500 mt-2">
                      <span className={`px-2 py-0.5 rounded-full border ${agentColor(selectedSkill.targetAgent)}`}>
                        {selectedSkill.targetAgent}
                      </span>
                      <span className="flex items-center gap-1 text-amber-400">
                        <Star size={10} fill="currentColor" />
                        {selectedSkill.rating.toFixed(1)}
                      </span>
                      <span className="flex items-center gap-1 text-slate-400">
                        <Download size={12} />
                        {selectedSkill.downloads.toLocaleString()}
                      </span>
                    </div>
                  </div>
                </div>
              </div>

              <div className="p-6 space-y-4 overflow-y-auto">
                <div>
                  <h4 className="text-xs uppercase tracking-widest text-slate-400 mb-2">Capabilities</h4>
                  <div className="flex flex-wrap gap-2">
                    {selectedSkill.capabilities.length > 0 ? (
                      selectedSkill.capabilities.map((capability) => (
                        <span key={capability} className="px-2 py-1 rounded-md bg-white/5 text-xs text-slate-300">
                          {capability}
                        </span>
                      ))
                    ) : (
                      <span className="text-xs text-slate-500">Capabilities pending.</span>
                    )}
                  </div>
                </div>
                <div>
                  <h4 className="text-xs uppercase tracking-widest text-slate-400 mb-2">Tags</h4>
                  <div className="flex flex-wrap gap-2">
                    {selectedSkill.tags.length > 0 ? (
                      selectedSkill.tags.map((tag) => (
                        <span key={tag} className="px-2 py-1 rounded-full bg-white/5 text-xs text-slate-300">
                          #{tag}
                        </span>
                      ))
                    ) : (
                      <span className="text-xs text-slate-500">No tags available.</span>
                    )}
                  </div>
                </div>
                <div className="text-xs text-slate-500 border-t border-white/10 pt-3 flex justify-between">
                  <span>Updated: {selectedSkill.lastUpdated}</span>
                  <span>ID: {selectedSkill.id}</span>
                </div>
              </div>

              <div className="p-4 border-t border-white/10 bg-black/30 flex justify-end gap-2">
                <button
                  onClick={() => setSelectedSkill(null)}
                  className="px-4 py-2 rounded-lg text-xs uppercase tracking-widest text-slate-400 hover:text-white hover:bg-white/10 transition-colors"
                >
                  Close
                </button>
                <button
                  onClick={() => {
                    void toggleActive(selectedSkill.id);
                    setSelectedSkill(null);
                  }}
                  className="px-5 py-2 rounded-lg text-xs uppercase tracking-widest font-semibold bg-alden-cyan/20 text-alden-cyan border border-alden-cyan/40 hover:bg-alden-cyan/30 transition-all"
                >
                  Activate Skill
                </button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default SkillMarketplace;

