/**
 * E2E Tests for Synapse Skill System
 *
 * Tests the Synapse skill system functionality:
 * - List available skills from marketplace
 * - Activate a skill for an agent
 * - Deactivate an active skill
 * - Verify skill instance persistence after reload
 * - Error handling for non-existent skill activation
 *
 * @see src/bridges/SynapseBridge.ts
 * @see src-tauri/src/synapse/commands.rs
 */

import { test, expect, Mocks } from './setup';

test.describe('Synapse Skill System', () => {
  test.beforeEach(async ({ page }) => {
    // Install mocks with skill data
    await Mocks.install(page);
    Mocks.scenarios.skillsAvailable();

    // Disable tutorial
    await page.addInitScript(() => {
      window.localStorage.setItem('hl_first7_state', JSON.stringify({
        currentDay: 7,
        completedDays: [1, 2, 3, 4, 5, 6, 7],
        completedSteps: [],
        lastActive: new Date().toISOString(),
        dismissed: true,
        autoOpened: true
      }));
    });

    // Navigate to Synapse
    await page.goto('/');
    await page.waitForLoadState('networkidle');

    const synapseButton = page.locator('[data-testid="module-synapse"]').or(
      page.getByRole('button', { name: /synapse/i })
    );
    await synapseButton.click();
    await page.waitForURL(/.*synapse.*/);
  });

  test.afterEach(async () => {
    Mocks.reset();
  });

  test.describe('Skill Marketplace', () => {
    test('should display available skills', async ({ page }) => {
      const skillsContainer = page.locator('[data-testid="skills-marketplace"]').or(
        page.locator('.skills-marketplace')
      );

      const exists = await skillsContainer.count();
      if (exists > 0) {
        await expect(skillsContainer).toBeVisible();

        // Check for skill cards
        const skillCards = page.locator('[data-testid="skill-card"]').or(
