/**
 * E2E Test Setup Utilities and Fixtures
 * Provides reusable test helpers for Hearthlink Playwright tests
 */

import { test as base, expect, Page } from '@playwright/test';
import {
  mockTauriCommands,
  mockTauriCommandsWithDelay,
  mockTauriCommandsWithErrors,
  mockScenarios,
  resetMocks,
  seedMockData,
  getMockData,
} from './mocks/tauri-commands';

/**
 * Custom test fixtures for Hearthlink
 */
export type HearthlinkFixtures = {
  authenticatedPage: Page;
  vaultPage: Page;
  aldenPage: Page;
  corePage: Page;
};

/**
 * Extended test with custom fixtures
 */
export const test = base.extend<HearthlinkFixtures>({
  // Authenticated page fixture - logs in before each test
  authenticatedPage: async ({ page }, use) => {
    await page.goto('/');
    await Helpers.waitForAppReady(page);

    // TODO: Implement actual authentication when Sentry auth is ready
    // For now, assume app loads without auth in dev mode

    await use(page);
  },

  // Vault module page
  vaultPage: async ({ page }, use) => {
    await page.goto('/');
    await Helpers.waitForAppReady(page);

    // Navigate to Vault
    const vaultButton = page.locator('[data-testid="module-vault"]').or(
      page.getByRole('button', { name: /vault/i })
    );
    await vaultButton.click();
    await page.waitForURL(/.*vault.*/);

    await use(page);
  },

  // Alden module page
  aldenPage: async ({ page }, use) => {
    await page.goto('/');
    await Helpers.waitForAppReady(page);

    // Navigate to Alden
    const aldenButton = page.locator('[data-testid="module-alden"]').or(
      page.getByRole('button', { name: /alden/i })
    );
    await aldenButton.click();
    await page.waitForURL(/.*alden.*/);

    await use(page);
  },

  // Core module page
  corePage: async ({ page }, use) => {
    await page.goto('/');
    await Helpers.waitForAppReady(page);

    // Navigate to Core
    const coreButton = page.locator('[data-testid="module-core"]').or(
      page.getByRole('button', { name: /core/i })
    );
    await coreButton.click();
    await page.waitForURL(/.*core.*/);

    await use(page);
  },
});

export { expect };

/**
 * Test data generators
 */
export const TestData = {
  /**
   * Generate a random task
   */
  randomTask: () => ({
    title: `Test Task ${Date.now()}`,
    description: 'This is a test task created by E2E tests',
    priority: ['low', 'medium', 'high'][Math.floor(Math.random() * 3)],
    dueDate: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString(),
  }),

  /**
   * Generate a random memory entry
   */
  randomMemory: () => ({
    content: `Test memory entry created at ${new Date().toLocaleString()}`,
    tags: ['test', 'e2e', 'automated'],
    type: 'episodic' as const,
  }),

  /**
   * Generate a random project
   */
  randomProject: () => ({
    name: `Test Project ${Date.now()}`,
    description: 'E2E test project',
    startDate: new Date().toISOString().split('T')[0],
    endDate: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
  }),

  /**
   * Generate a random risk item
   */
  randomRisk: () => ({
    title: `Test Risk ${Date.now()}`,
    description: 'This is a test risk item',
    impact: Math.floor(Math.random() * 5) + 1,
    likelihood: Math.floor(Math.random() * 5) + 1,
  }),
};

/**
 * Helper utilities for common E2E operations
 */
export const Helpers = {
  /**
   * Wait for element to be visible with custom timeout
   */
  async waitForVisible(page: Page, selector: string, timeout = 10000) {
    await page.waitForSelector(selector, { state: 'visible', timeout });
  },

  /**
   * Wait for app to be fully ready (replaces unstable networkidle)
   */
  async waitForAppReady(page: Page, timeout = 15000) {
    await page.waitForSelector('[data-testid="app-ready"]', { state: 'visible', timeout });
  },

  /**
   * Wait for network to be idle
   */
  async waitForNetwork(page: Page) {
    await page.waitForLoadState('networkidle');
  },

  /**
   * Take a screenshot with custom name
   */
  async screenshot(page: Page, name: string) {
    await page.screenshot({
      path: `test-results/screenshots/${name}-${Date.now()}.png`,
      fullPage: true,
    });
  },

  /**
   * Clear all test data (use in afterEach/afterAll)
   */
  async cleanupTestData(page: Page) {
    // TODO: Implement cleanup via API calls when backend is ready
    // For now, we'll rely on test database isolation
    console.log('Test cleanup executed');
  },

  /**
   * Fill form field by label
   */
  async fillByLabel(page: Page, label: string, value: string) {
    const input = page.getByLabel(label, { exact: false });
    await input.fill(value);
  },

  /**
   * Click button by text
   */
  async clickButton(page: Page, text: string | RegExp) {
    const button = page.getByRole('button', { name: text });
    await button.click();
  },

  /**
   * Wait for toast notification
   */
  async waitForToast(page: Page, message?: string | RegExp) {
    const toast = page.locator('[data-sonner-toast]');
    await toast.waitFor({ state: 'visible', timeout: 5000 });

    if (message) {
      await expect(toast).toContainText(message);
    }

    return toast;
  },

  /**
   * Open command palette
   */
  async openCommandPalette(page: Page) {
    // Cmd+K or Ctrl+K
    const isMac = process.platform === 'darwin';
    await page.keyboard.press(isMac ? 'Meta+KeyK' : 'Control+KeyK');

    // Wait for command palette to appear
    await page.waitForSelector('[data-testid="command-palette"]', {
      state: 'visible',
      timeout: 3000,
    });
  },

  /**
   * Navigate using command palette
   */
  async navigateViaCommandPalette(page: Page, command: string) {
    await Helpers.openCommandPalette(page);

    // Type command
    await page.keyboard.type(command);

    // Press enter to execute
    await page.keyboard.press('Enter');

    // Wait for navigation
    await page.waitForLoadState('networkidle');
  },

  /**
   * Check accessibility violations (basic)
   */
  async checkA11y(page: Page, selector?: string) {
    // Basic accessibility checks
    const target = selector ? page.locator(selector) : page;

    // Check for alt text on images
    const images = await target.locator('img').all();
    for (const img of images) {
      const alt = await img.getAttribute('alt');
      expect(alt).toBeTruthy();
    }

    // Check for aria-labels on interactive elements without text
    const buttons = await target.locator('button:not(:has-text)').all();
    for (const button of buttons) {
      const ariaLabel = await button.getAttribute('aria-label');
      const ariaLabelledBy = await button.getAttribute('aria-labelledby');
      expect(ariaLabel || ariaLabelledBy).toBeTruthy();
    }
  },

  /**
   * Simulate keyboard navigation
   */
  async testKeyboardNav(page: Page, expectedStops: number) {
    let tabCount = 0;
    const maxTabs = expectedStops + 5; // Safety margin

    while (tabCount < maxTabs) {
      await page.keyboard.press('Tab');
      tabCount++;

      const focused = await page.evaluate(() => {
        const el = document.activeElement;
        return {
          tagName: el?.tagName,
          id: el?.id,
          className: el?.className,
        };
      });

      if (focused.tagName === 'BODY') {
        break; // Wrapped around
      }
    }

    expect(tabCount).toBeGreaterThanOrEqual(expectedStops);
  },
};

/**
 * Test selectors (data-testid attributes)
 */
export const Selectors = {
  // Navigation
  moduleVault: '[data-testid="module-vault"]',
