  terminal: '[data-testid="terminal"]',
};

/**
 * API mocking utilities (for future use)
 */
export const ApiMocks = {
  /**
   * Mock Tauri IPC calls
   */
  mockTauriCommand: async (page: Page, command: string, response: any) => {
    await page.evaluate(
      ({ cmd, res }) => {
        (window as any).__TAURI_IPC__ = (window as any).__TAURI_IPC__ || {};
        (window as any).__TAURI_IPC__[cmd] = res;
      },
      { cmd: command, res: response }
    );
  },

  /**
   * Mock successful task creation
   */
  mockTaskCreation: async (page: Page, taskId: string = 'test-task-123') => {
    await ApiMocks.mockTauriCommand(page, 'alden_tasks_create', {
      id: taskId,
      title: 'Test Task',
      status: 'pending',
      createdAt: Date.now(),
    });
  },
};

/**
 * Performance measurement utilities
 */
export const Performance = {
  /**
   * Measure page load time
   */
  async measurePageLoad(page: Page) {
    const metrics = await page.evaluate(() => {
      const navigation = performance.getEntriesByType('navigation')[0] as PerformanceNavigationTiming;
      return {
        domContentLoaded: navigation.domContentLoadedEventEnd - navigation.domContentLoadedEventStart,
        loadComplete: navigation.loadEventEnd - navigation.loadEventStart,
        totalTime: navigation.loadEventEnd - navigation.fetchStart,
      };
    });

    return metrics;
  },

  /**
   * Measure interaction time
   */
  async measureInteraction(page: Page, action: () => Promise<void>) {
    const startTime = Date.now();
    await action();
    const endTime = Date.now();

    return endTime - startTime;
  },
};

/**
 * Accessibility testing utilities
 */
export const A11y = {
  /**
   * Test keyboard-only navigation through a form
   */
  async testFormKeyboardNav(page: Page, formSelector: string) {
    const form = page.locator(formSelector);
    await form.focus();

    // Tab through all form fields
    const inputs = await form.locator('input, select, textarea, button').all();

    for (let i = 0; i < inputs.length; i++) {
      await page.keyboard.press('Tab');

      const focused = await page.evaluate(() => document.activeElement?.tagName);
      expect(['INPUT', 'SELECT', 'TEXTAREA', 'BUTTON']).toContain(focused);
    }
  },

  /**
   * Test screen reader announcements (requires actual screen reader)
   */
  async checkAriaLiveRegions(page: Page) {
    const liveRegions = await page.locator('[aria-live]').all();
    expect(liveRegions.length).toBeGreaterThan(0);

    for (const region of liveRegions) {
      const ariaLive = await region.getAttribute('aria-live');
      expect(['polite', 'assertive', 'off']).toContain(ariaLive);
    }
  },

  /**
   * Check color contrast (simplified)
   */
  async checkContrast(page: Page, selector: string) {
    const element = page.locator(selector);
    const styles = await element.evaluate((el) => {
      const computed = window.getComputedStyle(el);
      return {
        color: computed.color,
        backgroundColor: computed.backgroundColor,
      };
    });

    // Note: Actual contrast calculation requires color parsing library
    // This is a placeholder for future implementation
    expect(styles.color).toBeTruthy();
    expect(styles.backgroundColor).toBeTruthy();
  },
};

/**
 * Mock utilities - re-export from mocks module
 */
export const Mocks = {
  install: mockTauriCommands,
