/**
 * Install Tauri command mocks in the page context
 */
export async function mockTauriCommands(page: Page, customHandlers: Partial<typeof commandHandlers> = {}) {
  // Merge custom handlers with defaults
  const handlers = { ...commandHandlers, ...customHandlers };

  // Inject mock into page before navigation
  await page.addInitScript((mockHandlers: any) => {
    // Create mock __TAURI__ global
    (window as any).__TAURI__ = {
      invoke: async (command: string, args?: any) => {
        console.log(`[MOCK] Tauri command: ${command}`, args);

        // Find handler
        const handler = mockHandlers[command];
        if (!handler) {
          throw new Error(`Mock handler not found for command: ${command}`);
        }

        // Execute handler (simulate async delay)
        await new Promise((resolve) => setTimeout(resolve, 50));
        const result = await handler(args);

        console.log(`[MOCK] Result:`, result);
        return result;
      },
    };

    // Also mock the tauri API
    (window as any).__TAURI_INTERNALS__ = {
      invoke: (window as any).__TAURI__.invoke,
    };
  }, handlers);
}

/**
 * Update mock data during test execution
 */
export async function updateMockData(page: Page, data: Partial<typeof mockStorage>) {
  await page.evaluate((newData) => {
    // Update the mock storage in page context
    Object.assign((window as any).__MOCK_STORAGE__ || {}, newData);
  }, data);
}

