/**
 * E2E Tests for Alden Email Module
 *
 * Tests email functionality including:
 * - Email inbox loading and navigation
 * - Email viewer with sanitization
 * - Email-to-task conversion
 * - Email composition
 * - Email account setup
 *
 * @see src/components/alden/EmailInbox.tsx
 * @see src/components/alden/EmailCompose.tsx
 * @see src/components/alden/EmailViewer.tsx
 * @see src/components/alden/EmailAccountSetup.tsx
 */

import { test, expect, Helpers, Mocks } from './setup';

test.describe('Alden Email Module', () => {
  test.beforeEach(async ({ page }) => {
    // Install mocks with email data
    await Mocks.install(page);
    Mocks.scenarios.emailInbox();

    // Disable tutorial
    await page.addInitScript(() => {
      window.localStorage.setItem('hl_first7_state', JSON.stringify({
        currentDay: 7,
        completedDays: [1, 2, 3, 4, 5, 6, 7],
        completedSteps: [],
        lastActive: new Date().toISOString(),
        dismissed: true,
        autoOpened: true
      }));
    });

    // Navigate to Alden
    await page.goto('/');
    await page.waitForLoadState('networkidle');

    const aldenButton = page.locator('[data-testid="module-alden"]').or(
      page.getByRole('button', { name: /alden/i })
    );
    await aldenButton.click();
    await page.waitForURL(/.*alden.*/);

    // Navigate to Email tab
    const emailTab = page.getByRole('tab', { name: /email/i }).or(
      page.getByRole('button', { name: /email/i })
    );
    const emailTabExists = await emailTab.count();
    if (emailTabExists > 0) {
      await emailTab.click();
    }
  });

  test.afterEach(async () => {
    Mocks.reset();
  });

  test.describe('Email Inbox', () => {
    test('should load email inbox', async ({ page }) => {
      // Verify email inbox container exists
      const emailInbox = page.locator('[data-testid="email-inbox"]').or(
        page.locator('.email-inbox')
      );

      const exists = await emailInbox.count();
      if (exists > 0) {
        await expect(emailInbox).toBeVisible();
      }

      // Verify either emails are loaded or empty state is shown
      const emailList = page.locator('[data-testid="email-list"]').or(
        page.locator('.email-list')
      );

      const emptyState = page.locator('[data-testid="empty-state"]').or(
        page.locator('.empty-state').or(
          page.getByText(/no emails|empty inbox/i)
        )
      );

      const listExists = await emailList.count();
      const emptyExists = await emptyState.count();

      expect(listExists + emptyExists).toBeGreaterThan(0);
    });

    test('should display email list items', async ({ page }) => {
      // Look for email items
      const emailItems = page.locator('[data-testid="email-item"]').or(
        page.locator('.email-item')
      );

      const count = await emailItems.count();
      if (count > 0) {
        // Verify each email has expected elements
        for (let i = 0; i < Math.min(count, 3); i++) {
          const item = emailItems.nth(i);
          await expect(item).toBeVisible();

          // Check for sender, subject, date
          const hasContent = await item.evaluate(el => {
            const text = el.textContent || '';
            return text.length > 0;
          });
          expect(hasContent).toBeTruthy();
        }
      }
    });

    test('should support email filtering', async ({ page }) => {
      // Look for filter controls
      const filterButton = page.getByRole('button', { name: /filter/i }).or(
        page.locator('[data-testid="filter-button"]')
      );

      const filterExists = await filterButton.count();
      if (filterExists > 0) {
        await filterButton.click();

        // Check for filter options (unread, flagged, etc.)
        const unreadFilter = page.getByRole('checkbox', { name: /unread/i }).or(
          page.getByText(/unread/i)
        );

        const unreadExists = await unreadFilter.count();
        if (unreadExists > 0) {
          await unreadFilter.click();
          await page.waitForTimeout(500);
        }
      }
    });

    test('should support email search', async ({ page }) => {
      // Look for search input
      const searchInput = page.getByPlaceholder(/search|find/i).or(
        page.locator('[data-testid="email-search"]')
      );

      const exists = await searchInput.count();
      if (exists > 0) {
        await searchInput.fill('test email');
        await page.waitForTimeout(500);

        // Verify search was performed
        const value = await searchInput.inputValue();
        expect(value).toBe('test email');
      }
    });

    test('should support keyboard navigation (j/k)', async ({ page }) => {
      // Look for email items
      const emailItems = page.locator('[data-testid="email-item"]').or(
        page.locator('.email-item')
      );

      const count = await emailItems.count();
      if (count > 0) {
        // Press 'j' to navigate down
        await page.keyboard.press('j');
        await page.waitForTimeout(200);

        // Press 'k' to navigate up
        await page.keyboard.press('k');
        await page.waitForTimeout(200);
      }
    });

    test('should refresh inbox on button click', async ({ page }) => {
      // Look for refresh button
      const refreshButton = page.getByRole('button', { name: /refresh|reload/i }).or(
        page.locator('[data-testid="refresh-button"]')
      );

      const exists = await refreshButton.count();
      if (exists > 0) {
        await refreshButton.click();
        await page.waitForTimeout(1000);

        // Verify refresh indicator or completion
        const isRefreshing = await page.evaluate(() => {
          const el = document.querySelector('[data-loading="true"]');
          return el !== null;
        });
        expect(isRefreshing).toBeFalsy();
      }
    });
  });

  test.describe('Email Viewer', () => {
    test.beforeEach(async ({ page }) => {
      // Click on first email to open viewer
      const emailItems = page.locator('[data-testid="email-item"]').or(
        page.locator('.email-item')
      );

      const count = await emailItems.count();
      if (count > 0) {
        await emailItems.first().click();
        await page.waitForTimeout(500);
      }
    });

    test('should open email viewer', async ({ page }) => {
      // Verify email viewer is visible
      const emailViewer = page.locator('[data-testid="email-viewer"]').or(
        page.locator('.email-viewer')
      );

      const exists = await emailViewer.count();
      if (exists > 0) {
        await expect(emailViewer).toBeVisible();
      }
    });

    test('should display email subject', async ({ page }) => {
      const subject = page.locator('[data-testid="email-subject"]').or(
        page.locator('.email-subject')
      );

      const exists = await subject.count();
      if (exists > 0) {
        await expect(subject).toBeVisible();
        const text = await subject.textContent();
        expect(text?.length).toBeGreaterThan(0);
      }
    });

    test('should display sender information', async ({ page }) => {
      const sender = page.locator('[data-testid="email-sender"]').or(
        page.locator('.email-sender')
      );

      const exists = await sender.count();
      if (exists > 0) {
        await expect(sender).toBeVisible();
      }
    });

    test('should display email body', async ({ page }) => {
      const body = page.locator('[data-testid="email-body"]').or(
        page.locator('.email-body')
      );

      const exists = await body.count();
      if (exists > 0) {
        await expect(body).toBeVisible();
      }
    });

    test('should sanitize HTML in email body', async ({ page }) => {
      const body = page.locator('[data-testid="email-body"]').or(
        page.locator('.email-body')
      );

      const exists = await body.count();
      if (exists > 0) {
        // Check that script tags are removed
        const hasScripts = await body.evaluate(el => {
          return el.querySelectorAll('script').length > 0;
        });
        expect(hasScripts).toBeFalsy();

        // Check that onclick handlers are removed
        const hasOnClick = await body.evaluate(el => {
          const elements = el.querySelectorAll('*');
          for (const elem of elements) {
            if (elem.getAttribute('onclick')) {
              return true;
            }
          }
          return false;
        });
        expect(hasOnClick).toBeFalsy();
      }
    });
  });

  test.describe('Email-to-Task Conversion', () => {
    test.beforeEach(async ({ page }) => {
      // Click on first email to open viewer
      const emailItems = page.locator('[data-testid="email-item"]').or(
        page.locator('.email-item')
      );

      const count = await emailItems.count();
      if (count > 0) {
        await emailItems.first().click();
        await page.waitForTimeout(500);
      }
