    test('should show convert to task button', async ({ page }) => {
      const convertButton = page.getByRole('button', { name: /convert.*task|create.*task/i }).or(
        page.locator('[data-testid="convert-to-task"]')
      );

      const exists = await convertButton.count();
      if (exists > 0) {
        await expect(convertButton).toBeVisible();
      }
    });

    test('should open task creation modal', async ({ page }) => {
      const convertButton = page.getByRole('button', { name: /convert.*task/i }).or(
        page.locator('[data-testid="convert-to-task"]')
      );

      const exists = await convertButton.count();
      if (exists > 0) {
        await convertButton.click();
        await page.waitForTimeout(500);

        // Verify task modal is open
        const taskModal = page.locator('[data-testid="task-modal"]').or(
          page.locator('[role="dialog"]')
        );

        const modalExists = await taskModal.count();
        if (modalExists > 0) {
          await expect(taskModal).toBeVisible();
        }
      }
    });

    test('should pre-fill task details from email', async ({ page }) => {
      const convertButton = page.getByRole('button', { name: /convert.*task/i }).or(
        page.locator('[data-testid="convert-to-task"]')
      );

      const exists = await convertButton.count();
      if (exists > 0) {
        await convertButton.click();
        await page.waitForTimeout(500);

        // Check if task title is pre-filled with email subject
        const taskTitle = page.getByRole('textbox', { name: /title|subject/i }).or(
          page.locator('[data-testid="task-title"]')
        );

        const titleExists = await taskTitle.count();
        if (titleExists > 0) {
          const value = await taskTitle.inputValue();
          expect(value?.length).toBeGreaterThan(0);
        }
      }
    });

    test('should create task from email', async ({ page }) => {
      const convertButton = page.getByRole('button', { name: /convert.*task/i }).or(
        page.locator('[data-testid="convert-to-task"]')
      );

      const exists = await convertButton.count();
      if (exists > 0) {
        await convertButton.click();
        await page.waitForTimeout(500);

        // Click create button
        const createButton = page.getByRole('button', { name: /create|save/i });
        const createExists = await createButton.count();
        if (createExists > 0) {
          await createButton.click();
