          page.locator('.skill-config')
        ).first();

        const configExists = await configPanel.count();
        if (configExists > 0) {
          const initialConfig = await configPanel.textContent();

          // Reload page
          await page.reload();
          await page.waitForLoadState('networkidle');

          await instancesTab.click();

          // Verify configuration is maintained
          const configPanelAfter = page.locator('[data-testid="skill-config"]').or(
            page.locator('.skill-config')
          ).first();

          const configAfterExists = await configPanelAfter.count();
