  onModuleClick: (module: Module) => void;
  onSubModuleClick: (subModule: SubModule) => void;
  onMouseEnter?: () => void;
  onMouseLeave?: () => void;
}

export const ModuleIcon: React.FC<ModuleIconProps> = ({ module, index, isSubModule, onModuleClick, onSubModuleClick, onMouseEnter, onMouseLeave }) => {
  const angle = module.angle;
  const radian = ((angle - 90) * Math.PI) / 180;
  // Use percentage of container size instead of vh to stay within bounds
  // Container radius is 35vmin (70vmin diameter / 2), positioning icons at ~67% of radius
  const radiusPercent = 33; // 33% of container = 23.1vmin radius for icons (closer to center)

  return (
    <button
      key={module.name}
      data-testid={`module-${module.name.toLowerCase()}`}
      data-hl-tour={module.name === 'Vault' ? 'vault-nav' : undefined}
