      style={{
        left: `calc(50% + ${radiusPercent * Math.cos(radian)}%)`,
        top: `calc(50% + ${radiusPercent * Math.sin(radian)}%)`,
        animationDelay: `${index * 150}ms`,
        transform: 'translate(-50%, -50%)'
