 * @date 2025-11-21
 */

import React, { useState, useEffect, useCallback, useRef, useMemo } from 'react';

import { useAldenBridge } from '../../hooks/useBridge';

import { LoadingSpinner } from '../shared/LoadingSpinner';
import { EmailViewer } from './EmailViewer';
import { EmailCompose } from './EmailCompose';
import { EmailAccountSetup } from './EmailAccountSetup';

import { EmailAccount, EmailMessage, Task } from '../../types/agents';
import { PremiumModal } from '../shared/premium/PremiumModal';
import { CreateTaskModal } from './CreateTaskModal';
import { EmailList } from './EmailList';
import { EmailAccountSelector } from './EmailAccountSelector';
import { SafeHtmlRenderer } from '../shared/SafeHtmlRenderer';

// View mode type for the inbox
type ViewMode = 'list' | 'split';

// Folder options for move action
const FOLDER_OPTIONS = ['Inbox', 'Archive', 'Drafts', 'Sent', 'Spam', 'Trash'];

// Keyboard shortcut help items
const KEYBOARD_SHORTCUTS = [
  { key: 'n', action: 'New email' },
  { key: 'r', action: 'Reply' },
  { key: 'a', action: 'Reply All' },
  { key: 'f', action: 'Forward' },
  { key: 'Delete', action: 'Delete email' },
  { key: 'j', action: 'Next email' },
  { key: 'k', action: 'Previous email' },
  { key: 's', action: 'Toggle star' },
  { key: 'u', action: 'Mark unread' },
  { key: '?', action: 'Show shortcuts' },
];

export const EmailInbox: React.FC = () => {
  const [accounts, setAccounts] = useState<EmailAccount[]>([]);
  const [selectedAccountId, setSelectedAccountId] = useState<string>('');
  const [messages, setMessages] = useState<EmailMessage[]>([]);
  const [selectedMessage, setSelectedMessage] = useState<EmailMessage | null>(null);
  const [selectedIndex, setSelectedIndex] = useState<number>(-1);
  const [showCompose, setShowCompose] = useState(false);
  const [loading, setLoading] = useState(false);
  const [syncing, setSyncing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [successMessage, setSuccessMessage] = useState<string | null>(null);
  const [showAccountSetup, setShowAccountSetup] = useState(false);
  const [viewMode, setViewMode] = useState<ViewMode>('list');
  const [searchQuery, setSearchQuery] = useState('');
  const [showFolderDropdown, setShowFolderDropdown] = useState(false);
  const [showShortcutsHelp, setShowShortcutsHelp] = useState(false);
  const [composeMode, setComposeMode] = useState<'new' | 'reply' | 'replyAll' | 'forward'>('new');
  const [replyToMessage, setReplyToMessage] = useState<EmailMessage | null>(null);
  
  // Create Task State
  const [showCreateTask, setShowCreateTask] = useState(false);
  const [taskInitialData, setTaskInitialData] = useState<{title: string, description: string}>({ title: '', description: '' });

  const { bridge } = useAldenBridge();
  const containerRef = useRef<HTMLDivElement>(null);
  const folderDropdownRef = useRef<HTMLDivElement>(null);

  // Filter messages based on search query
  const filteredMessages = useMemo(() => {
    if (!searchQuery.trim()) return messages;

    const query = searchQuery.toLowerCase();
    return messages.filter(message =>
      message.subject?.toLowerCase().includes(query) ||
      message.from?.toLowerCase().includes(query) ||
      message.body?.toLowerCase().includes(query)
    );
  }, [messages, searchQuery]);





  // Load email accounts
  const loadAccounts = useCallback(async () => {
    if (!bridge) return;
    setLoading(true);
    setError(null);

    try {
      const result = await bridge.getEmailAccounts('user');

      if (!result.success) {
        setError(result.error || 'Failed to load email accounts');
        return;
      }

      setAccounts(result.data);

      // Auto-select first account or default
      if (result.data.length > 0) {
        const defaultAccount = result.data[0];
        setSelectedAccountId(defaultAccount.id || '');
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to load email accounts');
    } finally {
      setLoading(false);
    }
  }, [bridge]);

  // Load messages for selected account
  const loadMessages = useCallback(async () => {
    if (!bridge || !selectedAccountId) return;

    setLoading(true);
    setError(null);
    setMessages([]);

    try {
      const result = await bridge.getEmailMessages(selectedAccountId, undefined, 50);

      if (!result.success) {
        setError(result.error || 'Failed to load messages');
        return;
      }

      // Sort by timestamp (newest first)
      const sorted = [...result.data].sort((a, b) => {
        const aTime = new Date(a.received_date || '').getTime();
        const bTime = new Date(b.received_date || '').getTime();
        return bTime - aTime;
      });
      setMessages(sorted);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to load messages');
    } finally {
      setLoading(false);
    }
  }, [bridge, selectedAccountId]);

  // Initial load of accounts
  useEffect(() => {
    loadAccounts();
  }, [loadAccounts]);

  // Load messages when account changes
  useEffect(() => {
    if (selectedAccountId) {
      loadMessages();
    }
  }, [selectedAccountId, loadMessages]);

  // Update selected index when message is selected
  useEffect(() => {
    if (selectedMessage) {
      const index = filteredMessages.findIndex(m => m.id === selectedMessage.id);
      setSelectedIndex(index);
    }
  }, [selectedMessage, filteredMessages]);

  // Close folder dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (folderDropdownRef.current && !folderDropdownRef.current.contains(event.target as Node)) {
        setShowFolderDropdown(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  // Navigate through messages with j/k keys
  const navigateMessages = useCallback((direction: number) => {
    if (filteredMessages.length === 0) return;

    let newIndex = selectedIndex + direction;
    if (newIndex < 0) newIndex = 0;
