// Compose mode type
export type ComposeMode = 'new' | 'reply' | 'replyAll' | 'forward';

interface EmailComposeProps {
  accountId: string;
  onClose: () => void;
  onSent: () => void;
  mode?: ComposeMode;
  replyToMessage?: EmailMessage | null;
}

// Helper to format the original message for reply/forward
const formatOriginalMessage = (message: EmailMessage, mode: ComposeMode): string => {
  const separator = '\n\n' + '-'.repeat(40) + '\n';
  const fromLine = `From: ${message.from || 'Unknown'}`;
  const dateLine = `Date: ${new Date(message.received_date || '').toLocaleString()}`;
  const subjectLine = `Subject: ${message.subject || '(No subject)'}`;
  const toLine = `To: ${message.to?.join(', ') || ''}`;

  let header = '';
  if (mode === 'forward') {
    header = `${separator}---------- Forwarded message ---------\n${fromLine}\n${dateLine}\n${subjectLine}\n${toLine}\n\n`;
  } else {
    header = `${separator}On ${new Date(message.received_date || '').toLocaleString()}, ${message.from} wrote:\n\n`;
  }

  const body = message.body || '';
  // Add '>' prefix for quoted text in replies
  const quotedBody = mode !== 'forward'
    ? body.split('\n').map(line => `> ${line}`).join('\n')
    : body;

  return header + quotedBody;
};

// Helper to get subject prefix
const getSubjectPrefix = (mode: ComposeMode, originalSubject: string): string => {
  const subject = originalSubject || '';

  if (mode === 'forward') {
    if (subject.toLowerCase().startsWith('fwd:')) return subject;
    return `Fwd: ${subject}`;
  }

  if (mode === 'reply' || mode === 'replyAll') {
    if (subject.toLowerCase().startsWith('re:')) return subject;
    return `Re: ${subject}`;
  }

  return subject;
};

export const EmailCompose: React.FC<EmailComposeProps> = ({
  accountId,
  onClose,
  onSent,
  mode = 'new',
  replyToMessage = null,
}) => {
  const [to, setTo] = useState('');
  const [cc, setCc] = useState('');
  const [bcc, setBcc] = useState('');
  const [subject, setSubject] = useState('');
  const [body, setBody] = useState('');
  const [sending, setSending] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [validationErrors, setValidationErrors] = useState<Record<string, string>>({});

  const toInputRef = useRef<HTMLInputElement>(null);
  const bodyTextareaRef = useRef<HTMLTextAreaElement>(null);

  // Initialize fields based on mode and reply message
  useEffect(() => {
    if (!replyToMessage) {
      // New email - focus on To field
      setTimeout(() => toInputRef.current?.focus(), 100);
      return;
    }

    const originalSubject = replyToMessage.subject || '';
    const originalFrom = replyToMessage.from || '';
    const originalTo = replyToMessage.to || [];
    const originalCc = replyToMessage.cc || [];

    switch (mode) {
      case 'reply':
        setTo(originalFrom);
        setSubject(getSubjectPrefix(mode, originalSubject));
        setBody(formatOriginalMessage(replyToMessage, mode));
        // Focus on body for replies
        setTimeout(() => bodyTextareaRef.current?.focus(), 100);
        break;

      case 'replyAll':
        // Reply to sender and all original recipients (excluding self)
        const allRecipients = [originalFrom, ...originalTo].filter(
          (email, index, arr) => arr.indexOf(email) === index // Remove duplicates
        );
        setTo(allRecipients.join(', '));
        setCc(originalCc.join(', '));
        setSubject(getSubjectPrefix(mode, originalSubject));
        setBody(formatOriginalMessage(replyToMessage, mode));
        setTimeout(() => bodyTextareaRef.current?.focus(), 100);
        break;

      case 'forward':
        setSubject(getSubjectPrefix(mode, originalSubject));
        setBody(formatOriginalMessage(replyToMessage, mode));
        // Focus on To field for forwards
        setTimeout(() => toInputRef.current?.focus(), 100);
        break;

      default:
        setTimeout(() => toInputRef.current?.focus(), 100);
    }
