  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    setError(null);

    try {
      const accountResponse = await AldenBridge.createEmailAccount({
        user_id: 'user',
        email_address: email,
        password: password,
        provider: provider,
        imap_host: showAdvanced || provider === 'generic' ? imapHost : undefined,
        imap_port: showAdvanced || provider === 'generic' ? imapPort : undefined,
        smtp_host: showAdvanced || provider === 'generic' ? smtpHost : undefined,
        smtp_port: showAdvanced || provider === 'generic' ? smtpPort : undefined,
        is_default: true,
      });

      if (accountResponse.success) {
        toast.success('Email account connected!', {
          description: 'Syncing messages...',
        });
        onSuccess?.(accountResponse.data);
        onClose();
      } else {
        setError(accountResponse.error || 'Failed to connect email account');
        toast.error('Connection failed', {
          description: 'Check your credentials and try again',
        });
      }

      // Reset form
      setEmail('');
      setPassword('');
